INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26582, 'nvi', 'João', 'jo', 12, 1, 'Seis dias antes da Páscoa Jesus chegou a Betânia, onde vivia Lázaro, a quem ressuscitara dos mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26583, 'nvi', 'João', 'jo', 12, 2, 'Ali prepararam um jantar para Jesus. Marta servia, enquanto Lázaro estava à mesa com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26584, 'nvi', 'João', 'jo', 12, 3, 'Então Maria pegou um frasco de nardo puro, que era um perfume caro, derramou-o sobre os pés de Jesus e os enxugou com os seus cabelos. E a casa encheu-se com a fragrância do perfume.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26585, 'nvi', 'João', 'jo', 12, 4, 'Mas um dos seus discípulos, Judas Iscariotes, que mais tarde iria traí-lo, fez uma objeção:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26586, 'nvi', 'João', 'jo', 12, 5, '"Por que este perfume não foi vendido, e o dinheiro dado aos pobres? Seriam trezentos denários".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26587, 'nvi', 'João', 'jo', 12, 6, 'Ele não falou isso por se interessar pelos pobres, mas porque era ladrão; sendo responsável pela bolsa de dinheiro, costumava tirar o que nela era colocado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26588, 'nvi', 'João', 'jo', 12, 7, 'Respondeu Jesus: "Deixe-a em paz; que o guarde para o dia do meu sepultamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26589, 'nvi', 'João', 'jo', 12, 8, 'Pois os pobres vocês sempre terão consigo, mas a mim vocês nem sempre terão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26590, 'nvi', 'João', 'jo', 12, 9, 'Enquanto isso, uma grande multidão de judeus, ao descobrir que Jesus estava ali, veio, não apenas por causa de Jesus, mas também para ver Lázaro, a quem ele ressuscitara dos mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26591, 'nvi', 'João', 'jo', 12, 10, 'Assim, os chefes dos sacerdotes fizeram planos para matar também Lázaro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26592, 'nvi', 'João', 'jo', 12, 11, 'pois por causa dele muitos estavam se afastando dos judeus e crendo em Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26593, 'nvi', 'João', 'jo', 12, 12, 'No dia seguinte, a grande multidão que tinha vindo para a festa ouviu falar que Jesus estava chegando a Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26594, 'nvi', 'João', 'jo', 12, 13, 'Pegaram ramos de palmeiras e saíram ao seu encontro, gritando: "Hosana! " "Bendito é o que vem em nome do Senhor! " "Bendito é o Rei de Israel! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26595, 'nvi', 'João', 'jo', 12, 14, 'Jesus conseguiu um jumentinho e montou nele, como está escrito:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26596, 'nvi', 'João', 'jo', 12, 15, '"Não tenhas medo, ó cidade de Sião; eis que o seu rei vem, montado num jumentinho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26597, 'nvi', 'João', 'jo', 12, 16, 'A princípio seus discípulos não entenderam isso. Só depois que Jesus foi glorificado, perceberam que lhe fizeram essas coisas, e que elas estavam escritas a respeito dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26598, 'nvi', 'João', 'jo', 12, 17, 'A multidão que estava com ele, quando mandara Lázaro sair do sepulcro e o ressuscitara dos mortos, continuou a espalhar o fato.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26599, 'nvi', 'João', 'jo', 12, 18, 'Muitas pessoas, por terem ouvido falar que ele realizara tal sinal miraculoso, foram ao seu encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26600, 'nvi', 'João', 'jo', 12, 19, 'E assim os fariseus disseram uns aos outros: "Não conseguimos nada. Olhem como o mundo todo vai atrás dele! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26601, 'nvi', 'João', 'jo', 12, 20, 'Entre os que tinham ido adorar a Deus na festa da Páscoa, estavam alguns gregos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26602, 'nvi', 'João', 'jo', 12, 21, 'Eles se aproximaram de Filipe, que era de Betsaida da Galiléia, com um pedido: "Senhor, queremos ver Jesus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26603, 'nvi', 'João', 'jo', 12, 22, 'Filipe foi dizê-lo a André, e os dois juntos o disseram a Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26604, 'nvi', 'João', 'jo', 12, 23, 'Jesus respondeu: "Chegou a hora de ser glorificado o Filho do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26605, 'nvi', 'João', 'jo', 12, 24, 'Digo-lhes verdadeiramente que, se o grão de trigo não cair na terra e não morrer, continuará ele só. Mas se morrer, dará muito fruto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26606, 'nvi', 'João', 'jo', 12, 25, 'Aquele que ama a sua vida, a perderá; ao passo que aquele que odeia a sua vida neste mundo, a conservará para a vida eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26607, 'nvi', 'João', 'jo', 12, 26, 'Quem me serve precisa seguir-me; e, onde estou, o meu servo também estará. Aquele que me serve, meu Pai o honrará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26608, 'nvi', 'João', 'jo', 12, 27, '"Agora meu coração está perturbado, e o que direi? Pai, salva-me desta hora? Não; eu vim exatamente para isto, para esta hora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26609, 'nvi', 'João', 'jo', 12, 28, 'Pai, glorifica o teu nome! " Então veio uma voz do céu: "Eu já o glorifiquei e o glorificarei novamente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26610, 'nvi', 'João', 'jo', 12, 29, 'A multidão que ali estava e a ouviu, disse que tinha trovejado; outros disseram que um anjo lhe tinha falado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26611, 'nvi', 'João', 'jo', 12, 30, 'Jesus disse: "Esta voz veio por causa de vocês, e não por minha causa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26612, 'nvi', 'João', 'jo', 12, 31, 'Chegou a hora de ser julgado este mundo; agora será expulso o príncipe deste mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26613, 'nvi', 'João', 'jo', 12, 32, 'Mas eu, quando for levantado da terra, atrairei todos a mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26614, 'nvi', 'João', 'jo', 12, 33, 'Ele disse isso para indicar o tipo de morte que haveria de sofrer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26615, 'nvi', 'João', 'jo', 12, 34, 'A multidão falou: "A Lei nos ensina que o Cristo permanecerá para sempre; como podes dizer: ‘O Filho do homem precisa ser levantado’? Quem é esse ‘Filho do homem’? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26616, 'nvi', 'João', 'jo', 12, 35, 'Disse-lhes então Jesus: "Por mais um pouco de tempo a luz estará entre vocês. Andem enquanto vocês têm a luz, para que as trevas não os surpreendam, pois aquele que anda nas trevas não sabe para onde está indo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26617, 'nvi', 'João', 'jo', 12, 36, 'Creiam na luz enquanto vocês a têm, para que se tornem filhos da luz". Terminando de falar, Jesus saiu e ocultou-se deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26618, 'nvi', 'João', 'jo', 12, 37, 'Mesmo depois que Jesus fez todos aqueles sinais miraculosos, não creram nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26619, 'nvi', 'João', 'jo', 12, 38, 'Isso aconteceu para se cumprir a palavra do profeta Isaías, que disse: "Senhor, quem creu em nossa mensagem, e a quem foi revelado o braço do Senhor? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26620, 'nvi', 'João', 'jo', 12, 39, 'Por esta razão eles não podiam crer, porque, como disse Isaías noutro lugar:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26621, 'nvi', 'João', 'jo', 12, 40, '"Cegou os seus olhos e endureceu os seus corações, para que não vejam com os olhos nem entendam com o coração, nem se convertam, e eu os cure".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26622, 'nvi', 'João', 'jo', 12, 41, 'Isaías disse isso porque viu a glória de Jesus e falou sobre ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26623, 'nvi', 'João', 'jo', 12, 42, 'Ainda assim, muitos líderes dos judeus creram nele. Mas, por causa dos fariseus, não confessavam a sua fé, com medo de serem expulsos da sinagoga;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26624, 'nvi', 'João', 'jo', 12, 43, 'pois preferiam a aprovação dos homens do que a aprovação de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26625, 'nvi', 'João', 'jo', 12, 44, 'Então Jesus disse em alta voz: "Quem crê em mim, não crê apenas em mim, mas naquele que me enviou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26626, 'nvi', 'João', 'jo', 12, 45, 'Quem me vê, vê aquele que me enviou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26627, 'nvi', 'João', 'jo', 12, 46, 'Eu vim ao mundo como luz, para que todo aquele que crê em mim não permaneça nas trevas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26628, 'nvi', 'João', 'jo', 12, 47, '"Se alguém ouve as minhas palavras, e não as guarda, eu não o julgo. Pois não vim para julgar o mundo, mas para salvá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26629, 'nvi', 'João', 'jo', 12, 48, 'Há um juiz para quem me rejeita e não aceita as minhas palavras; a própria palavra que proferi o condenará no último dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26630, 'nvi', 'João', 'jo', 12, 49, 'Pois não falei por mim mesmo, mas o Pai que me enviou me ordenou o que dizer e o que falar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26631, 'nvi', 'João', 'jo', 12, 50, 'Sei que o seu mandamento é a vida eterna. Portanto, o que eu digo é exatamente o que o Pai me mandou dizer".');
