INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26670, 'nvi', 'João', 'jo', 14, 1, '"Não se perturbe o coração de vocês. Creiam em Deus; creiam também em mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26671, 'nvi', 'João', 'jo', 14, 2, 'Na casa de meu Pai há muitos aposentos; se não fosse assim, eu lhes teria dito. Vou preparar-lhes lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26672, 'nvi', 'João', 'jo', 14, 3, 'E se eu for e lhes preparar lugar, voltarei e os levarei para mim, para que vocês estejam onde eu estiver.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26673, 'nvi', 'João', 'jo', 14, 4, 'Vocês conhecem o caminho para onde vou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26674, 'nvi', 'João', 'jo', 14, 5, 'Disse-lhe Tomé: "Senhor, não sabemos para onde vais; como então podemos saber o caminho? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26675, 'nvi', 'João', 'jo', 14, 6, 'Respondeu Jesus: "Eu sou o caminho, a verdade e a vida. Ninguém vem ao Pai, a não ser por mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26676, 'nvi', 'João', 'jo', 14, 7, 'Se vocês realmente me conhecessem, conheceriam também o meu Pai. Já agora vocês o conhecem e o têm visto".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26677, 'nvi', 'João', 'jo', 14, 8, 'Disse Filipe: "Senhor, mostra-nos o Pai, e isso nos basta".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26678, 'nvi', 'João', 'jo', 14, 9, 'Jesus respondeu: "Você não me conhece, Filipe, mesmo depois de eu ter estado com vocês durante tanto tempo? Quem me vê, vê o Pai. Como você pode dizer: ‘Mostra-nos o Pai’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26679, 'nvi', 'João', 'jo', 14, 10, 'Você não crê que eu estou no Pai e que o Pai está em mim? As palavras que eu lhes digo não são apenas minhas. Pelo contrário, o Pai, que vive em mim, está realizando a sua obra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26680, 'nvi', 'João', 'jo', 14, 11, 'Creiam em mim quando digo que estou no Pai e que o Pai está em mim; ou pelo menos creiam por causa das mesmas obras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26681, 'nvi', 'João', 'jo', 14, 12, 'Digo-lhes a verdade: Aquele que crê em mim fará também as obras que tenho realizado. Fará coisas ainda maiores do que estas, porque eu estou indo para o Pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26682, 'nvi', 'João', 'jo', 14, 13, 'E eu farei o que vocês pedirem em meu nome, para que o Pai seja glorificado no Filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26683, 'nvi', 'João', 'jo', 14, 14, 'O que vocês pedirem em meu nome, eu farei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26684, 'nvi', 'João', 'jo', 14, 15, '"Se vocês me amam, obedecerão aos meus mandamentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26685, 'nvi', 'João', 'jo', 14, 16, 'E eu pedirei ao Pai, e ele lhes dará outro Conselheiro para estar com vocês para sempre,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26686, 'nvi', 'João', 'jo', 14, 17, 'o Espírito da verdade. O mundo não pode recebê-lo, porque não o vê nem o conhece. Mas vocês o conhecem, pois ele vive com vocês e estará em vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26687, 'nvi', 'João', 'jo', 14, 18, 'Não os deixarei órfãos; voltarei para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26688, 'nvi', 'João', 'jo', 14, 19, 'Dentro de pouco tempo o mundo já não me verá mais; vocês, porém, me verão. Porque eu vivo, vocês também viverão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26689, 'nvi', 'João', 'jo', 14, 20, 'Naquele dia compreenderão que estou em meu Pai, vocês em mim, e eu em vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26690, 'nvi', 'João', 'jo', 14, 21, 'Quem tem os meus mandamentos e lhes obedece, esse é o que me ama. Aquele que me ama será amado por meu Pai, e eu também o amarei e me revelarei a ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26691, 'nvi', 'João', 'jo', 14, 22, 'Disse então Judas ( não o Iscariotes ): "Senhor, mas por que te revelarás a nós e não ao mundo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26692, 'nvi', 'João', 'jo', 14, 23, 'Respondeu Jesus: "Se alguém me ama, guardará a minha palavra. Meu Pai o amará, nós viremos a ele e faremos nele morada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26693, 'nvi', 'João', 'jo', 14, 24, 'Aquele que não me ama não guarda as minhas palavras. Estas palavras que vocês estão ouvindo não são minhas; são de meu Pai que me enviou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26694, 'nvi', 'João', 'jo', 14, 25, '"Tudo isso lhes tenho dito enquanto ainda estou com vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26695, 'nvi', 'João', 'jo', 14, 26, 'Mas o Conselheiro, o Espírito Santo, que o Pai enviará em meu nome, lhes ensinará todas as coisas e lhes fará lembrar tudo o que eu lhes disse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26696, 'nvi', 'João', 'jo', 14, 27, 'Deixo-lhes a paz; a minha paz lhes dou. Não a dou como o mundo a dá. Não se perturbem os seus corações, nem tenham medo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26697, 'nvi', 'João', 'jo', 14, 28, '"Vocês me ouviram dizer: Vou, mas volto para vocês. Se vocês me amassem, ficariam contentes porque vou para o Pai, pois o Pai é maior do que eu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26698, 'nvi', 'João', 'jo', 14, 29, 'Isso eu lhes disse agora, antes que aconteça, para que, quando acontecer, vocês creiam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26699, 'nvi', 'João', 'jo', 14, 30, 'Já não lhes falarei muito, pois o príncipe deste mundo está vindo. Ele não tem nenhum direito sobre mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26700, 'nvi', 'João', 'jo', 14, 31, 'Todavia para que o mundo saiba que amo o Pai e que faço o que meu Pai me ordenou. Levantem-se, vamo-nos daqui! "');
