INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26701, 'nvi', 'João', 'jo', 15, 1, '"Eu sou a videira verdadeira, e meu Pai é o agricultor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26702, 'nvi', 'João', 'jo', 15, 2, 'Todo ramo que, estando em mim, não dá fruto, ele corta; e todo que dá fruto ele poda, para que dê mais fruto ainda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26703, 'nvi', 'João', 'jo', 15, 3, 'Vocês já estão limpos, pela palavra que lhes tenho falado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26704, 'nvi', 'João', 'jo', 15, 4, 'Permaneçam em mim, e eu permanecerei em vocês. Nenhum ramo pode dar fruto por si mesmo, se não permanecer na videira. Vocês também não podem dar fruto, se não permanecerem em mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26705, 'nvi', 'João', 'jo', 15, 5, '"Eu sou a videira; vocês são os ramos. Se alguém permanecer em mim e eu nele, esse dá muito fruto; pois sem mim vocês não podem fazer coisa alguma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26706, 'nvi', 'João', 'jo', 15, 6, 'Se alguém não permanecer em mim, será como o ramo que é jogado fora e seca. Tais ramos são apanhados, lançados ao fogo e queimados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26707, 'nvi', 'João', 'jo', 15, 7, 'Se vocês permanecerem em mim, e as minhas palavras permanecerem em vocês, pedirão o que quiserem, e lhes será concedido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26708, 'nvi', 'João', 'jo', 15, 8, 'Meu Pai é glorificado pelo fato de vocês darem muito fruto; e assim serão meus discípulos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26709, 'nvi', 'João', 'jo', 15, 9, '"Como o Pai me amou, assim eu os amei; permaneçam no meu amor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26710, 'nvi', 'João', 'jo', 15, 10, 'Se vocês obedecerem aos meus mandamentos, permanecerão no meu amor, assim como tenho obedecido aos mandamentos de meu Pai e em seu amor permaneço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26711, 'nvi', 'João', 'jo', 15, 11, 'Tenho lhes dito estas palavras para que a minha alegria esteja em vocês e a alegria de vocês seja completa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26712, 'nvi', 'João', 'jo', 15, 12, 'O meu mandamento é este: amem-se uns aos outros como eu os amei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26713, 'nvi', 'João', 'jo', 15, 13, 'Ninguém tem maior amor do que aquele que dá a sua vida pelos seus amigos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26714, 'nvi', 'João', 'jo', 15, 14, 'Vocês serão meus amigos, se fizerem o que eu lhes ordeno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26715, 'nvi', 'João', 'jo', 15, 15, 'Já não os chamo servos, porque o servo não sabe o que o seu senhor faz. Em vez disso, eu os tenho chamado amigos, porque tudo o que ouvi de meu Pai eu lhes tornei conhecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26716, 'nvi', 'João', 'jo', 15, 16, 'Vocês não me escolheram, mas eu os escolhi para irem e darem fruto, fruto que permaneça, a fim de que o Pai lhes conceda o que pedirem em meu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26717, 'nvi', 'João', 'jo', 15, 17, 'Este é o meu mandamento: amem-se uns aos outros".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26718, 'nvi', 'João', 'jo', 15, 18, '"Se o mundo os odeia, tenham em mente que antes odiou a mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26719, 'nvi', 'João', 'jo', 15, 19, 'Se vocês pertencessem ao mundo, ele os amaria como se fossem dele. Todavia, vocês não são do mundo, mas eu os escolhi, tirando-os do mundo; por isso o mundo os odeia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26720, 'nvi', 'João', 'jo', 15, 20, 'Lembrem-se das palavras que eu lhes disse: nenhum escravo é maior do que o seu senhor. Se me perseguiram, também perseguirão vocês. Se obedeceram à minha palavra, também obedecerão à de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26721, 'nvi', 'João', 'jo', 15, 21, 'Tratarão assim vocês por causa do meu nome, pois não conhecem aquele que me enviou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26722, 'nvi', 'João', 'jo', 15, 22, 'Se eu não tivesse vindo e lhes falado, não seriam culpados de pecado. Agora, contudo, eles não têm desculpa para o seu pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26723, 'nvi', 'João', 'jo', 15, 23, 'Aquele que me odeia, também odeia o meu Pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26724, 'nvi', 'João', 'jo', 15, 24, 'Se eu não tivesse realizado no meio deles obras que ninguém mais fez, eles não seriam culpados de pecado. Mas agora eles as viram e odiaram a mim e a meu Pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26725, 'nvi', 'João', 'jo', 15, 25, 'Mas isto aconteceu para se cumprir o que está escrito na Lei deles: ‘Odiaram-me sem razão’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26726, 'nvi', 'João', 'jo', 15, 26, '"Quando vier o Conselheiro, que eu enviarei a vocês da parte do Pai, o Espírito da verdade que provém do Pai, ele testemunhará a meu respeito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26727, 'nvi', 'João', 'jo', 15, 27, 'E vocês também testemunharão, pois estão comigo desde o princípio".');
