INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26728, 'nvi', 'João', 'jo', 16, 1, '"Tenho-lhes dito tudo isso para que vocês não venham a tropeçar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26729, 'nvi', 'João', 'jo', 16, 2, 'Vocês serão expulsos das sinagogas; de fato, virá o tempo quando quem os matar pensará que está prestando culto a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26730, 'nvi', 'João', 'jo', 16, 3, 'Farão essas coisas porque não conheceram nem o Pai, nem a mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26731, 'nvi', 'João', 'jo', 16, 4, 'Estou lhes dizendo isto para que, quando chegar a hora, lembrem-se de que eu os avisei. Não lhes disse isso no princípio, porque eu estava com vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26732, 'nvi', 'João', 'jo', 16, 5, '"Agora que vou para aquele que me enviou, nenhum de vocês me pergunta: ‘Para onde vais? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26733, 'nvi', 'João', 'jo', 16, 6, 'Porque falei estas coisas, o coração de vocês encheu-se de tristeza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26734, 'nvi', 'João', 'jo', 16, 7, 'Mas eu lhes afirmo que é para o bem de vocês que eu vou. Se eu não for, o Conselheiro não virá para vocês; mas se eu for, eu o enviarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26735, 'nvi', 'João', 'jo', 16, 8, 'Quando ele vier, convencerá o mundo do pecado, da justiça e do juízo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26736, 'nvi', 'João', 'jo', 16, 9, 'Do pecado, porque os homens não crêem em mim;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26737, 'nvi', 'João', 'jo', 16, 10, 'da justiça, porque vou para o Pai, e vocês não me verão mais;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26738, 'nvi', 'João', 'jo', 16, 11, 'e do juízo, porque o príncipe deste mundo já está condenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26739, 'nvi', 'João', 'jo', 16, 12, '"Tenho ainda muito que lhes dizer, mas vocês não o podem suportar agora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26740, 'nvi', 'João', 'jo', 16, 13, 'Mas quando o Espírito da verdade vier, ele os guiará a toda a verdade. Não falará de si mesmo; falará apenas o que ouvir, e lhes anunciará o que está por vir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26741, 'nvi', 'João', 'jo', 16, 14, 'Ele me glorificará, porque receberá do que é meu e o tornará conhecido a vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26742, 'nvi', 'João', 'jo', 16, 15, 'Tudo o que pertence ao Pai é meu. Por isso eu disse que o Espírito receberá do que é meu e o tornará conhecido a vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26743, 'nvi', 'João', 'jo', 16, 16, '"Mais um pouco e já não me verão; um pouco mais, e me verão de novo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26744, 'nvi', 'João', 'jo', 16, 17, 'Alguns dos seus discípulos disseram uns aos outros: "O que ele quer dizer com isso: ‘Mais um pouco e não me verão’; e ‘um pouco mais e me verão de novo’, e ‘Porque vou para o Pai’? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26745, 'nvi', 'João', 'jo', 16, 18, 'E perguntavam: "Que quer dizer ‘um pouco mais’? Não entendemos o que ele está dizendo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26746, 'nvi', 'João', 'jo', 16, 19, 'Jesus percebeu que desejavam interrogá-lo a respeito disso, pelo que lhes disse: "Vocês estão perguntando uns aos outros o que eu quis dizer quando falei: Mais um pouco e não me verão; um pouco mais e me verão de novo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26747, 'nvi', 'João', 'jo', 16, 20, 'Digo-lhes que certamente vocês chorarão e se lamentarão, mas o mundo se alegrará. Vocês se entristecerão, mas a tristeza de vocês se transformará em alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26748, 'nvi', 'João', 'jo', 16, 21, 'A mulher que está dando à luz sente dores, porque chegou a sua hora; mas, quando o bebê nasce, ela esquece a angústia, por causa da alegria de ter nascido no mundo um menino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26749, 'nvi', 'João', 'jo', 16, 22, 'Assim acontece com vocês: agora é hora de tristeza para vocês, mas eu os verei outra vez, e vocês se alegrarão, e ninguém lhes tirará essa alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26750, 'nvi', 'João', 'jo', 16, 23, 'Naquele dia vocês não me perguntarão mais nada. Eu lhes asseguro que meu Pai lhes dará tudo o que pedirem em meu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26751, 'nvi', 'João', 'jo', 16, 24, 'Até agora vocês não pediram nada em meu nome. Peçam e receberão, para que a alegria de vocês seja completa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26752, 'nvi', 'João', 'jo', 16, 25, '"Embora eu tenha falado por meio de figuras, vem a hora em que não usarei mais esse tipo de linguagem, mas lhes falarei abertamente a respeito de meu Pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26753, 'nvi', 'João', 'jo', 16, 26, 'Nesse dia, vocês pedirão em meu nome. Não digo que pedirei ao Pai em favor de vocês,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26754, 'nvi', 'João', 'jo', 16, 27, 'pois o próprio Pai os ama, porquanto vocês me amaram e creram que eu vim de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26755, 'nvi', 'João', 'jo', 16, 28, 'Eu vim do Pai e entrei no mundo; agora deixo o mundo e volto para o Pai".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26756, 'nvi', 'João', 'jo', 16, 29, 'Então os discípulos de Jesus disseram: "Agora estás falando claramente, e não por figuras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26757, 'nvi', 'João', 'jo', 16, 30, 'Agora podemos perceber que sabes todas as coisas e nem precisas que te façam perguntas. Por isso cremos que vieste de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26758, 'nvi', 'João', 'jo', 16, 31, 'Respondeu Jesus: "Agora vocês crêem? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26759, 'nvi', 'João', 'jo', 16, 32, 'Aproxima-se a hora, e já chegou, quando vocês serão espalhados cada um para a sua casa. Vocês me deixarão sozinho. Mas, eu não estou sozinho, pois meu Pai está comigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26760, 'nvi', 'João', 'jo', 16, 33, '"Eu lhes disse essas coisas para que em mim vocês tenham paz. Neste mundo vocês terão aflições; contudo, tenham ânimo! Eu venci o mundo".');
