INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26761, 'nvi', 'João', 'jo', 17, 1, 'Depois de dizer isso, Jesus olhou para o céu e orou: "Pai, chegou a hora. Glorifica o teu Filho, para que o teu Filho te glorifique.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26762, 'nvi', 'João', 'jo', 17, 2, 'Pois lhe deste autoridade sobre toda a humanidade, para que conceda a vida eterna a todos os que lhe deste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26763, 'nvi', 'João', 'jo', 17, 3, 'Esta é a vida eterna: que te conheçam, o único Deus verdadeiro, e a Jesus Cristo, a quem enviaste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26764, 'nvi', 'João', 'jo', 17, 4, 'Eu te glorifiquei na terra, completando a obra que me deste para fazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26765, 'nvi', 'João', 'jo', 17, 5, 'E agora, Pai, glorifica-me junto a ti, com a glória que eu tinha contigo antes que o mundo existisse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26766, 'nvi', 'João', 'jo', 17, 6, '"Eu revelei teu nome àqueles que do mundo me deste. Eles eram teus; tu os deste a mim, e eles têm guardado a tua palavra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26767, 'nvi', 'João', 'jo', 17, 7, 'Agora eles sabem que tudo o que me deste vem de ti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26768, 'nvi', 'João', 'jo', 17, 8, 'Pois eu lhes transmiti as palavras que me deste, e eles as aceitaram. Eles reconheceram de fato que vim de ti e creram que me enviaste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26769, 'nvi', 'João', 'jo', 17, 9, 'Eu rogo por eles. Não estou rogando pelo mundo, mas por aqueles que me deste, pois são teus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26770, 'nvi', 'João', 'jo', 17, 10, 'Tudo o que tenho é teu, e tudo o que tens é meu. E eu tenho sido glorificado por meio deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26771, 'nvi', 'João', 'jo', 17, 11, 'Não ficarei mais no mundo, mas eles ainda estão no mundo, e eu vou para ti. Pai santo, protege-os em teu nome, o nome que me deste, para que sejam um, assim como somos um.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26772, 'nvi', 'João', 'jo', 17, 12, 'Enquanto estava com eles, eu os protegi e os guardei pelo nome que me deste. Nenhum deles se perdeu, a não ser aquele que estava destinado à perdição, para que se cumprisse a Escritura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26773, 'nvi', 'João', 'jo', 17, 13, '"Agora vou para ti, mas digo estas coisas enquanto ainda estou no mundo, para que eles tenham a plenitude da minha alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26774, 'nvi', 'João', 'jo', 17, 14, 'Dei-lhes a tua palavra, e o mundo os odiou, pois eles não são do mundo, como eu também não sou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26775, 'nvi', 'João', 'jo', 17, 15, 'Não rogo que os tires do mundo, mas que os protejas do Maligno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26776, 'nvi', 'João', 'jo', 17, 16, 'Eles não são do mundo, como eu também não sou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26777, 'nvi', 'João', 'jo', 17, 17, 'Santifica-os na verdade; a tua palavra é a verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26778, 'nvi', 'João', 'jo', 17, 18, 'Assim como me enviaste ao mundo, eu os enviei ao mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26779, 'nvi', 'João', 'jo', 17, 19, 'Em favor deles eu me santifico, para que também eles sejam santificados pela verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26780, 'nvi', 'João', 'jo', 17, 20, '"Minha oração não é apenas por eles. Rogo também por aqueles que crerão em mim, por meio da mensagem deles,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26781, 'nvi', 'João', 'jo', 17, 21, 'para que todos sejam um, Pai, como tu estás em mim e eu em ti. Que eles também estejam em nós, para que o mundo creia que tu me enviaste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26782, 'nvi', 'João', 'jo', 17, 22, 'Dei-lhes a glória que me deste, para que eles sejam um, assim como nós somos um:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26783, 'nvi', 'João', 'jo', 17, 23, 'eu neles e tu em mim. Que eles sejam levados à plena unidade, para que o mundo saiba que tu me enviaste, e os amaste como igualmente me amaste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26784, 'nvi', 'João', 'jo', 17, 24, '"Pai, quero que os que me deste estejam comigo onde eu estou e vejam a minha glória, a glória que me deste porque me amaste antes da criação do mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26785, 'nvi', 'João', 'jo', 17, 25, '"Pai justo, embora o mundo não te conheça, eu te conheço, e estes sabem que me enviaste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26786, 'nvi', 'João', 'jo', 17, 26, 'Eu os fiz conhecer o teu nome, e continuarei a fazê-lo, a fim de que o amor que tens por mim esteja neles, e eu neles esteja".');
