INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26787, 'nvi', 'João', 'jo', 18, 1, 'Tendo terminado de orar, Jesus saiu com os seus discípulos e atravessou o vale do Cedrom. Do outro lado havia um olival, onde entrou com eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26788, 'nvi', 'João', 'jo', 18, 2, 'Ora, Judas, o traidor, conhecia aquele lugar, porque Jesus muitas vezes se reunira ali com os seus discípulos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26789, 'nvi', 'João', 'jo', 18, 3, 'Então Judas foi para o olival, levando consigo um destacamento de soldados e alguns guardas enviados pelos chefes dos sacerdotes e fariseus, levando tochas, lanternas e armas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26790, 'nvi', 'João', 'jo', 18, 4, 'Jesus, sabendo tudo o que lhe ia acontecer, saiu e lhes perguntou: "A quem vocês estão procurando? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26791, 'nvi', 'João', 'jo', 18, 5, '"A Jesus de Nazaré", responderam eles. "Sou eu", disse Jesus. ( E Judas, o traidor, estava com eles. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26792, 'nvi', 'João', 'jo', 18, 6, 'Quando Jesus disse: "Sou eu", eles recuaram e caíram por terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26793, 'nvi', 'João', 'jo', 18, 7, 'Novamente lhes perguntou: "A quem procuram? " E eles disseram: "A Jesus de Nazaré".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26794, 'nvi', 'João', 'jo', 18, 8, 'Respondeu Jesus: "Já lhes disse que sou eu. Se vocês estão me procurando, deixem ir embora estes homens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26795, 'nvi', 'João', 'jo', 18, 9, 'Isso aconteceu para que se cumprissem as palavras que ele dissera: "Não perdi nenhum dos que me deste".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26796, 'nvi', 'João', 'jo', 18, 10, 'Simão Pedro, que trazia uma espada, tirou-a e feriu o servo do sumo sacerdote, decepando-lhe a orelha direita. ( O nome daquele servo era Malco. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26797, 'nvi', 'João', 'jo', 18, 11, 'Jesus, porém, ordenou a Pedro: "Guarde a espada! Acaso não haverei de beber o cálice que o Pai me deu? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26798, 'nvi', 'João', 'jo', 18, 12, 'Assim, o destacamento de soldados com o seu comandante e os guardas dos judeus prenderam Jesus. Amarraram-no');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26799, 'nvi', 'João', 'jo', 18, 13, 'e o levaram primeiramente a Anás, que era sogro de Caifás, o sumo sacerdote naquele ano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26800, 'nvi', 'João', 'jo', 18, 14, 'Caifás era quem tinha dito aos judeus que seria bom que um homem morresse pelo povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26801, 'nvi', 'João', 'jo', 18, 15, 'Simão Pedro e outro discípulo estavam seguindo Jesus. Por ser conhecido do sumo sacerdote, este discípulo entrou com Jesus no pátio da casa do sumo sacerdote,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26802, 'nvi', 'João', 'jo', 18, 16, 'mas Pedro teve que ficar esperando do lado de fora da porta. O outro discípulo, que era conhecido do sumo sacerdote, voltou, falou com a moça encarregada da porta e fez Pedro entrar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26803, 'nvi', 'João', 'jo', 18, 17, 'Ela então perguntou a Pedro: "Você não é um dos discípulos desse homem? " Ele respondeu: "Não sou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26804, 'nvi', 'João', 'jo', 18, 18, 'Fazia frio; os servos e os guardas estavam ao redor de uma fogueira que haviam feito para se aquecerem. Pedro também estava em pé com eles, aquecendo-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26805, 'nvi', 'João', 'jo', 18, 19, 'Enquanto isso, o sumo sacerdote interrogou Jesus acerca dos seus discípulos e dos seus ensinamentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26806, 'nvi', 'João', 'jo', 18, 20, 'Respondeu-lhe Jesus: "Eu falei abertamente ao mundo; sempre ensinei nas sinagogas e no templo, onde todos os judeus se reúnem. Nada disse em segredo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26807, 'nvi', 'João', 'jo', 18, 21, 'Por que me interrogas? Pergunta aos que me ouviram. Certamente eles sabem o que eu disse".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26808, 'nvi', 'João', 'jo', 18, 22, 'Quando Jesus disse isso, um dos guardas que estava perto bateu-lhe no rosto. "Isso é jeito de responder ao sumo sacerdote? ", perguntou ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26809, 'nvi', 'João', 'jo', 18, 23, 'Respondeu Jesus: "Se eu disse algo de mal, denuncie o mal. Mas se falei a verdade, por que me bateu? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26810, 'nvi', 'João', 'jo', 18, 24, 'Então, Anás enviou Jesus, de mãos amarradas, a Caifás, o sumo sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26811, 'nvi', 'João', 'jo', 18, 25, 'Enquanto Simão Pedro estava se aquecendo, perguntaram-lhe: "Você não é um dos discípulos dele? " Ele negou, dizendo: "Não sou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26812, 'nvi', 'João', 'jo', 18, 26, 'Um dos servos do sumo sacerdote, parente do homem cuja orelha Pedro decepara, insistiu: "Eu não o vi com ele no olival? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26813, 'nvi', 'João', 'jo', 18, 27, 'Mais uma vez Pedro negou, e no mesmo instante um galo cantou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26814, 'nvi', 'João', 'jo', 18, 28, 'Em seguida, de Caifás os judeus levaram Jesus para o Pretório. Já estava amanhecendo e, para evitar contaminação cerimonial, os judeus não entraram no Pretório; pois queriam participar da Páscoa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26815, 'nvi', 'João', 'jo', 18, 29, 'Então Pilatos saiu para falar com eles e perguntou: "Que acusação vocês têm contra este homem? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26816, 'nvi', 'João', 'jo', 18, 30, 'Responderam eles: "Se ele não fosse criminoso, não o teríamos entregado a ti".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26817, 'nvi', 'João', 'jo', 18, 31, 'Pilatos disse: "Levem-no e julguem-no conforme a lei de vocês". "Mas nós não temos o direito de executar ninguém", protestaram os judeus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26818, 'nvi', 'João', 'jo', 18, 32, 'Isso aconteceu para que se cumprissem as palavras que Jesus tinha dito, indicando a espécie de morte que ele estava para sofrer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26819, 'nvi', 'João', 'jo', 18, 33, 'Pilatos então voltou para o Pretório, chamou Jesus e lhe perguntou: "Você é o rei dos judeus? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26820, 'nvi', 'João', 'jo', 18, 34, 'Perguntou-lhe Jesus: "Essa pergunta é tua, ou outros te falaram a meu respeito? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26821, 'nvi', 'João', 'jo', 18, 35, 'Respondeu Pilatos: "Acaso sou judeu? Foram o seu povo e os chefes dos sacerdotes que entregaram você a mim. Que é que você fez? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26822, 'nvi', 'João', 'jo', 18, 36, 'Disse Jesus: "O meu Reino não é deste mundo. Se fosse, os meus servos lutariam para impedir que os judeus me prendessem. Mas agora o meu Reino não é daqui".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26823, 'nvi', 'João', 'jo', 18, 37, '"Então, você é rei! ", disse Pilatos. Jesus respondeu: "Tu dizes que sou rei. De fato, por esta razão nasci e para isto vim ao mundo: para testemunhar da verdade. Todos os que são da verdade me ouvem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26824, 'nvi', 'João', 'jo', 18, 38, '"Que é a verdade? ", perguntou Pilatos. Ele disse isso e saiu novamente para onde estavam os judeus e disse: "Não acho nele motivo algum de acusação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26825, 'nvi', 'João', 'jo', 18, 39, 'Contudo, segundo o costume de vocês, devo libertar um prisioneiro por ocasião da Páscoa. Querem que eu solte ‘o rei dos judeus’? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26826, 'nvi', 'João', 'jo', 18, 40, 'Eles, em resposta, gritaram: "Não, ele não! Queremos Barrabás! " Ora, Barrabás era um bandido.');
