INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26097, 'nvi', 'João', 'jo', 2, 1, 'No terceiro dia houve um casamento em Caná da Galiléia. A mãe de Jesus estava ali;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26098, 'nvi', 'João', 'jo', 2, 2, 'Jesus e seus discípulos também haviam sido convidados para o casamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26099, 'nvi', 'João', 'jo', 2, 3, 'Tendo acabado o vinho, a mãe de Jesus lhe disse: "Eles não têm mais vinho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26100, 'nvi', 'João', 'jo', 2, 4, 'Respondeu Jesus: "Que temos nós em comum, mulher? A minha hora ainda não chegou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26101, 'nvi', 'João', 'jo', 2, 5, 'Sua mãe disse aos serviçais: "Façam tudo o que ele lhes mandar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26102, 'nvi', 'João', 'jo', 2, 6, 'Ali perto havia seis potes de pedra, do tipo usado pelos judeus para as purificações cerimoniais; em cada pote cabia entre oitenta a cento e vinte litros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26103, 'nvi', 'João', 'jo', 2, 7, 'Disse Jesus aos serviçais: "Encham os potes com água". E os encheram até à borda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26104, 'nvi', 'João', 'jo', 2, 8, 'Então lhes disse: "Agora, levem um pouco do vinho ao encarregado da festa". Eles assim o fizeram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26105, 'nvi', 'João', 'jo', 2, 9, 'e o encarregado da festa provou a água que fora transformada em vinho, sem saber de onde este viera, embora o soubessem os serviçais que haviam tirado a água. Então chamou o noivo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26106, 'nvi', 'João', 'jo', 2, 10, 'e disse: "Todos servem primeiro o melhor vinho e, depois que os convidados já beberam bastante, o vinho inferior é servido; mas você guardou o melhor até agora".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26107, 'nvi', 'João', 'jo', 2, 11, 'Este sinal miraculoso, em Caná da Galiléia, foi o primeiro que Jesus realizou. Revelou assim a sua glória, e os seus discípulos creram nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26108, 'nvi', 'João', 'jo', 2, 12, 'Depois disso ele desceu a Cafarnaum com sua mãe, seus irmãos e seus discípulos. Ali ficaram durante alguns dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26109, 'nvi', 'João', 'jo', 2, 13, 'Quando já estava chegando a Páscoa judaica, Jesus subiu a Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26110, 'nvi', 'João', 'jo', 2, 14, 'No pátio do templo viu alguns vendendo bois, ovelhas e pombas, e outros assentados diante de mesas, trocando dinheiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26111, 'nvi', 'João', 'jo', 2, 15, 'Então ele fez um chicote de cordas e expulsou todos do templo, bem como as ovelhas e os bois; espalhou as moedas dos cambistas e virou as suas mesas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26112, 'nvi', 'João', 'jo', 2, 16, 'Aos que vendiam pombas disse: "Tirem estas coisas daqui! Parem de fazer da casa de meu Pai um mercado! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26113, 'nvi', 'João', 'jo', 2, 17, 'Seus discípulos lembraram-se que está escrito: "O zelo pela tua casa me consumirá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26114, 'nvi', 'João', 'jo', 2, 18, 'Então os judeus lhe perguntaram: "Que sinal miraculoso o senhor pode mostrar-nos como prova da sua autoridade para fazer tudo isso? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26115, 'nvi', 'João', 'jo', 2, 19, 'Jesus lhes respondeu: "Destruam este templo, e eu o levantarei em três dias".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26116, 'nvi', 'João', 'jo', 2, 20, 'Os judeus responderam: "Este templo levou quarenta e seis anos para ser edificado, e o senhor vai levantá-lo em três dias? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26117, 'nvi', 'João', 'jo', 2, 21, 'Mas o templo do qual ele falava era o seu corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26118, 'nvi', 'João', 'jo', 2, 22, 'Depois que ressuscitou dos mortos, os seus discípulos lembraram-se do que ele tinha dito. Então creram na Escritura e na palavra que Jesus dissera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26119, 'nvi', 'João', 'jo', 2, 23, 'Enquanto estava em Jerusalém, na festa da Páscoa, muitos viram os sinais miraculosos que ele estava realizando e creram em seu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26120, 'nvi', 'João', 'jo', 2, 24, 'Mas Jesus não se confiava a eles, pois conhecia a todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26121, 'nvi', 'João', 'jo', 2, 25, 'Não precisava que ninguém lhe desse testemunho a respeito do homem, pois ele bem sabia o que havia no homem.');
