INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26869, 'nvi', 'João', 'jo', 20, 1, 'No primeiro dia da semana, bem cedo, estando ainda escuro, Maria Madalena chegou ao sepulcro e viu que a pedra da entrada tinha sido removida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26870, 'nvi', 'João', 'jo', 20, 2, 'Então correu ao encontro de Simão Pedro e do outro discípulo, aquele a quem Jesus amava, e disse: "Tiraram o Senhor do sepulcro, e não sabemos onde o colocaram! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26871, 'nvi', 'João', 'jo', 20, 3, 'Pedro e o outro discípulo saíram e foram para o sepulcro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26872, 'nvi', 'João', 'jo', 20, 4, 'Os dois corriam, mas o outro discípulo foi mais rápido que Pedro e chegou primeiro ao sepulcro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26873, 'nvi', 'João', 'jo', 20, 5, 'Ele se curvou e olhou para dentro, viu as faixas de linho ali, mas não entrou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26874, 'nvi', 'João', 'jo', 20, 6, 'A seguir Simão Pedro, que vinha atrás dele, chegou, entrou no sepulcro e viu as faixas de linho,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26875, 'nvi', 'João', 'jo', 20, 7, 'bem como o lenço que estivera sobre a cabeça de Jesus. Ele estava dobrado à parte, separado das faixas de linho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26876, 'nvi', 'João', 'jo', 20, 8, 'Depois o outro discípulo, que chegara primeiro ao sepulcro, também entrou. Ele viu e creu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26877, 'nvi', 'João', 'jo', 20, 9, '( Eles ainda não haviam compreendido que, conforme a Escritura, era necessário que Jesus ressuscitasse dos mortos. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26878, 'nvi', 'João', 'jo', 20, 10, 'Os discípulos voltaram para casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26879, 'nvi', 'João', 'jo', 20, 11, 'Maria, porém, ficou à entrada do sepulcro, chorando. Enquanto chorava, curvou-se para olhar dentro do sepulcro');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26880, 'nvi', 'João', 'jo', 20, 12, 'e viu dois anjos vestidos de branco, sentados onde estivera o corpo de Jesus, um à cabeceira e o outro aos pés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26881, 'nvi', 'João', 'jo', 20, 13, 'Eles lhe perguntaram: "Mulher, por que você está chorando? " "Levaram embora o meu Senhor", respondeu ela, "e não sei onde o puseram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26882, 'nvi', 'João', 'jo', 20, 14, 'Nisso ela se voltou e viu Jesus ali, em pé, mas não o reconheceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26883, 'nvi', 'João', 'jo', 20, 15, 'Disse ele: "Mulher, por que está chorando? Quem você está procurando? " Pensando que fosse o jardineiro, ela disse: "Se o senhor o levou embora, diga-me onde o colocou, e eu o levarei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26884, 'nvi', 'João', 'jo', 20, 16, 'Jesus lhe disse: "Maria! " Então, voltando-se para ele, Maria exclamou em aramaico: "Rabôni! " ( que significa Mestre ).');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26885, 'nvi', 'João', 'jo', 20, 17, 'Jesus disse: "Não me segure, pois ainda não voltei para o Pai. Vá, porém, a meus irmãos e diga-lhes: Estou voltando para meu Pai e Pai de vocês, para meu Deus e Deus de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26886, 'nvi', 'João', 'jo', 20, 18, 'Maria Madalena foi e anunciou aos discípulos: "Eu vi o Senhor! " E contou o que ele lhe dissera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26887, 'nvi', 'João', 'jo', 20, 19, 'Ao cair da tarde daquele primeiro dia da semana, estando os discípulos reunidos a portas trancadas, por medo dos judeus, Jesus entrou, pôs-se no meio deles e disse: "Paz seja com vocês! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26888, 'nvi', 'João', 'jo', 20, 20, 'Tendo dito isso, mostrou-lhes as mãos e o lado. Os discípulos alegraram-se quando viram o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26889, 'nvi', 'João', 'jo', 20, 21, 'Novamente Jesus disse: "Paz seja com vocês! Assim como o Pai me enviou, eu os envio".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26890, 'nvi', 'João', 'jo', 20, 22, 'E com isso, soprou sobre eles e disse: "Recebam o Espírito Santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26891, 'nvi', 'João', 'jo', 20, 23, 'Se perdoarem os pecados de alguém, estarão perdoados; se não os perdoarem, não estarão perdoados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26892, 'nvi', 'João', 'jo', 20, 24, 'Tomé, chamado Dídimo, um dos Doze, não estava com os discípulos quando Jesus apareceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26893, 'nvi', 'João', 'jo', 20, 25, 'Os outros discípulos lhe disseram: "Vimos o Senhor! " Mas ele lhes disse: "Se eu não vir as marcas dos pregos nas suas mãos, não colocar o meu dedo onde estavam os pregos e não puser a minha mão no seu lado, não crerei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26894, 'nvi', 'João', 'jo', 20, 26, 'Uma semana mais tarde, os seus discípulos estavam outra vez ali, e Tomé com eles. Apesar de estarem trancadas as portas, Jesus entrou, pôs-se no meio deles e disse: "Paz seja com vocês! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26895, 'nvi', 'João', 'jo', 20, 27, 'E Jesus disse a Tomé: "Coloque o seu dedo aqui; veja as minhas mãos. Estenda a mão e coloque-a no meu lado. Pare de duvidar e creia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26896, 'nvi', 'João', 'jo', 20, 28, 'Disse-lhe Tomé: "Senhor meu e Deus meu! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26897, 'nvi', 'João', 'jo', 20, 29, 'Então Jesus lhe disse: "Porque me viu, você creu? Felizes os que não viram e creram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26898, 'nvi', 'João', 'jo', 20, 30, 'Jesus realizou na presença dos seus discípulos muitos outros sinais miraculosos, que não estão registrados neste livro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26899, 'nvi', 'João', 'jo', 20, 31, 'Mas estes foram escritos para que vocês creiam que Jesus é o Cristo, o Filho de Deus e, crendo, tenham vida em seu nome.');
