INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26900, 'nvi', 'João', 'jo', 21, 1, 'Depois disso Jesus apareceu novamente aos seus discípulos, à margem do mar de Tiberíades. Foi assim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26901, 'nvi', 'João', 'jo', 21, 2, 'Estavam juntos Simão Pedro; Tomé, chamado Dídimo; Natanael, de Caná da Galiléia; os filhos de Zebedeu; e dois outros discípulos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26902, 'nvi', 'João', 'jo', 21, 3, '"Vou pescar", disse-lhes Simão Pedro. E eles disseram: "Nós vamos com você". Eles foram e entraram no barco, mas naquela noite não pegaram nada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26903, 'nvi', 'João', 'jo', 21, 4, 'Ao amanhecer, Jesus estava na praia, mas os discípulos não o reconheceram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26904, 'nvi', 'João', 'jo', 21, 5, 'Ele lhes perguntou: "Filhos, vocês têm algo para comer? " "Não", responderam eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26905, 'nvi', 'João', 'jo', 21, 6, 'Ele disse: "Lancem a rede do lado direito do barco e vocês encontrarão". Eles a lançaram, e não conseguiam recolher a rede, tal era a quantidade de peixes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26906, 'nvi', 'João', 'jo', 21, 7, 'O discípulo a quem Jesus amava disse a Pedro: "É o Senhor! " Simão Pedro, ouvindo-o dizer isso, vestiu a capa, pois a havia tirado, e lançou-se ao mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26907, 'nvi', 'João', 'jo', 21, 8, 'Os outros discípulos vieram no barco, arrastando a rede cheia de peixes, pois estavam apenas a cerca de noventa metros da praia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26908, 'nvi', 'João', 'jo', 21, 9, 'Quando desembarcaram, viram ali uma fogueira, peixe sobre brasas, e um pouco de pão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26909, 'nvi', 'João', 'jo', 21, 10, 'Disse-lhes Jesus: "Tragam alguns dos peixes que acabaram de pescar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26910, 'nvi', 'João', 'jo', 21, 11, 'Simão Pedro entrou no barco e arrastou a rede para a praia. Ela estava cheia: tinha cento e cinqüenta e três grandes peixes. Embora houvesse tantos peixes, a rede não se rompeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26911, 'nvi', 'João', 'jo', 21, 12, 'Jesus lhes disse: "Venham comer". Nenhum dos discípulos tinha coragem de lhe perguntar: "Quem és tu? " Sabiam que era o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26912, 'nvi', 'João', 'jo', 21, 13, 'Jesus aproximou-se, tomou o pão e o deu a eles, fazendo o mesmo com o peixe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26913, 'nvi', 'João', 'jo', 21, 14, 'Esta foi a terceira vez que Jesus apareceu aos seus discípulos, depois que ressuscitou dos mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26914, 'nvi', 'João', 'jo', 21, 15, 'Depois de comerem, Jesus perguntou a Simão Pedro: "Simão, filho de João, você me ama realmente mais do que estes? " Disse ele: "Sim, Senhor, tu sabes que te amo". Disse Jesus: "Cuide dos meus cordeiros".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26915, 'nvi', 'João', 'jo', 21, 16, 'Novamente Jesus disse: "Simão, filho de João, você realmente me ama? " Ele respondeu: "Sim, Senhor tu sabes que te amo". Disse Jesus: "Pastoreie as minhas ovelhas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26916, 'nvi', 'João', 'jo', 21, 17, 'Pela terceira vez, ele lhe disse: "Simão, filho de João, você me ama? " Pedro ficou magoado por Jesus lhe ter perguntado pela terceira vez "Você me ama? " e lhe disse: "Senhor, tu sabes todas as coisas e sabes que te amo". Disse-lhe Jesus: "Cuide das minhas ovelhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26917, 'nvi', 'João', 'jo', 21, 18, 'Digo-lhe a verdade: Quando você era mais jovem, vestia-se e ia para onde queria; mas quando for velho, estenderá as mãos e outra pessoa o vestirá e o levará para onde você não deseja ir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26918, 'nvi', 'João', 'jo', 21, 19, 'Jesus disse isso para indicar o tipo de morte com a qual Pedro iria glorificar a Deus. E então lhe disse: "Siga-me! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26919, 'nvi', 'João', 'jo', 21, 20, 'Pedro voltou-se e viu que o discípulo a quem Jesus amava os seguia. ( Este era o que se inclinara para Jesus durante a ceia e perguntara: "Senhor, quem te irá trair? " )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26920, 'nvi', 'João', 'jo', 21, 21, 'Quando Pedro o viu, perguntou: "Senhor, e quanto a ele? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26921, 'nvi', 'João', 'jo', 21, 22, 'Respondeu Jesus: "Se eu quiser que ele permaneça vivo até que eu volte, o que lhe importa? Siga-me você".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26922, 'nvi', 'João', 'jo', 21, 23, 'Foi por isso que se espalhou entre os irmãos o rumor de que aquele discípulo não iria morrer. Mas Jesus não disse que ele não iria morrer; apenas disse: "Se eu quiser que ele permaneça vivo até que eu volte, o que lhe importa? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26923, 'nvi', 'João', 'jo', 21, 24, 'Este é o discípulo que dá testemunho dessas coisas e que as registrou. Sabemos que o seu testemunho é verdadeiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26924, 'nvi', 'João', 'jo', 21, 25, 'Jesus fez também muitas outras coisas. Se cada uma delas fosse escrita, penso que nem mesmo no mundo inteiro haveria espaço suficiente para os livros que seriam escritos.');
