INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26122, 'nvi', 'João', 'jo', 3, 1, 'Havia um fariseu chamado Nicodemos, uma autoridade entre os judeus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26123, 'nvi', 'João', 'jo', 3, 2, 'Ele veio a Jesus, à noite, e disse: "Mestre, sabemos que ensinas da parte de Deus, pois ninguém pode realizar os sinais miraculosos que estás fazendo, se Deus não estiver com ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26124, 'nvi', 'João', 'jo', 3, 3, 'Em resposta, Jesus declarou: "Digo-lhe a verdade: Ninguém pode ver o Reino de Deus, se não nascer de novo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26125, 'nvi', 'João', 'jo', 3, 4, 'Perguntou Nicodemos: "Como alguém pode nascer, sendo velho? É claro que não pode entrar pela segunda vez no ventre de sua mãe e renascer! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26126, 'nvi', 'João', 'jo', 3, 5, 'Respondeu Jesus: "Digo-lhe a verdade: Ninguém pode entrar no Reino de Deus, se não nascer da água e do Espírito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26127, 'nvi', 'João', 'jo', 3, 6, 'O que nasce da carne é carne, mas o que nasce do Espírito é espírito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26128, 'nvi', 'João', 'jo', 3, 7, 'Não se surpreenda pelo fato de eu ter dito: É necessário que vocês nasçam de novo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26129, 'nvi', 'João', 'jo', 3, 8, 'O vento sopra onde quer. Você o escuta, mas não pode dizer de onde vem nem para onde vai. Assim acontece com todos os nascidos do Espírito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26130, 'nvi', 'João', 'jo', 3, 9, 'Perguntou Nicodemos: "Como pode ser isso? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26131, 'nvi', 'João', 'jo', 3, 10, 'Disse Jesus: "Você é mestre em Israel e não entende essas coisas?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26132, 'nvi', 'João', 'jo', 3, 11, 'Asseguro-lhe que nós falamos do que conhecemos e testemunhamos do que vimos, mas mesmo assim vocês não aceitam o nosso testemunho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26133, 'nvi', 'João', 'jo', 3, 12, 'Eu lhes falei de coisas terrenas e vocês não creram; como crerão se lhes falar de coisas celestiais?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26134, 'nvi', 'João', 'jo', 3, 13, 'Ninguém jamais subiu ao céu, a não ser aquele que veio do céu: o Filho do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26135, 'nvi', 'João', 'jo', 3, 14, 'Da mesma forma como Moisés levantou a serpente no deserto, assim também é necessário que o Filho do homem seja levantado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26136, 'nvi', 'João', 'jo', 3, 15, 'para que todo o que nele crer tenha a vida eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26137, 'nvi', 'João', 'jo', 3, 16, '"Porque Deus tanto amou o mundo que deu o seu Filho Unigênito, para que todo o que nele crer não pereça, mas tenha a vida eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26138, 'nvi', 'João', 'jo', 3, 17, 'Pois Deus enviou o seu Filho ao mundo, não para condenar o mundo, mas para que este fosse salvo por meio dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26139, 'nvi', 'João', 'jo', 3, 18, 'Quem nele crê não é condenado, mas quem não crê já está condenado, por não crer no nome do Filho Unigênito de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26140, 'nvi', 'João', 'jo', 3, 19, 'Este é o julgamento: a luz veio ao mundo, mas os homens amaram as trevas, e não a luz, porque as suas obras eram más.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26141, 'nvi', 'João', 'jo', 3, 20, 'Quem pratica o mal odeia a luz e não se aproxima da luz, temendo que as suas obras sejam manifestas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26142, 'nvi', 'João', 'jo', 3, 21, 'Mas quem pratica a verdade vem para a luz, para que se veja claramente que as suas obras são realizadas por intermédio de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26143, 'nvi', 'João', 'jo', 3, 22, 'Depois disso Jesus foi com os seus discípulos para a terra da Judéia, onde passou algum tempo com eles e batizava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26144, 'nvi', 'João', 'jo', 3, 23, 'João também estava batizando em Enom, perto de Salim, porque havia ali muitas águas, e o povo vinha para ser batizado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26145, 'nvi', 'João', 'jo', 3, 24, '( Isto se deu antes de João ser preso. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26146, 'nvi', 'João', 'jo', 3, 25, 'Surgiu uma discussão entre alguns discípulos de João e um certo judeu, a respeito da purificação cerimonial.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26147, 'nvi', 'João', 'jo', 3, 26, 'Eles se dirigiram a João e lhe disseram: "Mestre, aquele homem que estava contigo no outro lado do Jordão, do qual testemunhaste, está batizando, e todos estão se dirigindo a ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26148, 'nvi', 'João', 'jo', 3, 27, 'A isso João respondeu: "Uma pessoa só pode receber o que lhe é dado do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26149, 'nvi', 'João', 'jo', 3, 28, 'Vocês mesmos são testemunhas de que eu disse: Eu não sou o Cristo, mas sou aquele que foi enviado adiante dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26150, 'nvi', 'João', 'jo', 3, 29, 'A noiva pertence ao noivo. O amigo que presta serviço ao noivo e que o atende e o ouve, enche-se de alegria quando ouve a voz do noivo. Esta é a minha alegria, que agora se completa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26151, 'nvi', 'João', 'jo', 3, 30, 'É necessário que ele cresça e que eu diminua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26152, 'nvi', 'João', 'jo', 3, 31, '"Aquele que vem do alto está acima de todos; aquele que é da terra pertence à terra e fala como quem é da terra. Aquele que vem do céu está acima de todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26153, 'nvi', 'João', 'jo', 3, 32, 'Ele testifica o que tem visto e ouvido, mas ninguém aceita o seu testemunho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26154, 'nvi', 'João', 'jo', 3, 33, 'Aquele que o aceita confirma que Deus é verdadeiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26155, 'nvi', 'João', 'jo', 3, 34, 'Pois aquele que Deus enviou fala as palavras de Deus, porque ele dá o Espírito sem limitações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26156, 'nvi', 'João', 'jo', 3, 35, 'O Pai ama o Filho e entregou tudo em suas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26157, 'nvi', 'João', 'jo', 3, 36, 'Quem crê no Filho tem a vida eterna; já quem rejeita o Filho não verá a vida, mas a ira de Deus permanece sobre ele".');
