INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26158, 'nvi', 'João', 'jo', 4, 1, 'Os fariseus ouviram falar que Jesus estava fazendo e batizando mais discípulos do que João,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26159, 'nvi', 'João', 'jo', 4, 2, 'embora não fosse Jesus quem batizasse, mas os seus discípulos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26160, 'nvi', 'João', 'jo', 4, 3, 'Quando o Senhor ficou sabendo disso, saiu da Judéia e voltou uma vez mais à Galiléia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26161, 'nvi', 'João', 'jo', 4, 4, 'Era-lhe necessário passar por Samaria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26162, 'nvi', 'João', 'jo', 4, 5, 'Assim, chegou a uma cidade de Samaria, chamada Sicar, perto das terras que Jacó dera a seu filho José.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26163, 'nvi', 'João', 'jo', 4, 6, 'Havia ali o poço de Jacó. Jesus, cansado da viagem, sentou-se à beira do poço. Isto se deu por volta do meio-dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26164, 'nvi', 'João', 'jo', 4, 7, 'Nisso veio uma mulher samaritana tirar água. Disse-lhe Jesus: "Dê-me um pouco de água".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26165, 'nvi', 'João', 'jo', 4, 8, '( Os seus discípulos tinham ido à cidade comprar comida. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26166, 'nvi', 'João', 'jo', 4, 9, 'A mulher samaritana lhe perguntou: "Como o senhor, sendo judeu, pede a mim, uma samaritana, água para beber? " ( Pois os judeus não se dão bem com os samaritanos. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26167, 'nvi', 'João', 'jo', 4, 10, 'Jesus lhe respondeu: "Se você conhecesse o dom de Deus e quem lhe está pedindo água, você lhe teria pedido e ele lhe teria dado água viva".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26168, 'nvi', 'João', 'jo', 4, 11, 'Disse a mulher: "O senhor não tem com que tirar a água, e o poço é fundo. Onde pode conseguir essa água viva?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26169, 'nvi', 'João', 'jo', 4, 12, 'Acaso o senhor é maior do que o nosso pai Jacó, que nos deu o poço, do qual ele mesmo bebeu, bem como seus filhos e seu gado? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26170, 'nvi', 'João', 'jo', 4, 13, 'Jesus respondeu: "Quem beber desta água terá sede outra vez,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26171, 'nvi', 'João', 'jo', 4, 14, 'mas quem beber da água que eu lhe der nunca mais terá sede. Pelo contrário, a água que eu lhe der se tornará nele uma fonte de água a jorrar para a vida eterna".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26172, 'nvi', 'João', 'jo', 4, 15, 'A mulher lhe disse: "Senhor, dê-me dessa água, para que eu não tenha mais sede, nem precise voltar aqui para tirar água".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26173, 'nvi', 'João', 'jo', 4, 16, 'Ele lhe disse: "Vá, chame o seu marido e volte".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26174, 'nvi', 'João', 'jo', 4, 17, '"Não tenho marido", respondeu ela. Disse-lhe Jesus: "Você falou corretamente, dizendo que não tem marido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26175, 'nvi', 'João', 'jo', 4, 18, 'O fato é que você já teve cinco; e o homem com quem agora vive não é seu marido. O que você acabou de dizer é verdade".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26176, 'nvi', 'João', 'jo', 4, 19, 'Disse a mulher: "Senhor, vejo que é profeta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26177, 'nvi', 'João', 'jo', 4, 20, 'Nossos antepassados adoraram neste monte, mas vocês, judeus, dizem que Jerusalém é o lugar onde se deve adorar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26178, 'nvi', 'João', 'jo', 4, 21, 'Jesus declarou: "Creia em mim, mulher: está próxima a hora em que vocês não adorarão o Pai nem neste monte, nem em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26179, 'nvi', 'João', 'jo', 4, 22, 'Vocês, samaritanos, adoram o que não conhecem; nós adoramos o que conhecemos, pois a salvação vem dos judeus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26180, 'nvi', 'João', 'jo', 4, 23, 'No entanto, está chegando a hora, e de fato já chegou, em que os verdadeiros adoradores adorarão o Pai em espírito e em verdade. São estes os adoradores que o Pai procura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26181, 'nvi', 'João', 'jo', 4, 24, 'Deus é espírito, e é necessário que os seus adoradores o adorem em espírito e em verdade".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26182, 'nvi', 'João', 'jo', 4, 25, 'Disse a mulher: "Eu sei que o Messias ( chamado Cristo ) está para vir. Quando ele vier, explicará tudo para nós".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26183, 'nvi', 'João', 'jo', 4, 26, 'Então Jesus declarou: "Eu sou o Messias! Eu, que estou falando com você".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26184, 'nvi', 'João', 'jo', 4, 27, 'Naquele momento os seus discípulos voltaram e ficaram surpresos ao encontrá-lo conversando com uma mulher. Mas ninguém perguntou: "Que queres saber? " ou: "Por que estás conversando com ela? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26185, 'nvi', 'João', 'jo', 4, 28, 'Então, deixando o seu cântaro, a mulher voltou à cidade e disse ao povo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26186, 'nvi', 'João', 'jo', 4, 29, '"Venham ver um homem que me disse tudo o que tenho feito. Será que ele não é o Cristo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26187, 'nvi', 'João', 'jo', 4, 30, 'Então saíram da cidade e foram para onde ele estava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26188, 'nvi', 'João', 'jo', 4, 31, 'Enquanto isso, os discípulos insistiam com ele: "Mestre, come alguma coisa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26189, 'nvi', 'João', 'jo', 4, 32, 'Mas ele lhes disse: "Tenho algo para comer que vocês não conhecem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26190, 'nvi', 'João', 'jo', 4, 33, 'Então os seus discípulos disseram uns aos outros: "Será que alguém lhe trouxe comida? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26191, 'nvi', 'João', 'jo', 4, 34, 'Disse Jesus: "A minha comida é fazer a vontade daquele que me enviou e concluir a sua obra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26192, 'nvi', 'João', 'jo', 4, 35, 'Vocês não dizem: ‘Daqui a quatro meses haverá a colheita’? Eu lhes digo: Abram os olhos e vejam os campos! Eles estão maduros para a colheita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26193, 'nvi', 'João', 'jo', 4, 36, 'Aquele que colhe já recebe o seu salário e colhe fruto para a vida eterna, de forma que se alegram juntos o que semeia e o que colhe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26194, 'nvi', 'João', 'jo', 4, 37, 'Assim é verdadeiro o ditado: ‘Um semeia, e outro colhe’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26195, 'nvi', 'João', 'jo', 4, 38, 'Eu os enviei para colherem o que vocês não cultivaram. Outros realizaram o trabalho árduo, e vocês vieram a usufruir do trabalho deles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26196, 'nvi', 'João', 'jo', 4, 39, 'Muitos samaritanos daquela cidade creram nele por causa do seguinte testemunho dado pela mulher: "Ele me disse tudo o que tenho feito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26197, 'nvi', 'João', 'jo', 4, 40, 'Assim, quando se aproximaram dele, os samaritanos insistiram em que ficasse com eles, e ele ficou dois dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26198, 'nvi', 'João', 'jo', 4, 41, 'E por causa da sua palavra, muitos outros creram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26199, 'nvi', 'João', 'jo', 4, 42, 'E disseram à mulher: "Agora cremos não somente por causa do que você disse, pois nós mesmos o ouvimos e sabemos que este é realmente o Salvador do mundo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26200, 'nvi', 'João', 'jo', 4, 43, 'Depois daqueles dois dias, ele partiu para a Galiléia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26201, 'nvi', 'João', 'jo', 4, 44, '( Jesus tinha afirmado que nenhum profeta tem honra em sua própria terra. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26202, 'nvi', 'João', 'jo', 4, 45, 'Quando chegou à Galiléia, os galileus deram-lhe boas-vindas. Eles tinham visto tudo o que ele fizera em Jerusalém, por ocasião da festa da Páscoa, pois também haviam estado lá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26203, 'nvi', 'João', 'jo', 4, 46, 'Mais uma vez, ele visitou Caná da Galiléia, onde tinha transformado água em vinho. E havia ali um oficial do rei, cujo filho estava doente em Cafarnaum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26204, 'nvi', 'João', 'jo', 4, 47, 'Quando ele ouviu falar que Jesus tinha chegado à Galiléia, vindo da Judéia, procurou-o e suplicou-lhe que fosse curar seu filho, que estava à beira da morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26205, 'nvi', 'João', 'jo', 4, 48, 'Disse-lhe Jesus: "Se vocês não virem sinais e maravilhas, nunca crerão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26206, 'nvi', 'João', 'jo', 4, 49, 'O oficial do rei disse: "Senhor, vem, antes que o meu filho morra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26207, 'nvi', 'João', 'jo', 4, 50, 'Jesus respondeu: "Pode ir. O seu filho continuará vivo". O homem confiou na palavra de Jesus e partiu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26208, 'nvi', 'João', 'jo', 4, 51, 'Estando ele ainda a caminho, seus servos vieram ao seu encontro com notícias de que o menino estava vivo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26209, 'nvi', 'João', 'jo', 4, 52, 'Quando perguntou a que horas o seu filho tinha melhorado, eles lhe disseram: "A febre o deixou ontem, à uma hora da tarde".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26210, 'nvi', 'João', 'jo', 4, 53, 'Então o pai percebeu que aquela fora exatamente a hora em que Jesus lhe dissera: "O seu filho continuará vivo". Assim, creram ele e todos os de sua casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26211, 'nvi', 'João', 'jo', 4, 54, 'Esse foi o segundo sinal miraculoso que Jesus realizou, depois que veio da Judéia para a Galiléia.');
