INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26212, 'nvi', 'João', 'jo', 5, 1, 'Algum tempo depois, Jesus subiu a Jerusalém para uma festa dos judeus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26213, 'nvi', 'João', 'jo', 5, 2, 'Há em Jerusalém, perto da porta das Ovelhas, um tanque que, em aramaico, é chamado Betesda, tendo cinco entradas em volta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26214, 'nvi', 'João', 'jo', 5, 3, 'Ali costumava ficar grande número de pessoas doentes e inválidas: cegos, mancos e paralíticos. Eles esperavam um movimento nas águas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26215, 'nvi', 'João', 'jo', 5, 4, 'De vez em quando descia um anjo do Senhor e agitava as águas. O primeiro que entrasse no tanque, depois de agitada as águas, era curado de qualquer doença que tivesse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26216, 'nvi', 'João', 'jo', 5, 5, 'Um dos que estavam ali era paralítico fazia trinta e oito anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26217, 'nvi', 'João', 'jo', 5, 6, 'Quando o viu deitado e soube que ele vivia naquele estado durante tanto tempo, Jesus lhe perguntou: "Você quer ser curado? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26218, 'nvi', 'João', 'jo', 5, 7, 'Disse o paralítico: "Senhor, não tenho ninguém que me ajude a entrar no tanque quando a água é agitada. Enquanto estou tentando entrar, outro chega antes de mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26219, 'nvi', 'João', 'jo', 5, 8, 'Então Jesus lhe disse: "Levante-se! Pegue a sua maca e ande".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26220, 'nvi', 'João', 'jo', 5, 9, 'Imediatamente o homem ficou curado, pegou a maca e começou a andar. Isso aconteceu num sábado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26221, 'nvi', 'João', 'jo', 5, 10, 'e, por essa razão, os judeus disseram ao homem que havia sido curado: "Hoje é sábado, não lhe é permitido carregar a maca".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26222, 'nvi', 'João', 'jo', 5, 11, 'Mas ele respondeu: "O homem que me curou me disse: ‘Pegue a sua maca e ande’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26223, 'nvi', 'João', 'jo', 5, 12, 'Então lhe perguntaram: "Quem é esse homem que lhe mandar pegar a maca e andar? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26224, 'nvi', 'João', 'jo', 5, 13, 'O homem que fora curado não tinha idéia de quem era ele, pois Jesus havia desaparecido no meio da multidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26225, 'nvi', 'João', 'jo', 5, 14, 'Mais tarde Jesus o encontrou no templo e lhe disse: "Olhe, você está curado. Não volte a pecar, para que algo pior não lhe aconteça".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26226, 'nvi', 'João', 'jo', 5, 15, 'O homem foi contar aos judeus que fora Jesus quem o tinha curado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26227, 'nvi', 'João', 'jo', 5, 16, 'Então os judeus passaram a perseguir Jesus, porque ele estava fazendo essas coisas no sábado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26228, 'nvi', 'João', 'jo', 5, 17, 'Disse-lhes Jesus: "Meu Pai continua trabalhando até hoje, e eu também estou trabalhando".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26229, 'nvi', 'João', 'jo', 5, 18, 'Por essa razão, os judeus mais ainda queriam matá-lo, pois não somente estava violando o sábado, mas também estava até mesmo dizendo que Deus era seu próprio Pai, igualando-se a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26230, 'nvi', 'João', 'jo', 5, 19, 'Jesus lhes deu esta resposta: "Eu lhes digo verdadeiramente que o Filho não pode fazer nada de si mesmo; só pode fazer o que vê o Pai fazer, porque o que o Pai faz o Filho também faz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26231, 'nvi', 'João', 'jo', 5, 20, 'Pois o Pai ama ao Filho e lhe mostra tudo o que faz. Sim, para admiração de vocês, ele lhe mostrará obras ainda maiores do que estas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26232, 'nvi', 'João', 'jo', 5, 21, 'Pois, da mesma forma que o Pai ressuscita os mortos e lhes dá vida, o Filho também dá vida a quem ele quer dá-la.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26233, 'nvi', 'João', 'jo', 5, 22, 'Além disso, o Pai a ninguém julga, mas confiou todo julgamento ao Filho,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26234, 'nvi', 'João', 'jo', 5, 23, 'para que todos honrem o Filho como honram o Pai. Aquele que não honra o Filho, também não honra o Pai que o enviou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26235, 'nvi', 'João', 'jo', 5, 24, '"Eu lhes asseguro: Quem ouve a minha palavra e crê naquele que me enviou, tem a vida eterna e não será condenado, mas já passou da morte para a vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26236, 'nvi', 'João', 'jo', 5, 25, 'Eu lhes afirmo que está chegando a hora, e já chegou, em que os mortos ouvirão a voz do Filho de Deus, e aqueles que a ouvirem, viverão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26237, 'nvi', 'João', 'jo', 5, 26, 'Pois, da mesma forma como o Pai tem vida em si mesmo, ele concedeu ao Filho ter vida em si mesmo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26238, 'nvi', 'João', 'jo', 5, 27, 'E deu-lhe autoridade para julgar, porque é o Filho do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26239, 'nvi', 'João', 'jo', 5, 28, '"Não fiquem admirados com isto, pois está chegando a hora em que todos os que estiverem nos túmulos ouvirão a sua voz');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26240, 'nvi', 'João', 'jo', 5, 29, 'e sairão; os que fizeram o bem ressuscitarão para a vida, e os que fizeram o mal ressuscitarão para serem condenados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26241, 'nvi', 'João', 'jo', 5, 30, 'Por mim mesmo, nada posso fazer; eu julgo apenas conforme ouço, e o meu julgamento é justo, pois não procuro agradar a mim mesmo, mas àquele que me enviou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26242, 'nvi', 'João', 'jo', 5, 31, '"Se testifico acerca de mim mesmo, o meu testemunho não é válido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26243, 'nvi', 'João', 'jo', 5, 32, 'Há outro que testemunha em meu favor, e sei que o seu testemunho a meu respeito é válido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26244, 'nvi', 'João', 'jo', 5, 33, '"Vocês enviaram representantes a João, e ele testemunhou da verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26245, 'nvi', 'João', 'jo', 5, 34, 'Não que eu busque testemunho humano, mas menciono isso para que vocês sejam salvos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26246, 'nvi', 'João', 'jo', 5, 35, 'João era uma candeia que queimava e irradiava luz, e durante certo tempo vocês quiseram alegrar-se com a sua luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26247, 'nvi', 'João', 'jo', 5, 36, '"Eu tenho um testemunho maior que o de João; a própria obra que o Pai me deu para concluir, e que estou realizando, testemunha que o Pai me enviou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26248, 'nvi', 'João', 'jo', 5, 37, 'E o Pai que me enviou, ele mesmo testemunhou a meu respeito. Vocês nunca ouviram a sua voz, nem viram a sua forma,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26249, 'nvi', 'João', 'jo', 5, 38, 'nem a sua palavra habita em vocês, pois não crêem naquele que ele enviou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26250, 'nvi', 'João', 'jo', 5, 39, 'Vocês estudam cuidadosamente as Escrituras, porque pensam que nelas vocês têm a vida eterna. E são as Escrituras que testemunham a meu respeito;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26251, 'nvi', 'João', 'jo', 5, 40, 'contudo, vocês não querem vir a mim para terem vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26252, 'nvi', 'João', 'jo', 5, 41, '"Eu não aceito glória dos homens,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26253, 'nvi', 'João', 'jo', 5, 42, 'mas conheço vocês. Sei que vocês não têm o amor de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26254, 'nvi', 'João', 'jo', 5, 43, 'Eu vim em nome de meu Pai, e vocês não me aceitaram; mas, se outro vier em seu próprio nome, vocês o aceitarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26255, 'nvi', 'João', 'jo', 5, 44, 'Como vocês podem crer, se aceitam glória uns dos outros, mas não procuram a glória que vem do Deus único?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26256, 'nvi', 'João', 'jo', 5, 45, '"Contudo, não pensem que eu os acusarei perante o Pai. Quem os acusa é Moisés, em quem estão as suas esperanças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26257, 'nvi', 'João', 'jo', 5, 46, 'Se vocês cressem em Moisés, creriam em mim, pois ele escreveu a meu respeito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26258, 'nvi', 'João', 'jo', 5, 47, 'Visto, porém, que não crêem no que ele escreveu, como crerão no que eu digo? "');
