INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26259, 'nvi', 'João', 'jo', 6, 1, 'Algum tempo depois Jesus partiu para a outra margem do mar da Galiléia ( ou seja, do mar de Tiberíades ),');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26260, 'nvi', 'João', 'jo', 6, 2, 'e grande multidão continuava a segui-lo, porque vira os sinais miraculosos que ele tinha realizado nos doentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26261, 'nvi', 'João', 'jo', 6, 3, 'Então Jesus subiu ao monte e sentou-se com os seus discípulos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26262, 'nvi', 'João', 'jo', 6, 4, 'Estava próxima a festa judaica da Páscoa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26263, 'nvi', 'João', 'jo', 6, 5, 'Levantando os olhos e vendo uma grande multidão que se aproximava, Jesus disse a Filipe: "Onde compraremos pão para esse povo comer? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26264, 'nvi', 'João', 'jo', 6, 6, 'Fez essa pergunta apenas para pô-lo à prova, pois já tinha em mente o que ia fazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26265, 'nvi', 'João', 'jo', 6, 7, 'Filipe lhe respondeu: "Duzentos denários não comprariam pão suficiente para que cada um recebesse um pedaço! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26266, 'nvi', 'João', 'jo', 6, 8, 'Outro discípulo, André, irmão de Simão Pedro, tomou a palavra:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26267, 'nvi', 'João', 'jo', 6, 9, '"Aqui está um rapaz com cinco pães de cevada e dois peixinhos, mas o que é isto para tanta gente? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26268, 'nvi', 'João', 'jo', 6, 10, 'Disse Jesus: "Mandem o povo assentar-se". Havia muita grama naquele lugar, e todos se assentaram. Eram cerca de cinco mil homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26269, 'nvi', 'João', 'jo', 6, 11, 'Então Jesus tomou os pães, deu graças e os repartiu entre os que estavam assentados, tanto quanto queriam; e fez o mesmo com os peixes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26270, 'nvi', 'João', 'jo', 6, 12, 'Depois que todos receberam o suficiente para comer, disse aos seus discípulos: "Ajuntem os pedaços que sobraram. Que nada seja desperdiçado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26271, 'nvi', 'João', 'jo', 6, 13, 'Então eles os ajuntaram e encheram doze cestos com os pedaços dos cinco pães de cevada deixados por aqueles que tinham comido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26272, 'nvi', 'João', 'jo', 6, 14, 'Depois de ver o sinal miraculoso que Jesus tinha realizado, o povo começou a dizer: "Sem dúvida este é o Profeta que devia vir ao mundo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26273, 'nvi', 'João', 'jo', 6, 15, 'Sabendo Jesus que pretendiam proclamá-lo rei à força, retirou-se novamente sozinho para o monte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26274, 'nvi', 'João', 'jo', 6, 16, 'Ao anoitecer seus discípulos desceram para o mar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26275, 'nvi', 'João', 'jo', 6, 17, 'entraram num barco e começaram a travessia para Cafarnaum. Já estava escuro, e Jesus ainda não tinha ido até onde eles estavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26276, 'nvi', 'João', 'jo', 6, 18, 'Soprava um vento forte, e as águas estavam agitadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26277, 'nvi', 'João', 'jo', 6, 19, 'Depois de terem remado cerca de cinco ou seis quilômetros, viram Jesus aproximando-se do barco, andando sobre o mar, e ficaram aterrorizados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26278, 'nvi', 'João', 'jo', 6, 20, 'Mas ele lhes disse: "Sou eu! Não tenham medo! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26279, 'nvi', 'João', 'jo', 6, 21, 'Então se animaram a recebê-lo no barco, e logo chegaram à praia para a qual se dirigiam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26280, 'nvi', 'João', 'jo', 6, 22, 'No dia seguinte, a multidão que tinha ficado no outro lado do mar percebeu que apenas um barco estivera ali, e que Jesus não havia entrado nele com os seus discípulos, mas que eles tinham partido sozinhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26281, 'nvi', 'João', 'jo', 6, 23, 'Então alguns barcos de Tiberíades aproximaram-se do lugar onde o povo tinha comido o pão após o Senhor ter dado graças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26282, 'nvi', 'João', 'jo', 6, 24, 'Quando a multidão percebeu que nem Jesus nem os discípulos estavam ali, entrou nos barcos e foi para Cafarnaum em busca de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26283, 'nvi', 'João', 'jo', 6, 25, 'Quando o encontraram do outro lado do mar, perguntaram-lhe: "Mestre, quando chegaste aqui? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26284, 'nvi', 'João', 'jo', 6, 26, 'Jesus respondeu: "A verdade é que vocês estão me procurando, não porque viram os sinais miraculosos, mas porque comeram os pães e ficaram satisfeitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26285, 'nvi', 'João', 'jo', 6, 27, 'Não trabalhem pela comida que se estraga, mas pela comida que permanece para a vida eterna, a qual o Filho do homem lhes dará. Deus, o Pai, nele colocou o seu selo de aprovação".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26286, 'nvi', 'João', 'jo', 6, 28, 'Então lhe perguntaram: "O que precisamos fazer para realizar as obras que Deus requer? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26287, 'nvi', 'João', 'jo', 6, 29, 'Jesus respondeu: "A obra de Deus é esta: crer naquele que ele enviou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26288, 'nvi', 'João', 'jo', 6, 30, 'Então lhe perguntaram: "Que sinal miraculoso mostrarás para que o vejamos e creiamos em ti? Que farás?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26289, 'nvi', 'João', 'jo', 6, 31, 'Os nossos antepassados comeram o maná no deserto; como está escrito: ‘Ele lhes deu a comer pão do céu’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26290, 'nvi', 'João', 'jo', 6, 32, 'Declarou-lhes Jesus: "Digo-lhes a verdade: Não foi Moisés quem lhes deu pão do céu, mas é meu Pai quem lhes dá o verdadeiro pão do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26291, 'nvi', 'João', 'jo', 6, 33, 'Pois o pão de Deus é aquele que desceu do céu e dá vida ao mundo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26292, 'nvi', 'João', 'jo', 6, 34, 'Disseram eles: "Senhor, dá-nos sempre desse pão! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26293, 'nvi', 'João', 'jo', 6, 35, 'Então Jesus declarou: "Eu sou o pão da vida. Aquele que vem a mim nunca terá fome; aquele que crê em mim nunca terá sede.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26294, 'nvi', 'João', 'jo', 6, 36, 'Mas, como eu lhes disse, vocês me viram, mas ainda não crêem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26295, 'nvi', 'João', 'jo', 6, 37, 'Todo o que o Pai me der virá a mim, e quem vier a mim eu jamais rejeitarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26296, 'nvi', 'João', 'jo', 6, 38, 'Pois desci do céu, não para fazer a minha vontade, mas para fazer a vontade daquele que me enviou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26297, 'nvi', 'João', 'jo', 6, 39, 'E esta é a vontade daquele que me enviou: que eu não perca nenhum dos que ele me deu, mas os ressuscite no último dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26298, 'nvi', 'João', 'jo', 6, 40, 'Porque a vontade de meu Pai é que todo o que olhar para o Filho e nele crer tenha a vida eterna, e eu o ressuscitarei no último dia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26299, 'nvi', 'João', 'jo', 6, 41, 'Com isso os judeus começaram a criticar Jesus, porque dissera: "Eu sou o pão que desceu do céu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26300, 'nvi', 'João', 'jo', 6, 42, 'E diziam: "Este não é Jesus, o filho de José? Não conhecemos seu pai e sua mãe? Como ele pode dizer: ‘Desci do céu’? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26301, 'nvi', 'João', 'jo', 6, 43, 'Respondeu Jesus: "Parem de fazer-me críticas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26302, 'nvi', 'João', 'jo', 6, 44, 'Ninguém pode vir a mim, se o Pai, que me enviou, não o atrair; e eu o ressuscitarei no último dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26303, 'nvi', 'João', 'jo', 6, 45, 'Está escrito nos Profetas: ‘Todos serão ensinados por Deus’. Todos os que ouvem o Pai e dele aprendem vêm a mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26304, 'nvi', 'João', 'jo', 6, 46, 'Ninguém viu o Pai, a não ser aquele que vem de Deus; somente ele viu ao Pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26305, 'nvi', 'João', 'jo', 6, 47, 'Asseguro-lhes que aquele que crê tem a vida eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26306, 'nvi', 'João', 'jo', 6, 48, 'Eu sou o pão da vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26307, 'nvi', 'João', 'jo', 6, 49, 'Os seus antepassados comeram o maná no deserto, mas morreram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26308, 'nvi', 'João', 'jo', 6, 50, 'Todavia, aqui está o pão que desce do céu, para que não morra quem dele comer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26309, 'nvi', 'João', 'jo', 6, 51, 'Eu sou o pão vivo que desceu do céu. Se alguém comer deste pão, viverá para sempre. Este pão é a minha carne, que eu darei pela vida do mundo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26310, 'nvi', 'João', 'jo', 6, 52, 'Então os judeus começaram a discutir exaltadamente entre si: "Como pode este homem nos oferecer a sua carne para comermos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26311, 'nvi', 'João', 'jo', 6, 53, 'Jesus lhes disse: "Eu lhes digo a verdade: Se vocês não comerem a carne do Filho do homem e não beberem o seu sangue, não terão vida em si mesmos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26312, 'nvi', 'João', 'jo', 6, 54, 'Todo o que come a minha carne e bebe o meu sangue tem a vida eterna, e eu o ressuscitarei no último dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26313, 'nvi', 'João', 'jo', 6, 55, 'Pois a minha carne é verdadeira comida e o meu sangue é verdadeira bebida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26314, 'nvi', 'João', 'jo', 6, 56, 'Todo o que come a minha carne e bebe o meu sangue permanece em mim e eu nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26315, 'nvi', 'João', 'jo', 6, 57, 'Da mesma forma como o Pai que vive me enviou e eu vivo por causa do Pai, assim aquele que se alimenta de mim viverá por minha causa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26316, 'nvi', 'João', 'jo', 6, 58, 'Este é o pão que desceu do céu. Os antepassados de vocês comeram o maná e morreram, mas aquele que se alimenta deste pão viverá para sempre".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26317, 'nvi', 'João', 'jo', 6, 59, 'Ele disse isso quando ensinava na sinagoga de Cafarnaum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26318, 'nvi', 'João', 'jo', 6, 60, 'Ao ouvirem isso, muitos dos seus discípulos disseram: "Dura é essa palavra. Quem consegue ouvi-la? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26319, 'nvi', 'João', 'jo', 6, 61, 'Sabendo em seu íntimo que os seus discípulos estavam se queixando do que ouviram, Jesus lhes disse: "Isso os escandaliza?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26320, 'nvi', 'João', 'jo', 6, 62, 'Que acontecerá se vocês virem o Filho do homem subir para onde estava antes!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26321, 'nvi', 'João', 'jo', 6, 63, 'O Espírito dá vida; a carne não produz nada que se aproveite. As palavras que eu lhes disse são espírito e vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26322, 'nvi', 'João', 'jo', 6, 64, 'Contudo, há alguns de vocês que não crêem". Pois Jesus sabia desde o princípio quais deles não criam e quem o iria trair.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26323, 'nvi', 'João', 'jo', 6, 65, 'E prosseguiu: "É por isso que eu lhes disse que ninguém pode vir a mim, a não ser que isto lhe seja dado pelo Pai".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26324, 'nvi', 'João', 'jo', 6, 66, 'Daquela hora em diante, muitos dos seus discípulos voltaram atrás e deixaram de segui-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26325, 'nvi', 'João', 'jo', 6, 67, 'Jesus perguntou aos Doze: "Vocês também não querem ir? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26326, 'nvi', 'João', 'jo', 6, 68, 'Simão Pedro lhe respondeu: "Senhor, para quem iremos? Tu tens as palavras de vida eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26327, 'nvi', 'João', 'jo', 6, 69, 'Nós cremos e sabemos que és o Santo de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26328, 'nvi', 'João', 'jo', 6, 70, 'Então Jesus respondeu: "Não fui eu que os escolhi, os Doze? Todavia, um de vocês é um diabo! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26329, 'nvi', 'João', 'jo', 6, 71, '( Ele se referia a Judas, filho de Simão Iscariotes, que, embora fosse um dos Doze, mais tarde haveria de traí-lo. )');
