INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26383, 'nvi', 'João', 'jo', 8, 1, 'Jesus, porém, foi para o monte das Oliveiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26384, 'nvi', 'João', 'jo', 8, 2, 'Ao amanhecer ele apareceu novamente no templo, onde todo o povo se reuniu ao seu redor, e ele se assentou para ensiná-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26385, 'nvi', 'João', 'jo', 8, 3, 'Os mestres da lei e os fariseus trouxeram-lhe uma mulher surpreendida em adultério. Fizeram-na ficar em pé diante de todos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26386, 'nvi', 'João', 'jo', 8, 4, 'e disseram a Jesus: "Mestre, esta mulher foi surpreendida em ato de adultério.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26387, 'nvi', 'João', 'jo', 8, 5, 'Na Lei, Moisés nos ordena apedrejar tais mulheres. E o senhor, que diz? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26388, 'nvi', 'João', 'jo', 8, 6, 'Eles estavam usando essa pergunta como armadilha, a fim de terem uma base para acusá-lo. Mas Jesus inclinou-se e começou a escrever no chão com o dedo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26389, 'nvi', 'João', 'jo', 8, 7, 'Visto que continuavam a interrogá-lo, ele se levantou e lhes disse: "Se algum de vocês estiver sem pecado, seja o primeiro a atirar pedra nela".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26390, 'nvi', 'João', 'jo', 8, 8, 'Inclinou-se novamente e continuou escrevendo no chão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26391, 'nvi', 'João', 'jo', 8, 9, 'Os que o ouviram foram saindo, um de cada vez, começando com os mais velhos. Jesus ficou só, com a mulher em pé diante dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26392, 'nvi', 'João', 'jo', 8, 10, 'Então Jesus pôs-se de pé e perguntou-lhe: "Mulher, onde estão eles? Ninguém a condenou? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26393, 'nvi', 'João', 'jo', 8, 11, '"Ninguém, Senhor", disse ela. Declarou Jesus: "Eu também não a condeno. Agora vá e abandone sua vida de pecado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26394, 'nvi', 'João', 'jo', 8, 12, 'Falando novamente ao povo, Jesus disse: "Eu sou a luz do mundo. Quem me segue, nunca andará em trevas, mas terá a luz da vida".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26395, 'nvi', 'João', 'jo', 8, 13, 'Os fariseus lhe disseram: "Você está testemunhando a respeito de si próprio. O seu testemunho não é válido! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26396, 'nvi', 'João', 'jo', 8, 14, 'Respondeu Jesus: "Ainda que eu mesmo testemunhe em meu favor, o meu testemunho é válido, pois sei de onde vim e para onde vou. Mas vocês não sabem de onde vim nem para onde vou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26397, 'nvi', 'João', 'jo', 8, 15, 'Vocês julgam por padrões humanos; eu não julgo ninguém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26398, 'nvi', 'João', 'jo', 8, 16, 'Mesmo que eu julgue, as minhas decisões são verdadeiras, porque não estou sozinho. Eu estou com o Pai, que me enviou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26399, 'nvi', 'João', 'jo', 8, 17, 'Na Lei de vocês está escrito que o testemunho de dois homens é válido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26400, 'nvi', 'João', 'jo', 8, 18, 'Eu testemunho acerca de mim mesmo; a minha outra testemunha é o Pai, que me enviou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26401, 'nvi', 'João', 'jo', 8, 19, 'Então lhe perguntaram: "Onde está o seu pai? " Respondeu Jesus: "Vocês não conhecem nem a mim nem a meu Pai. Se me conhecessem, também conheceriam a meu Pai".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26402, 'nvi', 'João', 'jo', 8, 20, 'Ele proferiu essas palavras enquanto ensinava no templo, perto do lugar onde se colocavam as ofertas. No entanto, ninguém o prendeu, porque a sua hora ainda não havia chegado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26403, 'nvi', 'João', 'jo', 8, 21, 'Mais uma vez, Jesus lhes disse: "Eu vou embora, e vocês procurarão por mim, e morrerão em seus pecados. Para onde vou, vocês não podem ir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26404, 'nvi', 'João', 'jo', 8, 22, 'Isso levou os judeus a perguntarem: "Será que ele irá matar-se? Será por isso que ele diz: ‘Para onde vou, vocês não podem ir’? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26405, 'nvi', 'João', 'jo', 8, 23, 'Mas ele continuou: "Vocês são daqui de baixo; eu sou lá de cima. Vocês são deste mundo; eu não sou deste mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26406, 'nvi', 'João', 'jo', 8, 24, 'Eu lhes disse que vocês morrerão em seus pecados. Se vocês não crerem que Eu Sou, de fato morrerão em seus pecados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26407, 'nvi', 'João', 'jo', 8, 25, '"Quem é você? ", perguntaram eles. "Exatamente o que tenho dito o tempo todo", respondeu Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26408, 'nvi', 'João', 'jo', 8, 26, '"Tenho muitas coisas para dizer e julgar a respeito de vocês. Pois aquele que me enviou merece confiança, e digo ao mundo aquilo que dele ouvi".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26409, 'nvi', 'João', 'jo', 8, 27, 'Eles não entenderam que lhes estava falando a respeito do Pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26410, 'nvi', 'João', 'jo', 8, 28, 'Então Jesus disse: "Quando vocês levantarem o Filho do homem, saberão que Eu Sou, e que nada faço de mim mesmo, mas falo exatamente o que o Pai me ensinou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26411, 'nvi', 'João', 'jo', 8, 29, 'Aquele que me enviou está comigo; ele não me deixou sozinho, pois sempre faço o que lhe agrada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26412, 'nvi', 'João', 'jo', 8, 30, 'Tendo dito essas coisas, muitos creram nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26413, 'nvi', 'João', 'jo', 8, 31, 'Disse Jesus aos judeus que haviam crido nele: "Se vocês permanecerem firmes na minha palavra, verdadeiramente serão meus discípulos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26414, 'nvi', 'João', 'jo', 8, 32, 'E conhecerão a verdade, e a verdade os libertará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26415, 'nvi', 'João', 'jo', 8, 33, 'Eles lhe responderam: "Somos descendentes de Abraão e nunca fomos escravos de ninguém. Como você pode dizer que seremos livres? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26416, 'nvi', 'João', 'jo', 8, 34, 'Jesus respondeu: "Digo-lhes a verdade: Todo aquele que vive pecando é escravo do pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26417, 'nvi', 'João', 'jo', 8, 35, 'O escravo não tem lugar permanente na família, mas o filho pertence a ela para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26418, 'nvi', 'João', 'jo', 8, 36, 'Portanto, se o Filho os libertar, vocês de fato serão livres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26419, 'nvi', 'João', 'jo', 8, 37, 'Eu sei que vocês são descendentes de Abraão. Contudo, estão procurando matar-me, porque em vocês não há lugar para a minha palavra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26420, 'nvi', 'João', 'jo', 8, 38, 'Eu lhes estou dizendo o que vi na presença do Pai, e vocês fazem o que ouviram do pai de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26421, 'nvi', 'João', 'jo', 8, 39, '"Abraão é o nosso pai", responderam eles. Disse Jesus: "Se vocês fossem filhos de Abraão, fariam as obras que Abraão fez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26422, 'nvi', 'João', 'jo', 8, 40, 'Mas vocês estão procurando matar-me, sendo que eu lhes falei a verdade que ouvi de Deus; Abraão não agiu assim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26423, 'nvi', 'João', 'jo', 8, 41, 'Vocês estão fazendo as obras do pai de vocês". Protestaram eles: "Nós não somos filhos ilegítimos. O único Pai que temos é Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26424, 'nvi', 'João', 'jo', 8, 42, 'Disse-lhes Jesus: "Se Deus fosse o Pai de vocês, vocês me amariam, pois eu vim de Deus e agora estou aqui. Eu não vim por mim mesmo, mas ele me enviou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26425, 'nvi', 'João', 'jo', 8, 43, 'Por que a minha linguagem não é clara para vocês? Porque são incapazes de ouvir o que eu digo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26426, 'nvi', 'João', 'jo', 8, 44, '"Vocês pertencem ao pai de vocês, o diabo, e querem realizar o desejo dele. Ele foi homicida desde o princípio e não se apegou à verdade, pois não há verdade nele. Quando mente, fala a sua própria língua, pois é mentiroso e pai da mentira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26427, 'nvi', 'João', 'jo', 8, 45, 'No entanto, vocês não crêem em mim, porque lhes digo a verdade!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26428, 'nvi', 'João', 'jo', 8, 46, 'Qual de vocês pode me acusar de algum pecado? Se estou falando a verdade, porque vocês não crêem em mim?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26429, 'nvi', 'João', 'jo', 8, 47, 'Aquele que pertence a Deus ouve o que Deus diz. Vocês não ouvem porque não pertencem a Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26430, 'nvi', 'João', 'jo', 8, 48, 'Os judeus lhe responderam: "Não estamos certos em dizer que você é samaritano e está endemoninhado? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26431, 'nvi', 'João', 'jo', 8, 49, 'Disse Jesus: "Não estou endemoninhado! Pelo contrário, honro o meu Pai, e vocês me desonram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26432, 'nvi', 'João', 'jo', 8, 50, 'Não estou buscando glória para mim mesmo; mas, há quem a busque e julgue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26433, 'nvi', 'João', 'jo', 8, 51, 'Asseguro-lhes que, se alguém guardar a minha palavra, jamais verá a morte".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26434, 'nvi', 'João', 'jo', 8, 52, 'Diante disso, os judeus exclamaram: "Agora sabemos que você está endemoninhado! Abraão morreu, bem como os profetas, mas você diz que se alguém guardar a sua palavra, nunca experimentará a morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26435, 'nvi', 'João', 'jo', 8, 53, 'Você é maior do que o nosso pai Abraão? Ele morreu, bem como os profetas. Quem você pensa que é? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26436, 'nvi', 'João', 'jo', 8, 54, 'Respondeu Jesus: "Se glorifico a mim mesmo, a minha glória nada significa. Meu Pai, que vocês dizem ser o Deus de vocês, é quem me glorifica.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26437, 'nvi', 'João', 'jo', 8, 55, 'Vocês não o conhecem, mas eu o conheço. Se eu dissesse que não o conheço, seria mentiroso como vocês, mas eu de fato o conheço e guardo a sua palavra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26438, 'nvi', 'João', 'jo', 8, 56, 'Abraão, pai de vocês, regozijou-se porque veria o meu dia; ele o viu e alegrou-se".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26439, 'nvi', 'João', 'jo', 8, 57, 'Disseram-lhe os judeus: "Você ainda não tem cinqüenta anos, e viu Abraão? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26440, 'nvi', 'João', 'jo', 8, 58, 'Respondeu Jesus: "Eu lhes afirmo que antes de Abraão nascer, Eu Sou! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26441, 'nvi', 'João', 'jo', 8, 59, 'Então eles apanharam pedras para apedrejá-lo, mas Jesus escondeu-se e saiu do templo.');
