INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26442, 'nvi', 'João', 'jo', 9, 1, 'Ao passar, Jesus viu um cego de nascença.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26443, 'nvi', 'João', 'jo', 9, 2, 'Seus discípulos lhe perguntaram: "Mestre, quem pecou: este homem ou seus pais, para que ele nascesse cego? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26444, 'nvi', 'João', 'jo', 9, 3, 'Disse Jesus: "Nem ele nem seus pais pecaram, mas isto aconteceu para que a obra de Deus se manifestasse na vida dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26445, 'nvi', 'João', 'jo', 9, 4, 'Enquanto é dia, precisamos realizar a obra daquele que me enviou. A noite se aproxima, quando ninguém pode trabalhar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26446, 'nvi', 'João', 'jo', 9, 5, 'Enquanto estou no mundo, sou a luz do mundo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26447, 'nvi', 'João', 'jo', 9, 6, 'Tendo dito isso, ele cuspiu no chão, misturou terra com saliva e aplicou-a aos olhos do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26448, 'nvi', 'João', 'jo', 9, 7, 'Então lhe disse: "Vá lavar-se no tanque de Siloé" ( que significa Enviado ). O homem foi, lavou-se e voltou vendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26449, 'nvi', 'João', 'jo', 9, 8, 'Seus vizinhos e os que anteriormente o tinham visto mendigando perguntaram: "Não é este o mesmo homem que costumava ficar sentado, mendigando? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26450, 'nvi', 'João', 'jo', 9, 9, 'Alguns afirmavam que era ele. Outros diziam: "Não, apenas se parece com ele". Mas ele próprio insistia: "Sou eu mesmo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26451, 'nvi', 'João', 'jo', 9, 10, '"Então, como foram abertos os seus olhos? ", interrogaram-no eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26452, 'nvi', 'João', 'jo', 9, 11, 'Ele respondeu: "O homem chamado Jesus misturou terra com saliva, colocou-a nos meus olhos e me disse que fosse lavar-me em Siloé. Fui, lavei-me, e agora vejo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26453, 'nvi', 'João', 'jo', 9, 12, 'Eles lhe perguntaram: "Onde está esse homem? " "Não sei", disse ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26454, 'nvi', 'João', 'jo', 9, 13, 'Levaram aos fariseus o homem que fora cego.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26455, 'nvi', 'João', 'jo', 9, 14, 'Era sábado o dia em que Jesus havia misturado terra com saliva e aberto os olhos daquele homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26456, 'nvi', 'João', 'jo', 9, 15, 'Então os fariseus também lhe perguntaram como ele recebera a visão. O homem respondeu: "Ele colocou uma mistura de terra e saliva em meus olhos, eu me lavei e agora vejo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26457, 'nvi', 'João', 'jo', 9, 16, 'Alguns dos fariseus disseram: "Esse homem não é de Deus, pois não guarda o sábado". Mas outros perguntavam: "Como pode um pecador fazer tais sinais miraculosos? " E houve divisão entre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26458, 'nvi', 'João', 'jo', 9, 17, 'Tornaram, pois, a perguntar ao cego: "Que diz você a respeito dele? Foram os seus olhos que ele abriu". O homem respondeu: "Ele é um profeta".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26459, 'nvi', 'João', 'jo', 9, 18, 'Os judeus não acreditaram que ele fora cego e havia sido curado enquanto não mandaram buscar os seus pais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26460, 'nvi', 'João', 'jo', 9, 19, 'Então perguntaram: "É este o seu filho, o qual vocês dizem que nasceu cego? Como ele pode ver agora? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26461, 'nvi', 'João', 'jo', 9, 20, 'Responderam os pais: "Sabemos que ele é nosso filho e que nasceu cego.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26462, 'nvi', 'João', 'jo', 9, 21, 'Mas não sabemos como ele pode ver agora ou quem lhe abriu os olhos. Perguntem a ele. Idade ele tem; falará por si mesmo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26463, 'nvi', 'João', 'jo', 9, 22, 'Seus pais disseram isso porque tinham medo dos judeus, pois estes já haviam decidido que, se alguém confessasse que Jesus era o Cristo, seria expulso da sinagoga.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26464, 'nvi', 'João', 'jo', 9, 23, 'Foi por isso que seus pais disseram: "Idade ele tem; perguntem a ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26465, 'nvi', 'João', 'jo', 9, 24, 'Pela segunda vez, chamaram o homem que fora cego e lhe disseram: "Para a glória de Deus, diga a verdade. Sabemos que esse homem é pecador".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26466, 'nvi', 'João', 'jo', 9, 25, 'Ele respondeu: "Não sei se ele é pecador ou não. Uma coisa sei: eu era cego e agora vejo! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26467, 'nvi', 'João', 'jo', 9, 26, 'Então lhe perguntaram: "O que ele lhe fez? Como lhe abriu os olhos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26468, 'nvi', 'João', 'jo', 9, 27, 'Ele respondeu: "Eu já lhes disse, e vocês não me deram ouvidos. Por que querem ouvir outra vez? Acaso vocês também querem ser discípulos dele? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26469, 'nvi', 'João', 'jo', 9, 28, 'Então o insultaram e disseram: "Discípulo dele é você! Nós somos discípulos de Moisés!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26470, 'nvi', 'João', 'jo', 9, 29, 'Sabemos que Deus falou a Moisés, mas, quanto a esse, nem sabemos de onde ele vem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26471, 'nvi', 'João', 'jo', 9, 30, 'O homem respondeu: "Ora, isso é extraordinário! Vocês não sabem de onde ele vem, contudo ele me abriu os olhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26472, 'nvi', 'João', 'jo', 9, 31, 'Sabemos que Deus não ouve a pecadores, mas ouve ao homem que o teme e pratica a sua vontade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26473, 'nvi', 'João', 'jo', 9, 32, '"Ninguém jamais ouviu que os olhos de um cego de nascença tivessem sido abertos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26474, 'nvi', 'João', 'jo', 9, 33, 'Se esse homem não fosse de Deus, não poderia fazer coisa alguma".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26475, 'nvi', 'João', 'jo', 9, 34, 'Diante disso, eles responderam: "Você nasceu cheio de pecado; como tem a ousadia de nos ensinar? " E o expulsaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26476, 'nvi', 'João', 'jo', 9, 35, 'Jesus ouviu que o haviam expulsado, e, ao encontrá-lo, disse: "Você crê no Filho do homem? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26477, 'nvi', 'João', 'jo', 9, 36, 'Perguntou o homem: "Quem é ele, Senhor, para que eu nele creia? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26478, 'nvi', 'João', 'jo', 9, 37, 'Disse Jesus: "Você já o tem visto. É aquele que está falando com você".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26479, 'nvi', 'João', 'jo', 9, 38, 'Então o homem disse: "Senhor, eu creio". E o adorou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26480, 'nvi', 'João', 'jo', 9, 39, 'Disse Jesus: "Eu vim a este mundo para julgamento, a fim de que os cegos vejam e os que vêem se tornem cegos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26481, 'nvi', 'João', 'jo', 9, 40, 'Alguns fariseus que estavam com ele ouviram-no dizer isso e perguntaram: "Acaso nós também somos cegos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26482, 'nvi', 'João', 'jo', 9, 41, 'Disse Jesus: "Se vocês fossem cegos, não seriam culpados de pecado; mas agora que dizem que podem ver, a culpa de vocês permanece".');
