INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18948, 'nvi', 'Jeremias', 'jr', 1, 1, 'As palavras de Jeremias, filho de Hilquias, um dos sacerdotes de Anatote, no território de Benjamim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18949, 'nvi', 'Jeremias', 'jr', 1, 2, 'A palavra do Senhor veio a ele no décimo terceiro ano do reinado de Josias, filho de Amom, rei de Judá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18950, 'nvi', 'Jeremias', 'jr', 1, 3, 'e durante o reinado de Jeoaquim, filho de Josias, rei de Judá, até o quinto mês do décimo primeiro ano de Zedequias, filho de Josias, rei de Judá, quando os habitantes de Jerusalém foram levados para o exílio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18951, 'nvi', 'Jeremias', 'jr', 1, 4, 'A palavra do Senhor veio a mim, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18952, 'nvi', 'Jeremias', 'jr', 1, 5, '"Antes de formá-lo no ventre eu o escolhi; antes de você nascer, eu o separei e o designei profeta às nações".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18953, 'nvi', 'Jeremias', 'jr', 1, 6, 'Mas eu disse: "Ah, Soberano Senhor! Eu não sei falar, pois ainda sou muito jovem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18954, 'nvi', 'Jeremias', 'jr', 1, 7, 'O Senhor, porém, me disse: "Não diga que é muito jovem. A todos a quem eu o enviar você irá e dirá tudo o que eu lhe ordenar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18955, 'nvi', 'Jeremias', 'jr', 1, 8, 'Não tenha medo deles, pois eu estou com você para protegê-lo", diz o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18956, 'nvi', 'Jeremias', 'jr', 1, 9, 'O Senhor estendeu a mão, tocou a minha boca e disse-me: "Agora ponho em sua boca as minhas palavras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18957, 'nvi', 'Jeremias', 'jr', 1, 10, 'Veja! Eu hoje dou a você autoridade sobre nações e reinos, para arrancar, despedaçar, arruinar e destruir; para edificar e para plantar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18958, 'nvi', 'Jeremias', 'jr', 1, 11, 'E a palavra do Senhor veio a mim: "O que você vê, Jeremias? " "Vejo o ramo de uma amendoeira", respondi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18959, 'nvi', 'Jeremias', 'jr', 1, 12, 'O Senhor me disse: "Você viu bem, pois estou vigiando para que a minha palavra se cumpra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18960, 'nvi', 'Jeremias', 'jr', 1, 13, 'A palavra do Senhor veio a mim pela segunda vez, dizendo: "O que você vê? " E eu respondi: "Vejo uma panela fervendo; ela está inclinada do norte para cá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18961, 'nvi', 'Jeremias', 'jr', 1, 14, 'O Senhor me disse: "Do norte se derramará a desgraça sobre todos os habitantes desta terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18962, 'nvi', 'Jeremias', 'jr', 1, 15, 'Estou convocando todos os povos dos reinos do norte", diz o Senhor. "Cada um virá e colocará o seu trono diante das portas de Jerusalém, virão contra todas as muralhas que a cercam e contra todas as cidades de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18963, 'nvi', 'Jeremias', 'jr', 1, 16, 'Pronunciarei a minha sentença contra o meu povo por todas as suas maldades; porque me abandonaram, queimaram incenso a outros deuses, e adoraram deuses que as suas mãos fizeram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18964, 'nvi', 'Jeremias', 'jr', 1, 17, '"E você, prepare-se! Vá dizer-lhes tudo o que eu ordenar. Não fique aterrorizado por causa deles, senão eu o aterrorizarei diante deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18965, 'nvi', 'Jeremias', 'jr', 1, 18, 'E hoje eu faço de você uma cidade fortificada, uma coluna de ferro e um muro de bronze, contra toda a terra: contra os reis de Judá, seus oficiais, seus sacerdotes e o povo da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18966, 'nvi', 'Jeremias', 'jr', 1, 19, 'Eles lutarão contra você, mas não o vencerão, pois eu estou com você e o protegerei", diz o Senhor.');
