INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19228, 'nvi', 'Jeremias', 'jr', 11, 1, 'Esta é a palavra que veio a Jeremias da parte do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19229, 'nvi', 'Jeremias', 'jr', 11, 2, '"Ouçam os termos desta aliança; e, você, repita-os ao povo de Judá e aos habitantes de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19230, 'nvi', 'Jeremias', 'jr', 11, 3, 'Diga-lhes que assim diz o Senhor, o Deus de Israel: ‘Maldito é aquele que não obedecer aos termos desta aliança,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19231, 'nvi', 'Jeremias', 'jr', 11, 4, 'os quais ordenei aos antepassados de vocês, quando eu os tirei do Egito, da fornalha de fundir ferro’. Eu disse: Obedeçam-me e façam tudo o que lhes ordeno, e vocês serão o meu povo, e eu serei o seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19232, 'nvi', 'Jeremias', 'jr', 11, 5, 'Então cumprirei a promessa que fiz sob juramento aos antepassados de vocês, de dar-lhes uma terra onde manam leite e mel, a terra que vocês hoje possuem". Então respondi: "Amém, Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19233, 'nvi', 'Jeremias', 'jr', 11, 6, 'O Senhor me disse: "Proclame todas estas palavras nas cidades de Judá e nas ruas de Jerusalém: ‘Ouçam os termos desta aliança e cumpram-nos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19234, 'nvi', 'Jeremias', 'jr', 11, 7, 'Desde a época em que tirei os seus antepassados do Egito até hoje, repetidas vezes os adverti, dizendo: Obedeçam-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19235, 'nvi', 'Jeremias', 'jr', 11, 8, 'Mas eles não me ouviram nem me deram atenção; pelo contrário, seguiram os seus corações duros e maus. Por isso eu trouxe sobre eles todas as maldições desta aliança, que eu tinha ordenado que cumprissem, mas que eles não cumpriram’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19236, 'nvi', 'Jeremias', 'jr', 11, 9, 'Então o Senhor me disse: "Há uma conspiração entre o povo de Judá e os habitantes de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19237, 'nvi', 'Jeremias', 'jr', 11, 10, 'Eles retornaram aos pecados de seus antepassados, que recusaram das ouvidos às minhas palavras e seguiram outros deuses para prestar-lhes culto. Tanto a comunidade de Israel como a de Judá quebraram a aliança que eu fiz com os antepassados deles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19238, 'nvi', 'Jeremias', 'jr', 11, 11, 'Por isso, assim diz o Senhor: "Trarei sobre eles uma desgraça da qual não poderão escapar. Ainda que venham a clamar a mim, eu não os ouvirei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19239, 'nvi', 'Jeremias', 'jr', 11, 12, 'Então as cidades de Judá e os habitantes de Jerusalém clamarão aos deuses, aos quais queimam incenso, mas eles não poderão salvá-los quando a desgraça os atingir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19240, 'nvi', 'Jeremias', 'jr', 11, 13, 'Você tem tantos deuses quantas são as suas cidades, ó Judá; e os altares que você construiu para queimar incenso àquela coisa vergonhosa chamada Baal são tantos quantas são as ruas de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19241, 'nvi', 'Jeremias', 'jr', 11, 14, '"E você, Jeremias, não ore em favor desse povo nem ofereça súplica ou petição alguma por eles, porque eu não ouvirei quando clamarem a mim na hora da desgraça. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19242, 'nvi', 'Jeremias', 'jr', 11, 15, '"O que a minha amada faz no meu templo, com intenção enganosa? Será que os votos e a carne consagrada evitarão o castigo? Poderá você, então, exultar? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19243, 'nvi', 'Jeremias', 'jr', 11, 16, 'O Senhor a chamou de oliveira verdejante, ornada de belos e bons frutos. Mas com o estrondo de um grande tumulto, ele a incendiará, e os seus ramos serão quebrados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19244, 'nvi', 'Jeremias', 'jr', 11, 17, 'O Senhor dos Exércitos, que a plantou, anunciou-lhe desgraça, porque a comunidade de Israel e a comunidade de Judá fizeram o que é reprovável e provocaram a minha ira, queimando incenso a Baal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19245, 'nvi', 'Jeremias', 'jr', 11, 18, 'Fiquei sabendo porque o Senhor me revelou; tu me mostraste o que eles estavam fazendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19246, 'nvi', 'Jeremias', 'jr', 11, 19, 'Eu era como um cordeiro manso levado ao matadouro; não tinha percebido que tramavam contra mim, dizendo: "Destruamos a árvore e a sua seiva, vamos cortá-lo da terra dos viventes para que o seu nome não seja mais lembrado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19247, 'nvi', 'Jeremias', 'jr', 11, 20, 'Ó Senhor dos Exércitos, justo juiz que provas o coração e a mente, espero ver a tua vingança sobre eles, pois a ti expus a minha causa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19248, 'nvi', 'Jeremias', 'jr', 11, 21, 'Em vista disso, assim diz o Senhor a respeito dos homens de Anatote que querem tirar a sua vida, e que dizem: "Não profetize em nome do Senhor, se não nós o mataremos";');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19249, 'nvi', 'Jeremias', 'jr', 11, 22, 'assim diz o Senhor dos Exércitos: "Eu os castigarei. Seus jovens morrerão à espada; seus filhos e suas filhas, de fome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19250, 'nvi', 'Jeremias', 'jr', 11, 23, 'Nem mesmo um remanescente lhes restará, porque trarei a desgraça sobre os homens de Anatote no ano do seu castigo".');
