INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19268, 'nvi', 'Jeremias', 'jr', 13, 1, 'Assim me disse o Senhor: "Vá comprar um cinto de linho e ponha-o em volta da cintura, mas não o deixe encostar na água".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19269, 'nvi', 'Jeremias', 'jr', 13, 2, 'Comprei um cinto e o pus em volta da cintura, como o Senhor me havia instruído.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19270, 'nvi', 'Jeremias', 'jr', 13, 3, 'O Senhor me dirigiu a palavra pela segunda vez, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19271, 'nvi', 'Jeremias', 'jr', 13, 4, '"Pegue o cinto que você comprou e está usando, vá agora a Perate e esconda-o ali numa fenda da rocha".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19272, 'nvi', 'Jeremias', 'jr', 13, 5, 'Assim, fui e o escondi em Perate, conforme o Senhor me havia ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19273, 'nvi', 'Jeremias', 'jr', 13, 6, 'Depois de muitos dias, o Senhor me disse: "Vá agora a Perate e pegue o cinto que lhe ordenei que escondesse ali".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19274, 'nvi', 'Jeremias', 'jr', 13, 7, 'Então fui a Perate, desenterrei o cinto e o tirei do lugar em que o havia escondido. O cinto estava podre e se tornara completamente inútil.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19275, 'nvi', 'Jeremias', 'jr', 13, 8, 'E o Senhor dirigiu-me a palavra, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19276, 'nvi', 'Jeremias', 'jr', 13, 9, '"Assim diz o Senhor: Do mesmo modo também arruinarei o orgulho de Judá e o orgulho desmedido de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19277, 'nvi', 'Jeremias', 'jr', 13, 10, 'Este povo ímpio, que se recusa a ouvir as minhas palavras, que age segundo a dureza de seus corações, seguindo outros deuses para prestar-lhes culto e adorá-los, que este povo seja como aquele cinto: completamente inútil!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19278, 'nvi', 'Jeremias', 'jr', 13, 11, 'Assim como um cinto se apega à cintura de um homem, da mesma forma fiz com que toda a comunidade de Israel e toda a comunidade de Judá se apegasse a mim, para que fosse o meu povo para o meu renome, louvor e honra. Mas eles não me ouviram", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19279, 'nvi', 'Jeremias', 'jr', 13, 12, '"Diga-lhes também: ‘Assim diz o Senhor, o Deus de Israel: Deve-se encher de vinho toda vasilha de couro’. E, se eles lhe disserem: ‘Será que não sabemos que se deve encher de vinho toda vasilha de couro? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19280, 'nvi', 'Jeremias', 'jr', 13, 13, 'Então você lhes dirá: ‘Assim diz o Senhor: Farei com que fiquem totalmente embriagados todos os habitantes desta terra, bem como os reis que se assentam no trono de Davi, os sacerdotes, os profetas e todos os habitantes de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19281, 'nvi', 'Jeremias', 'jr', 13, 14, 'Eu os despedaçarei, colocando uns contra os outros, os pais e os filhos, diz o Senhor. Nem a piedade, nem a misericórdia nem a compaixão me impedirão de destruí-los’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19282, 'nvi', 'Jeremias', 'jr', 13, 15, 'Escutem e dêem atenção, não sejam arrogantes, pois o Senhor falou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19283, 'nvi', 'Jeremias', 'jr', 13, 16, 'Dêem glória ao Senhor, ao seu Deus, antes que ele traga trevas, antes que os pés de vocês tropecem nas colinas ao escurecer. Vocês esperam a luz, mas ele fará dela uma escuridão profunda sim, ele a transformará em densas trevas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19284, 'nvi', 'Jeremias', 'jr', 13, 17, 'Mas, se vocês não ouvirem, eu chorarei em segredo por causa do orgulho de vocês. Chorarei amargamente, e de lágrimas os meus olhos transbordarão, porque o rebanho do Senhor foi levado para o cativeiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19285, 'nvi', 'Jeremias', 'jr', 13, 18, 'Diga-se ao rei e à rainha-mãe: "Desçam do trono, pois as suas coroas gloriosas caíram de suas cabeças".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19286, 'nvi', 'Jeremias', 'jr', 13, 19, 'As cidades do Neguebe estão bloqueadas e não há quem nelas consiga entrar. Todo o Judá foi levado para o exílio, todos os exilados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19287, 'nvi', 'Jeremias', 'jr', 13, 20, 'Erga os olhos, Jerusalém, e veja aqueles que vêm do norte. Onde está o rebanho que lhe foi confiado, as ovelhas das quais você se orgulhava?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19288, 'nvi', 'Jeremias', 'jr', 13, 21, 'O que você dirá quando sobre você dominarem aqueles que você sempre teve como aliados? Você não irá sentir dores como as de uma mulher em trabalho de parto?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19289, 'nvi', 'Jeremias', 'jr', 13, 22, 'E se você se perguntar: ‘Por que aconteceu isso comigo? ’, saiba que foi por causa dos seus muitos pecados que as suas vestes foram levantadas e você foi violentada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19290, 'nvi', 'Jeremias', 'jr', 13, 23, 'Será que o etíope pode mudar a sua pele? Ou o leopardo as suas pintas? Assim também vocês são incapazes de fazer o bem, vocês que estão acostumados a praticar o mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19291, 'nvi', 'Jeremias', 'jr', 13, 24, '"Espalharei vocês como a palha levada pelo vento do deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19292, 'nvi', 'Jeremias', 'jr', 13, 25, 'Esta é a sua parte, a porção que lhe determinei", declara o Senhor, "porque você se esqueceu de mim e confiou em deuses falsos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19293, 'nvi', 'Jeremias', 'jr', 13, 26, 'Eu mesmo levantarei as suas vestes até o seu rosto para que as suas vergonhas sejam expostas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19294, 'nvi', 'Jeremias', 'jr', 13, 27, 'Tenho visto os seus atos repugnantes os seus adultérios, os seus relinchos, a sua prostituição desavergonhada sobre as colinas e nos campos. Ai de você, Jerusalém! Até quando você continuará impura? "');
