INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19295, 'nvi', 'Jeremias', 'jr', 14, 1, 'Esta é a palavra que o Senhor dirigiu a Jeremias acerca da seca:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19296, 'nvi', 'Jeremias', 'jr', 14, 2, '"Judá pranteia, as suas cidades estão definhando e os seus habitantes se lamentam, prostrados no chão! O grito de Jerusalém sobe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19297, 'nvi', 'Jeremias', 'jr', 14, 3, 'Os nobres mandam os seus servos à procura de água; eles vão às cisternas mas nada encontram. Voltam com os potes vazios, e, decepcionados e desesperados, cobrem a cabeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19298, 'nvi', 'Jeremias', 'jr', 14, 4, 'A terra nada produziu, porque não houve chuva; e os lavradores, decepcionados, cobrem a cabeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19299, 'nvi', 'Jeremias', 'jr', 14, 5, 'Até mesmo a corça no campo abandona a cria recém-nascida, porque não há capim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19300, 'nvi', 'Jeremias', 'jr', 14, 6, 'Os jumentos selvagens permanecem nos altos, farejando o vento como os chacais, mas a sua visão falha, por falta de pastagem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19301, 'nvi', 'Jeremias', 'jr', 14, 7, 'Embora os nossos pecados nos acusem, age por amor do teu nome, ó Senhor! Nossas infidelidades são muitas; temos pecado contra ti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19302, 'nvi', 'Jeremias', 'jr', 14, 8, 'Ó Esperança de Israel, tu que o salvas na hora da adversidade, por que te comportas como um estrangeiro na terra, ou como um viajante que fica somente uma noite?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19303, 'nvi', 'Jeremias', 'jr', 14, 9, 'Por que ages como um homem que foi pego de surpresa, como um guerreiro que não pode salvar? Tu estás em nosso meio, ó Senhor, e nós pertencemos a ti; não nos abandones!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19304, 'nvi', 'Jeremias', 'jr', 14, 10, 'Assim diz o Senhor acerca deste povo: "Eles gostam muito de vaguear; não controlam os pés. Por isso o Senhor não os aceita; agora ele se lembrará da iniqüidade deles e os castigará por causa dos seus pecados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19305, 'nvi', 'Jeremias', 'jr', 14, 11, 'Então o Senhor me disse: "Não ore pelo bem-estar deste povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19306, 'nvi', 'Jeremias', 'jr', 14, 12, 'Ainda que jejuem, não escutarei o clamor deles; ainda que ofereçam holocaustos e ofertas de cereal, não os aceitarei. Mas eu os destruirei pela guerra, pela fome e pela peste".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19307, 'nvi', 'Jeremias', 'jr', 14, 13, 'Mas eu disse: "Ah, Soberano Senhor, os profetas estão dizendo a eles: ‘Vocês não verão a guerra nem a fome; eu lhes darei prosperidade duradoura neste lugar’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19308, 'nvi', 'Jeremias', 'jr', 14, 14, 'Então o Senhor me disse: "É mentira o que os profetas estão profetizando em meu nome. Eu não os enviei nem lhes dei ordem nenhuma, nem falei com eles. Eles estão profetizando para vocês falsas visões, adivinhações inúteis e ilusões de suas próprias mentes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19309, 'nvi', 'Jeremias', 'jr', 14, 15, 'Por isso, assim diz o Senhor: "Quanto aos profetas que estão profetizando em meu nome, embora eu não os tenha enviado, e que dizem: ‘Nem guerra nem fome alcançarão esta terra’, aqueles mesmos profetas perecerão pela guerra e pela fome!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19310, 'nvi', 'Jeremias', 'jr', 14, 16, 'E aqueles a quem estão profetizando serão jogados nas ruas de Jerusalém, por causa da fome e da guerra. E não haverá ninguém para sepultá-los, nem para sepultar as suas mulheres, os seus filhos e as suas filhas. Despejarei sobre eles o castigo que merecem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19311, 'nvi', 'Jeremias', 'jr', 14, 17, '"Diga-lhes isto: " ‘Que os meus olhos derramem lágrimas, noite e dia sem cessar; pois a minha filha virgem, o meu povo, sofreu um ferimento terrível, um golpe fatal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19312, 'nvi', 'Jeremias', 'jr', 14, 18, 'Se vou para o campo, vejo os que morreram pela espada; se entro na cidade, vejo a devastação da fome. Tanto o profeta como o sacerdote percorrem a terra sem nada compreender’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19313, 'nvi', 'Jeremias', 'jr', 14, 19, 'Rejeitaste Judá completamente? Desprezaste Sião? Por que nos feriste a ponto de não podermos ser curados? Esperávamos a paz, mas não veio bem algum; esperávamos um tempo de cura, mas há somente terror.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19314, 'nvi', 'Jeremias', 'jr', 14, 20, 'Senhor, reconhecemos a nossa impiedade e a iniqüidade dos nossos pais; temos de fato pecado contra ti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19315, 'nvi', 'Jeremias', 'jr', 14, 21, 'Por amor do teu nome não nos desprezes; não desonres o teu trono glorioso. Lembra-te da tua aliança conosco e não a quebres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19316, 'nvi', 'Jeremias', 'jr', 14, 22, 'Entre os ídolos inúteis das nações, existe algum que possa trazer chuva? Podem os céus, por si mesmos, produzir chuvas copiosas? Somente tu o podes, Senhor, nosso Deus! Portanto, a nossa esperança está em ti, pois tu fazes todas essas coisas.');
