INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18967, 'nvi', 'Jeremias', 'jr', 2, 1, 'A palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18968, 'nvi', 'Jeremias', 'jr', 2, 2, '"Vá proclamar aos ouvidos de Jerusalém: "Eu me lembro de sua fidelidade quando você era jovem: como noiva, você me amava e me seguia pelo deserto, por uma terra não semeada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18969, 'nvi', 'Jeremias', 'jr', 2, 3, 'Israel era santo para o Senhor, os primeiros frutos de sua colheita; todos os que o devoravam eram considerados culpados, e a desgraça os alcançava", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18970, 'nvi', 'Jeremias', 'jr', 2, 4, 'Ouçam a palavra do Senhor, ó comunidade de Jacó, todos os clãs da comunidade de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18971, 'nvi', 'Jeremias', 'jr', 2, 5, 'Assim diz o Senhor: "Que falta os seus antepassados encontraram em mim, para que me deixassem e se afastassem de mim? Eles seguiram ídolos sem valor, tornando-se eles próprios sem valor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18972, 'nvi', 'Jeremias', 'jr', 2, 6, 'Eles não perguntaram: ‘Onde está o Senhor, que nos trouxe do Egito e nos conduziu pelo deserto, por uma terra árida e cheia de covas, terra de seca e de trevas, terra pela qual ninguém passa e onde ninguém vive? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18973, 'nvi', 'Jeremias', 'jr', 2, 7, 'Eu trouxe vocês a uma terra fértil, para que comessem dos seus frutos e dos seus bons produtos. Entretanto, vocês contaminaram a minha terra; tornaram a minha herança repugnante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18974, 'nvi', 'Jeremias', 'jr', 2, 8, 'Os sacerdotes não perguntaram pelo Senhor; os intérpretes da lei não me conheciam, e os líderes do povo se rebelaram contra mim. Os profetas profetizavam em nome de Baal, seguindo deuses inúteis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18975, 'nvi', 'Jeremias', 'jr', 2, 9, '"Por isso, eu ainda faço denúncias contra vocês", diz o Senhor, "e farei denúncias contra os seus descendentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18976, 'nvi', 'Jeremias', 'jr', 2, 10, 'Atravessem o mar até o litoral de Chipre e vejam; mandem observadores a Quedar e reparem de perto; e vejam se alguma vez aconteceu algo assim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18977, 'nvi', 'Jeremias', 'jr', 2, 11, 'Alguma nação já trocou os seus deuses? E eles nem sequer são deuses! Mas o meu povo trocou a sua Glória por deuses inúteis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18978, 'nvi', 'Jeremias', 'jr', 2, 12, 'Espantem-se diante disso, ó céus! Fiquem horrorizados e abismados", diz o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18979, 'nvi', 'Jeremias', 'jr', 2, 13, '"O meu povo cometeu dois crimes: eles me abandonaram, a mim, a fonte de água viva; e cavaram as suas próprias cisternas, cisternas rachadas que não retêm água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18980, 'nvi', 'Jeremias', 'jr', 2, 14, 'Acaso é Israel escravo, escravo de nascimento? Por que foi então que se tornou presa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18981, 'nvi', 'Jeremias', 'jr', 2, 15, 'de leões que rugem e urram contra ela? Arrasaram a sua terra, queimaram as suas cidades e as deixaram desabitadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18982, 'nvi', 'Jeremias', 'jr', 2, 16, 'Até mesmo os homens de Mênfis e de Tafnes raparam o seu crânio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18983, 'nvi', 'Jeremias', 'jr', 2, 17, 'Não foi você mesma a responsável pelo que lhe aconteceu, ao abandonar o Senhor, o seu Deus?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18984, 'nvi', 'Jeremias', 'jr', 2, 18, 'Agora, por que você vai ao Egito para beber água do Nilo? E por que vai à Assíria para beber água do Eufrates?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18985, 'nvi', 'Jeremias', 'jr', 2, 19, 'O seu crime a castigará e a sua rebelião a repreenderá. Compreenda e veja como é mau e amargo abandonar o Senhor, o seu Deus, e não ter temor de mim", diz o Soberano, o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18986, 'nvi', 'Jeremias', 'jr', 2, 20, '"Há muito tempo, eu quebrei o seu jugo e despedacei as correias que a prendiam. Mas você disse: "Eu não servirei! " Ao contrário, em todo monte elevado e debaixo de toda árvore verdejante, você se deitava como uma prostituta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18987, 'nvi', 'Jeremias', 'jr', 2, 21, 'Eu a plantei como uma videira seleta, de semente absolutamente pura. Como, então, contra mim você se tornou uma videira degenerada e selvagem?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18988, 'nvi', 'Jeremias', 'jr', 2, 22, 'Mesmo que você se lave com soda e com muito sabão, a mancha da sua iniqüidade permanecerá diante de mim", diz o Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18989, 'nvi', 'Jeremias', 'jr', 2, 23, '"Como você pode dizer que não está contaminada e que não correu atrás dos baalins? Reveja o seu procedimento no vale e considere o que você tem feito. Você é como uma camela jovem e arisca que corre para todos os lados;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18990, 'nvi', 'Jeremias', 'jr', 2, 24, 'como uma jumenta selvagem habituada ao deserto, cheirando o vento em seu desejo. Quem é capaz de controlá-la quando está no cio? Os machos que a procuram não precisam se cansar, porque logo encontrarão a que está no mês do cio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18991, 'nvi', 'Jeremias', 'jr', 2, 25, 'Não deixe que os seus pés se esfolem nem que a sua garganta fique seca. Mas você disse: ‘Não adianta! Eu amo os deuses estrangeiros, e continuarei a ir atrás deles’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18992, 'nvi', 'Jeremias', 'jr', 2, 26, '"Assim como o ladrão fica envergonhado quando é apanhado em flagrante, também a comunidade de Israel ficará envergonhada: seus reis e oficiais, seus sacerdotes e profetas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18993, 'nvi', 'Jeremias', 'jr', 2, 27, 'Pois dizem à madeira: ‘Você é meu pai’ e à pedra: ‘Você me deu à luz’. Voltaram para mim as costas e não o rosto, mas na hora da adversidade dizem: ‘Vem salvar-nos! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18994, 'nvi', 'Jeremias', 'jr', 2, 28, 'E onde estão os deuses que você fabricou para si? Que eles venham, se puderem salvá-la na hora da adversidade! Porque os seus deuses são tão numerosos como as suas cidades, ó Judá!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18995, 'nvi', 'Jeremias', 'jr', 2, 29, '"Por que vocês fazem denúncias contra mim? Todos vocês se rebelaram contra mim", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18996, 'nvi', 'Jeremias', 'jr', 2, 30, '"De nada adiantou castigar o seu povo, eles não aceitaram a correção. A sua espada tem destruído os seus profetas como um leão devorador.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18997, 'nvi', 'Jeremias', 'jr', 2, 31, '"Vocês, desta geração, considerem a palavra do Senhor: "Tenho sido um deserto para Israel? Uma terra de grandes trevas? Por que o meu povo diz: ‘Nós assumimos o controle! Não mais viremos a ti’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18998, 'nvi', 'Jeremias', 'jr', 2, 32, 'Será que uma jovem se esquece das suas jóias, ou uma noiva, de seus enfeites nupciais? Contudo, o meu povo esqueceu-se de mim, por dias sem fim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18999, 'nvi', 'Jeremias', 'jr', 2, 33, 'Com quanta habilidade você busca o amor! Mesmo as mulheres da pior espécie aprenderam com o seu procedimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19000, 'nvi', 'Jeremias', 'jr', 2, 34, 'Nas suas roupas encontrou-se o sangue de pobres inocentes, os quais não foram flagrados arrombando casas. Contudo, apesar de tudo isso,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19001, 'nvi', 'Jeremias', 'jr', 2, 35, 'você diz: ‘Sou inocente; ele não está irado comigo’. Mas eu passarei sentença contra você porque você disse que não pecou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19002, 'nvi', 'Jeremias', 'jr', 2, 36, 'Por que você não leva a sério a sua mudança de rumo? Você ficará decepcionada com o Egito, como ficou com a Assíria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19003, 'nvi', 'Jeremias', 'jr', 2, 37, 'Você também deixará aquele lugar, com as mãos na cabeça, pois o Senhor rejeitou aqueles em quem você confia; você não receberá a ajuda deles.');
