INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19456, 'nvi', 'Jeremias', 'jr', 22, 1, 'Assim diz o Senhor: "Desça ao palácio do rei de Judá e proclame ali esta mensagem:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19457, 'nvi', 'Jeremias', 'jr', 22, 2, '‘Ouça a palavra do Senhor, ó rei de Judá, tu que te assentas no trono de Davi; tu e teus conselheiros, e teu povo que passa por estas portas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19458, 'nvi', 'Jeremias', 'jr', 22, 3, 'Assim diz o Senhor: Administrem a justiça e o direito: livrem o explorado das mãos do opressor. Não oprimam nem maltratem o estrangeiro, o órfão ou a viúva; nem derramem sangue inocente neste lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19459, 'nvi', 'Jeremias', 'jr', 22, 4, 'Porque, se vocês tiverem o cuidado de cumprir essas ordens, então os reis que se assentarem no trono de Davi entrarão pelas portas deste palácio em carruagens e cavalos, em companhia de seus conselheiros e de seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19460, 'nvi', 'Jeremias', 'jr', 22, 5, 'Mas se vocês desobedecerem a essas ordens, declara o Senhor, juro por mim mesmo que este palácio ficará deserto’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19461, 'nvi', 'Jeremias', 'jr', 22, 6, 'Porque assim diz o Senhor a respeito do palácio real de Judá: "Apesar de você ser para mim como Gileade e como o topo do Líbano, certamente farei de você um deserto, uma cidade desabitada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19462, 'nvi', 'Jeremias', 'jr', 22, 7, 'Prepararei destruidores contra você, cada um com as suas armas; eles cortarão o melhor dos seus cedros e o lançarão ao fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19463, 'nvi', 'Jeremias', 'jr', 22, 8, '"De numerosas nações muitos passarão por esta cidade e perguntarão uns aos outros: ‘Por que o Senhor fez uma coisa dessas a esta grande cidade? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19464, 'nvi', 'Jeremias', 'jr', 22, 9, 'E lhes responderão: ‘Foi porque abandonaram a aliança do Senhor, do seu Deus, e adoraram outros deuses e prestaram-lhes culto’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19465, 'nvi', 'Jeremias', 'jr', 22, 10, 'Não chorem pelo rei morto nem lamentem sua perda. Chorem amargamente, porém, por aquele que está indo para o exílio, porque jamais voltará nem verá sua terra natal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19466, 'nvi', 'Jeremias', 'jr', 22, 11, 'Porque assim diz o Senhor acerca de Salum, rei de Judá, sucessor de seu pai Josias, que partiu deste lugar: "Ele jamais voltará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19467, 'nvi', 'Jeremias', 'jr', 22, 12, 'Morrerá no lugar para onde o levaram prisioneiro; não verá novamente esta terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19468, 'nvi', 'Jeremias', 'jr', 22, 13, '"Ai daquele que constrói o seu palácio por meios corruptos, seus aposentos, pela injustiça, fazendo os seus compatriotas trabalharem por nada, sem pagar-lhes o devido salário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19469, 'nvi', 'Jeremias', 'jr', 22, 14, 'Ele diz: ‘Construirei para mim um grande palácio, com aposentos espaçosos’. Faz amplas janelas, reveste o palácio de cedro e pinta-o de vermelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19470, 'nvi', 'Jeremias', 'jr', 22, 15, '"Você acha que acumular cedro faz de você um rei? O seu pai não teve comida e bebida? Ele fez o que era justo e certo, e tudo ia bem com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19471, 'nvi', 'Jeremias', 'jr', 22, 16, 'Ele defendeu a causa do pobre e do necessitado, e, assim, tudo corria bem. Não é isso que significa conhecer-me? ", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19472, 'nvi', 'Jeremias', 'jr', 22, 17, '"Mas você não vê nem pensa noutra coisa além de lucro desonesto, derramar sangue inocente, opressão e extorsão. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19473, 'nvi', 'Jeremias', 'jr', 22, 18, 'Portanto, assim diz o Senhor a respeito de Jeoaquim, filho de Josias, rei de Judá: "Não se lamentarão por ele, clamando: ‘Ah, meu irmão! ’ ou ‘Ah, minha irmã! ’ Nem se lamentarão, clamando: ‘Ah, meu senhor! ’ ou ‘Ah, sua majestade! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19474, 'nvi', 'Jeremias', 'jr', 22, 19, 'Ele terá o enterro de um jumento: arrastado e lançado fora das portas de Jerusalém!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19475, 'nvi', 'Jeremias', 'jr', 22, 20, '"Jerusalém, suba ao Líbano e clame, seja ouvida a sua voz em Basã, clame desde Abarim, pois todos os seus aliados foram esmagados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19476, 'nvi', 'Jeremias', 'jr', 22, 21, 'Eu a adverti quando você se sentiu segura, mas você não quis ouvir-me. Esse foi sempre o seu procedimento, pois desde a sua juventude você não me obedece.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19477, 'nvi', 'Jeremias', 'jr', 22, 22, 'O vento conduzirá para longe todos os governantes que conduzem você, e os seus aliados irão para o exílio. Então você será envergonhada e humilhada por causa de todas as suas maldades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19478, 'nvi', 'Jeremias', 'jr', 22, 23, 'Você que está entronizada no Líbano, que está aninhada em prédios de cedro, como você gemerá quando lhe vierem as dores de parto, dores como as de uma mulher que está para dar à luz!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19479, 'nvi', 'Jeremias', 'jr', 22, 24, '"Juro pelo meu nome", diz o Senhor, "que ainda que você, Joaquim, filho de Jeoaquim, rei de Judá, fosse um anel de selar em minha mão direita, eu o arrancaria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19480, 'nvi', 'Jeremias', 'jr', 22, 25, 'Eu o entregarei nas mãos daqueles que querem tirar-lhe a vida, daqueles que você teme, nas mãos de Nabucodonosor, rei da Babilônia, e dos babilônios');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19481, 'nvi', 'Jeremias', 'jr', 22, 26, 'Expulsarei você e sua mãe, a mulher que lhe deu à luz, para um outro país onde vocês não nascerão, e no qual ambos morrerão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19482, 'nvi', 'Jeremias', 'jr', 22, 27, 'Jamais retornarão à terra à qual anseiam voltar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19483, 'nvi', 'Jeremias', 'jr', 22, 28, 'É Joaquim um vaso desprezível e quebrado, um utensílio que ninguém quer? Por que ele e os seus descendentes serão expulsos e lançados num país que não conhecem?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19484, 'nvi', 'Jeremias', 'jr', 22, 29, 'Ó terra, terra, terra, ouça a palavra do Senhor!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19485, 'nvi', 'Jeremias', 'jr', 22, 30, 'Assim diz o Senhor: "Registrem esse homem como homem sem filhos. Ele não prosperará em toda a sua vida; nenhum dos seus descendentes prosperará nem se assentará no trono de Davi nem governará em Judá".');
