INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19486, 'nvi', 'Jeremias', 'jr', 23, 1, '"Ai dos pastores que destróem e dispersam as ovelhas do meu pasto! ", diz o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19487, 'nvi', 'Jeremias', 'jr', 23, 2, 'Portanto, assim diz o Senhor, Deus de Israel, aos pastores que tomam conta do meu povo: "Foram vocês que dispersaram e expulsaram o meu rebanho, e não cuidaram dele. Mas eu cuidarei de vocês pelos seus maus procedimentos", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19488, 'nvi', 'Jeremias', 'jr', 23, 3, '"Eu mesmo reunirei os remanescentes do meu rebanho de todas as terras para onde os expulsei e os trarei de volta à sua pastagem, a fim de que cresçam e se multipliquem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19489, 'nvi', 'Jeremias', 'jr', 23, 4, 'Estabelecerei sobre eles pastores que cuidarão deles. E eles não mais terão medo ou pavor, e nenhum deles faltará", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19490, 'nvi', 'Jeremias', 'jr', 23, 5, '"Dias virão", declara o Senhor, "em que levantarei para Davi um Renovo justo, um rei que reinará com sabedoria e fará o que é justo e certo na terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19491, 'nvi', 'Jeremias', 'jr', 23, 6, 'Em seus dias Judá será salva, Israel viverá em segurança, e este é o nome pelo qual será chamado: O Senhor é a Nossa Justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19492, 'nvi', 'Jeremias', 'jr', 23, 7, '"Portanto, vêm dias", diz o Senhor, "em que não mais se dirá: ‘Juro pelo nome do Senhor, que trouxe os israelitas do Egito’,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19493, 'nvi', 'Jeremias', 'jr', 23, 8, 'mas se dirá: ‘Juro pelo nome do Senhor, que trouxe os descendentes de Israel da terra do norte e de todas as nações para onde os expulsou’. E eles viverão na sua própria terra. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19494, 'nvi', 'Jeremias', 'jr', 23, 9, 'Acerca dos profetas: Meu coração está partido dentro de mim; todos os meus ossos tremem. Sou como um bêbado, como um homem dominado pelo vinho, por causa do Senhor e de suas santas palavras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19495, 'nvi', 'Jeremias', 'jr', 23, 10, 'A terra está cheia de adúlteros, e por causa disso a terra chora e as pastagens do deserto estão secas. Seu modo de vida é perveso e o seu poder é ilegítimo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19496, 'nvi', 'Jeremias', 'jr', 23, 11, '"Tanto o profeta como o sacerdote são profanos; até no meu templo encontro as iniqüidades deles", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19497, 'nvi', 'Jeremias', 'jr', 23, 12, '"Por isso, o caminho deles será como lugares escorregadios nas trevas, para as quais serão banidos, e nelas cairão. Trarei a desgraça sobre eles, no ano do seu castigo", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19498, 'nvi', 'Jeremias', 'jr', 23, 13, '"Entre os profetas de Samaria vi algo repugnante: Eles profetizaram por Baal e desviaram Israel, o meu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19499, 'nvi', 'Jeremias', 'jr', 23, 14, 'E entre os profetas de Jerusalém vi algo horrível: Eles cometem adultério e vivem uma mentira. Encorajam os que praticam o mal, para que nenhum deles se converta de sua impiedade. Para mim são todos como Sodoma; o povo de Jerusalém é como Gomorra. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19500, 'nvi', 'Jeremias', 'jr', 23, 15, 'Por isso assim diz o Senhor dos Exércitos acerca dos profetas: "Eu os farei comer comida amarga e beber água envenenada, porque dos profetas de Jerusalém a impiedade se espalhou por toda esta terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19501, 'nvi', 'Jeremias', 'jr', 23, 16, 'Assim diz o Senhor dos Exércitos: "Não ouçam o que os profetas estão profetizando para vocês; eles os enchem de falsas esperanças. Falam de visões inventadas por eles mesmos, e que não vêm da boca do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19502, 'nvi', 'Jeremias', 'jr', 23, 17, 'Vivem dizendo àqueles que desprezam a palavra do Senhor: ‘Vocês terão paz’. E a todos os que seguem a obstinação dos seus corações dizem: ‘Vocês não sofrerão desgraça alguma’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19503, 'nvi', 'Jeremias', 'jr', 23, 18, 'Mas qual deles esteve no conselho do Senhor para ver ou ouvir a sua palavra? Quem deu atenção e obedeceu à minha palavra?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19504, 'nvi', 'Jeremias', 'jr', 23, 19, 'Vejam, a tempestade do Senhor! A sua fúria está à solta! Um vendaval vem sobre a cabeça dos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19505, 'nvi', 'Jeremias', 'jr', 23, 20, 'A ira do Senhor não se afastará até que ele tenha completado os seus propósitos. Em dias vindouros vocês o compreenderão claramente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19506, 'nvi', 'Jeremias', 'jr', 23, 21, 'Não enviei esses profetas, mas eles foram correndo levar sua mensagem; não falei com eles, mas eles profetizaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19507, 'nvi', 'Jeremias', 'jr', 23, 22, 'Mas se eles tivessem comparecido ao meu conselho, anunciariam as minhas palavras ao meu povo e teriam feito com que se convertessem do seu mau procedimento e das suas obras más.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19508, 'nvi', 'Jeremias', 'jr', 23, 23, '"Sou eu apenas um Deus de perto", pergunta o Senhor, "e não também um Deus de longe?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19509, 'nvi', 'Jeremias', 'jr', 23, 24, 'Poderá alguém esconder-se sem que eu o veja? ", pergunta o Senhor. "Não sou eu aquele que enche os céus e a terra? ", pergunta o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19510, 'nvi', 'Jeremias', 'jr', 23, 25, '"Ouvi o que dizem os profetas, que profetizam mentiras em meu nome, dizendo: ‘Tive um sonho! Tive um sonho! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19511, 'nvi', 'Jeremias', 'jr', 23, 26, 'Até quando os profetas continuarão a profetizar mentiras e as ilusões de suas próprias mentes?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19512, 'nvi', 'Jeremias', 'jr', 23, 27, 'Eles imaginam que os sonhos que contam uns aos outros farão o povo esquecer o meu nome, assim como os seus antepassados esqueceram o meu nome por causa de Baal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19513, 'nvi', 'Jeremias', 'jr', 23, 28, 'O profeta que tem um sonho, conte o sonho, e o que tem a minha palavra, fale a minha palavra com fidelidade. Pois o que tem a palha com o trigo? ", pergunta o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19514, 'nvi', 'Jeremias', 'jr', 23, 29, '"Não é a minha palavra como o fogo", pergunta o Senhor, "e como um martelo que despedaça a rocha?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19515, 'nvi', 'Jeremias', 'jr', 23, 30, '"Portanto", declara o Senhor, "estou contra os profetas que roubam uns dos outros as minhas palavras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19516, 'nvi', 'Jeremias', 'jr', 23, 31, 'Sim", declara o Senhor, "estou contra os profetas que com as suas próprias línguas declaram oráculos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19517, 'nvi', 'Jeremias', 'jr', 23, 32, 'Sim, estou contra os que profetizam sonhos falsos", declara o Senhor. "Eles os relatam e com as suas mentiras irresponsáveis desviam o meu povo. Eu não os enviei nem lhes autorizei; e eles não trazem benefício algum a este povo", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19518, 'nvi', 'Jeremias', 'jr', 23, 33, '"Quando este povo ou um profeta ou um sacerdote lhe perguntar: ‘Qual é a mensagem pesada da qual o Senhor lhe encarregou? ’, diga-lhes: ‘Vocês são o peso! E eu os abandonarei’, declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19519, 'nvi', 'Jeremias', 'jr', 23, 34, 'Se um profeta ou um sacerdote ou alguém do povo afirmar ‘Esta é a mensagem da qual o Senhor me encarregou’, eu castigarei esse homem e a sua família.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19520, 'nvi', 'Jeremias', 'jr', 23, 35, 'Assim dirá cada um de vocês ao seu amigo ou parente: ‘O que o Senhor respondeu? O que o Senhor falou? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19521, 'nvi', 'Jeremias', 'jr', 23, 36, 'Nunca mais mencionem a expressão ‘Esta é a mensagem da qual o Senhor me encarregou’, senão essa palavra se tornará uma ‘carga’ para aquele que a proferir; porque vocês distorcem as palavras do Deus vivo, do Senhor dos Exércitos, do nosso Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19522, 'nvi', 'Jeremias', 'jr', 23, 37, 'É assim que vocês dirão ao profeta: ‘Qual é a resposta do Senhor para você? ’ ou ‘O que o Senhor falou? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19523, 'nvi', 'Jeremias', 'jr', 23, 38, 'Mas se vocês disserem ‘Esta é a mensagem da qual o Senhor me encarregou’, assim diz o Senhor: Vocês dizem: ‘Esta é a mensagem da qual o Senhor me encarregou’, quando eu lhes adverti que não dissessem isso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19524, 'nvi', 'Jeremias', 'jr', 23, 39, 'Por isso me esquecerei de vocês e os lançarei fora da minha presença, juntamente com a cidade que dei a vocês e aos seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19525, 'nvi', 'Jeremias', 'jr', 23, 40, 'Trarei sobre vocês humilhação perpétua, vergonha permanente, que jamais será esquecida. "');
