INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19536, 'nvi', 'Jeremias', 'jr', 25, 1, 'A palavra veio a Jeremias a respeito de todo o povo de Judá no quarto ano de Jeoaquim, filho de Josias, rei de Judá, que foi o primeiro ano de Nabucodonosor, rei da Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19537, 'nvi', 'Jeremias', 'jr', 25, 2, 'O que o profeta Jeremias anunciou a todo o povo de Judá e aos habitantes de Jerusalém foi isto:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19538, 'nvi', 'Jeremias', 'jr', 25, 3, 'Durante vinte e três anos a palavra do Senhor tem vindo a mim, desde o décimo terceiro ano de Josias, filho de Amom, rei de Judá, até o dia de hoje. E eu a tenho anunciado a vocês, dia após dia, mas vocês não me deram ouvidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19539, 'nvi', 'Jeremias', 'jr', 25, 4, 'Embora o Senhor tenha enviado a vocês os seus servos, os profetas, dia após dia, vocês não os ouviram nem lhes deram atenção,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19540, 'nvi', 'Jeremias', 'jr', 25, 5, 'quando disseram: "Converta-se cada um do seu caminho mau e de suas más obras, e vocês permanecerão na terra que o Senhor deu a vocês e aos seus antepassados para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19541, 'nvi', 'Jeremias', 'jr', 25, 6, 'Não sigam outros deuses para prestar-lhes culto e adorá-los; não provoquem a minha ira com ídolos feitos por vocês. E eu não trarei desgraça sobre vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19542, 'nvi', 'Jeremias', 'jr', 25, 7, '"Mas vocês não me deram ouvidos e me provocaram à ira com os ídolos que vocês fizeram, trazendo desgraça sobre si mesmos", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19543, 'nvi', 'Jeremias', 'jr', 25, 8, 'Portanto, assim diz o Senhor dos Exércitos: "Visto que vocês não ouviram as minhas palavras,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19544, 'nvi', 'Jeremias', 'jr', 25, 9, 'convocarei todos os povos do norte e o meu servo Nabucodonosor, rei da Babilônia", declara o Senhor, "e os trarei para atacar esta terra, os seus habitantes e todas as nações ao redor. Eu os destruirei completamente e os farei um objeto de pavor e de zombaria, e uma ruína permanente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19545, 'nvi', 'Jeremias', 'jr', 25, 10, 'Darei fim às vozes de júbilo e de alegria, às vozes do noivo e da noiva, ao som do moinho e à luz das candeias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19546, 'nvi', 'Jeremias', 'jr', 25, 11, 'Toda esta terra se tornará uma ruína desolada, e essas nações estarão sujeitas ao rei da Babilônia durante setenta anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19547, 'nvi', 'Jeremias', 'jr', 25, 12, '"Quando se completarem os setenta anos, castigarei o rei da Babilônia e a sua nação, a terra dos babilônios, por causa de suas iniqüidades", declara o Senhor, "e a deixarei arrasada para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19548, 'nvi', 'Jeremias', 'jr', 25, 13, 'Cumprirei naquela terra tudo o que falei contra ela, tudo o que está escrito neste livro e que Jeremias profetizou contra todas as nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19549, 'nvi', 'Jeremias', 'jr', 25, 14, 'Porque os próprios babilônios serão escravizados por muitas nações e grandes reis; eu lhes retribuirei conforme as suas ações e as suas obras".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19550, 'nvi', 'Jeremias', 'jr', 25, 15, 'Assim me disse o Senhor, o Deus de Israel: "Pegue de minha mão este cálice com o vinho da minha ira e faça com que bebam dele todas as nações a quem eu o envio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19551, 'nvi', 'Jeremias', 'jr', 25, 16, 'Quando o beberem, ficarão cambaleando, enlouquecidos por causa da espada que enviarei entre elas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19552, 'nvi', 'Jeremias', 'jr', 25, 17, 'Então peguei o cálice da mão do Senhor, e fiz com que dele bebessem todas as nações às quais o Senhor me enviou:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19553, 'nvi', 'Jeremias', 'jr', 25, 18, 'Jerusalém e as cidades de Judá, seus reis e seus líderes, para fazer deles uma desolação e um objeto de pavor, zombaria e maldição, como hoje acontece;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19554, 'nvi', 'Jeremias', 'jr', 25, 19, 'o faraó, o rei do Egito, seus conselheiros e seus líderes, todo o seu povo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19555, 'nvi', 'Jeremias', 'jr', 25, 20, 'e todos os estrangeiros que lá residem; todos os reis de Uz; todos os reis dos filisteus: de Ascalom, Gaza, Ecrom e o povo que restou em Asdode;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19556, 'nvi', 'Jeremias', 'jr', 25, 21, 'Edom, Moabe e os amonitas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19557, 'nvi', 'Jeremias', 'jr', 25, 22, 'os reis de Tiro e de Sidom; os reis das ilhas e das terras de além do mar;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19558, 'nvi', 'Jeremias', 'jr', 25, 23, 'Dedã, Temá, Buz e todos os que rapam a cabeça;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19559, 'nvi', 'Jeremias', 'jr', 25, 24, 'e os reis da Arábia e todos os reis dos estrangeiros que vivem no deserto;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19560, 'nvi', 'Jeremias', 'jr', 25, 25, 'todos os reis de Zinri, de Elão e da Média;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19561, 'nvi', 'Jeremias', 'jr', 25, 26, 'e todos os reis do norte, próximos ou distantes, um após outro; e todos os reinos da face da terra. Depois de todos eles, o rei de Sesaque também beberá do cálice.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19562, 'nvi', 'Jeremias', 'jr', 25, 27, '"A seguir diga-lhes: ‘Assim diz o Senhor dos Exércitos, o Deus de Israel: Bebam, embriaguem-se, vomitem, caiam e não mais se levantem, por causa da espada que envio no meio de vocês’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19563, 'nvi', 'Jeremias', 'jr', 25, 28, 'Mas se eles se recusarem a beber, diga-lhes: ‘Assim diz o Senhor dos Exércitos: Vocês vão bebê-lo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19564, 'nvi', 'Jeremias', 'jr', 25, 29, 'Começo a trazer desgraça sobre a cidade que leva o meu nome; e vocês sairiam impunes? Sem dúvida não ficarão sem castigo! Estou trazendo a espada contra todos os habitantes da terra’, declara o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19565, 'nvi', 'Jeremias', 'jr', 25, 30, '"E você, profetize todas estas palavras contra eles, dizendo: " ‘O Senhor ruge do alto; troveja de sua santa morada; ruge poderosamente contra a sua propriedade. Ele grita como os que pisam as uvas; grita contra todos os habitantes da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19566, 'nvi', 'Jeremias', 'jr', 25, 31, 'Um tumulto ressoa até os confins da terra, pois o Senhor faz acusações contra as nações, e julga toda a humanidade: ele entregará os ímpios à espada’, declara o Senhor. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19567, 'nvi', 'Jeremias', 'jr', 25, 32, 'Assim diz o Senhor: "Vejam! A desgraça está se espalhando de nação em nação; uma terrível tempestade se levanta desde os confins da terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19568, 'nvi', 'Jeremias', 'jr', 25, 33, 'Naquela dia, os mortos pelo Senhor estarão em todo lugar, de um lado ao outro da terra. Ninguém pranteará por eles, e não serão recolhidos e sepultados, mas servirão de esterco sobre o solo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19569, 'nvi', 'Jeremias', 'jr', 25, 34, 'Lamentem-se e gritem, pastores! Rolem no pó, chefes do rebanho! Porque chegou para vocês o dia da matança e da sua dispersão; vocês cairão e serão esmigalhados como vasos finos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19570, 'nvi', 'Jeremias', 'jr', 25, 35, 'Não haverá refúgio para os pastores nem escapatória para os chefes do rebanho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19571, 'nvi', 'Jeremias', 'jr', 25, 36, 'Ouvem-se os gritos dos pastores e o lamento dos chefes do rebanho, pois o Senhor está destruindo as pastagens deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19572, 'nvi', 'Jeremias', 'jr', 25, 37, 'Os pastos tranqüilos estão devastados por causa do fogo da ira do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19573, 'nvi', 'Jeremias', 'jr', 25, 38, 'Como um leão, ele saiu de sua toca; a terra deles ficou devastada, por causa da espada do opressor e do fogo de sua ira.');
