INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19598, 'nvi', 'Jeremias', 'jr', 27, 1, 'No início do reinado de Zedequias, filho de Josias, rei de Judá, veio esta palavra a Jeremias da parte do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19599, 'nvi', 'Jeremias', 'jr', 27, 2, 'Assim me ordenou o Senhor: "Faça para você um jugo com cordas e madeira e ponha-o sobre o pescoço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19600, 'nvi', 'Jeremias', 'jr', 27, 3, 'Depois mande uma mensagem aos reis de Edom, de Moabe, de Amom, de Tiro e de Sidom, por meio dos embaixadores que vieram a Jerusalém para ver Zedequias, rei de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19601, 'nvi', 'Jeremias', 'jr', 27, 4, 'Esta é a mensagem que deverão transmitir aos seus senhores: Assim diz o Senhor dos Exércitos, o Deus de Israel:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19602, 'nvi', 'Jeremias', 'jr', 27, 5, '‘Eu fiz a terra, os seres humanos e os animais que nela estão, com o meu grande poder e com meu braço estendido, e eu a dou a quem eu quiser.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19603, 'nvi', 'Jeremias', 'jr', 27, 6, 'Agora, sou eu mesmo que entrego todas essas nações nas mãos do meu servo Nabucodonosor, rei da Babilônia; sujeitei a ele até mesmo os animais selvagens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19604, 'nvi', 'Jeremias', 'jr', 27, 7, 'Todas as nações estarão sujeitas a ele, a seu filho e a seu neto; até que chegue a hora em que a terra dele seja subjugada por muitas nações e por reis poderosos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19605, 'nvi', 'Jeremias', 'jr', 27, 8, '" ‘Se, porém, alguma nação ou reino não se sujeitar a Nabucodonosor, rei da Babilônia, nem colocar o pescoço sob o seu jugo, eu castigarei aquela nação com a guerra, a fome e a peste’, declara o Senhor, ‘e por meio dele eu a destruirei completamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19606, 'nvi', 'Jeremias', 'jr', 27, 9, 'Não ouçam os seus profetas, os seus adivinhos, os seus intérpretes de sonhos, os seus médiuns e os seus feiticeiros, os quais lhes dizem para não se sujeitar ao rei da Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19607, 'nvi', 'Jeremias', 'jr', 27, 10, 'Porque são mentiras o que eles profetizam para vocês, o que os levará para longe de sua terra. Eu banirei vocês, e vocês perecerão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19608, 'nvi', 'Jeremias', 'jr', 27, 11, 'Mas, se alguma nação colocar o pescoço sob o jugo do rei da Babilônia e a ele se sujeitar, então deixarei aquela nação permanecer na sua própria terra para cultivá-la e nela viver’, declara o Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19609, 'nvi', 'Jeremias', 'jr', 27, 12, 'Entreguei a mesma mensagem a Zedequias, rei de Judá, dizendo-lhe: Coloquem o pescoço sob o jugo do rei da Babilônia, sujeitem-se a ele e ao seu povo, e vocês viverão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19610, 'nvi', 'Jeremias', 'jr', 27, 13, 'Por que razão você e o seu povo morreriam pela guerra, pela fome e pela peste, com as quais o Senhor ameaça a nação que não se sujeitar ao rei da Babilônia?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19611, 'nvi', 'Jeremias', 'jr', 27, 14, 'Não dêem atenção às palavras dos profetas que dizem que vocês não devem sujeitar-se ao rei da Babilônia; estão profetizando mentiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19612, 'nvi', 'Jeremias', 'jr', 27, 15, '"Eu não os enviei! ", declara o Senhor. "Eles profetizam mentiras em meu nome. Por isso, eu banirei vocês, e vocês perecerão juntamente com os profetas que lhes estão profetizando".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19613, 'nvi', 'Jeremias', 'jr', 27, 16, 'Então eu disse aos sacerdotes e a todo este povo: Assim diz o Senhor: "Não ouçam os seus profetas que dizem que em breve os utensílios do templo do Senhor serão trazidos de volta da Babilônia. Eles estão profetizando mentiras".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19614, 'nvi', 'Jeremias', 'jr', 27, 17, 'Não os ouçam. Sujeitem-se ao rei da Babilônia, e vocês viverão. Por que deveria esta cidade ficar em ruínas?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19615, 'nvi', 'Jeremias', 'jr', 27, 18, 'Se eles são profetas e têm a palavra do Senhor, que implorem ao Senhor dos Exércitos, pedindo que os utensílios que restam no templo do Senhor no palácio do rei de Judá e em Jerusalém não sejam levados para a Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19616, 'nvi', 'Jeremias', 'jr', 27, 19, 'Porque assim diz o Senhor dos Exércitos acerca das colunas, do tanque, dos suportes e dos outros utensílios que foram deixados nesta cidade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19617, 'nvi', 'Jeremias', 'jr', 27, 20, 'os quais Nabucodonosor, rei da Babilônia, não levou consigo de Jerusalém para a Babilônia, quando exilou Joaquim, filho de Jeoaquim, rei de Judá, com os nobres de Judá e de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19618, 'nvi', 'Jeremias', 'jr', 27, 21, 'Sim, assim diz o Senhor dos Exércitos, Deus de Israel, acerca dos utensílios que restaram no templo do Senhor e no palácio do rei de Judá e em Jerusalém:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19619, 'nvi', 'Jeremias', 'jr', 27, 22, '"Serão levados para a Babilônia e ali ficarão até o dia em que eu os quiser buscar", declara o Senhor. "Então os trarei de volta e os restabelecerei a este lugar".');
