INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19637, 'nvi', 'Jeremias', 'jr', 29, 1, 'Este é o conteúdo da carta que o profeta Jeremias enviou de Jerusalém aos líderes, que ainda restavam entre os exilados, aos sacerdotes, aos profetas e a todo o povo que Nabucodonosor deportara de Jerusalém para a Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19638, 'nvi', 'Jeremias', 'jr', 29, 2, 'Isso aconteceu depois que o rei Joaquim e a rainha-mãe, os oficiais do palácio real, os líderes de Judá e Jerusalém, os artesãos e os artífices foram sido deportados de Jerusalém para a Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19639, 'nvi', 'Jeremias', 'jr', 29, 3, 'Ele enviou a carta por intermédio de Eleasá, filho de Safã, e Gemarias, filho de Hilquias, os quais Zedequias, rei de Judá, mandou a Nabucodonosor, rei da Babilônia. A carta dizia o seguinte:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19640, 'nvi', 'Jeremias', 'jr', 29, 4, 'Assim diz o Senhor dos Exércitos, o Deus de Israel, a todos os exilados, que deportei de Jerusalém para a Babilônia:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19641, 'nvi', 'Jeremias', 'jr', 29, 5, '"Construam casas e habitem nelas; plantem jardins e comam de seus frutos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19642, 'nvi', 'Jeremias', 'jr', 29, 6, 'Casem-se e tenham filhos e filhas; escolham mulheres para casar-se com seus filhos e dêem as suas filhas em casamento, para que também tenham filhos e filhas. Multipliquem-se e não diminuam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19643, 'nvi', 'Jeremias', 'jr', 29, 7, 'Busquem a prosperidade da cidade para a qual eu os deportei e orem ao Senhor em favor dela, porque a prosperidade de vocês depende da prosperidade dela".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19644, 'nvi', 'Jeremias', 'jr', 29, 8, 'Porque assim diz o Senhor dos Exércitos, o Deus de Israel: "Não deixem que os profetas e adivinhos que há no meio de vocês os enganem. Não dêem atenção aos sonhos que vocês os encorajam a terem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19645, 'nvi', 'Jeremias', 'jr', 29, 9, 'Eles estão profetizando mentiras em meu nome. Eu não os enviei", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19646, 'nvi', 'Jeremias', 'jr', 29, 10, 'Assim diz o Senhor: "Quando se completarem os setenta anos da Babilônia, eu cumprirei a minha promessa em favor de vocês, de trazê-los de volta para este lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19647, 'nvi', 'Jeremias', 'jr', 29, 11, 'Porque sou eu que conheço os planos que tenho para vocês", diz o Senhor, "planos de fazê-los prosperar e não de lhes causar dano, planos de dar-lhes esperança e um futuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19648, 'nvi', 'Jeremias', 'jr', 29, 12, 'Então vocês clamarão a mim, virão orar a mim, e eu os ouvirei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19649, 'nvi', 'Jeremias', 'jr', 29, 13, 'Vocês me procurarão e me acharão quando me procurarem de todo o coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19650, 'nvi', 'Jeremias', 'jr', 29, 14, 'Eu me deixarei ser encontrado por vocês", declara o Senhor, "e os trarei de volta do cativeiro. Eu os reunirei de todas as nações e de todos os lugares para onde eu os dispersei, e os trarei de volta para o lugar de onde os deportei", diz o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19651, 'nvi', 'Jeremias', 'jr', 29, 15, 'Vocês podem dizer: "O Senhor levantou profetas para nós na Babilônia",');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19652, 'nvi', 'Jeremias', 'jr', 29, 16, 'mas assim diz o Senhor sobre o rei que se assenta no trono de Davi e sobre todo o povo que permanece nesta cidade, seus compatriotas que não foram com vocês para o exílio;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19653, 'nvi', 'Jeremias', 'jr', 29, 17, 'assim diz o Senhor dos Exércitos: "Enviarei a guerra, a fome e a peste contra eles; lidarei com eles como se lida com figos ruins, que são intragáveis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19654, 'nvi', 'Jeremias', 'jr', 29, 18, 'Eu os perseguirei com a guerra, a fome e a peste; farei deles objeto de terror para todos os reinos da terra, maldição e exemplo, zombaria e afronta entre todas as nações para onde eu os dispersei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19655, 'nvi', 'Jeremias', 'jr', 29, 19, 'Porque eles não deram atenção às minhas palavras", declara o Senhor, "palavras que lhes enviei pelos meus servos, os profetas. E vocês também não deram atenção! ", diz o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19656, 'nvi', 'Jeremias', 'jr', 29, 20, 'Ouçam, agora, a palavra do Senhor, todos vocês exilados, que deportei de Jerusalém para a Babilônia!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19657, 'nvi', 'Jeremias', 'jr', 29, 21, 'Assim diz o Senhor dos Exércitos, o Deus de Israel, a respeito de Acabe, filho de Colaías, e a respeito de Zedequias, filho de Maaséias, os quais estão profetizando mentiras a vocês em meu nome: "Eu os entregarei nas mãos de Nabucodonosor, rei da Babilônia, e ele os matará diante de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19658, 'nvi', 'Jeremias', 'jr', 29, 22, 'Em razão disso, os exilados de Judá que estão na Babilônia usarão esta maldição: ‘Que o Senhor o trate como tratou Zedequias e Acabe, os quais o rei da Babilônia queimou vivos’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19659, 'nvi', 'Jeremias', 'jr', 29, 23, 'Porque cometeram loucura em Israel: adulteraram com as mulheres de seus amigos e em meu nome falaram mentiras, que eu não ordenei que falassem. Mas eu estou sabendo; sou testemunha disso", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19660, 'nvi', 'Jeremias', 'jr', 29, 24, 'Diga a Semaías, de Neelam:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19661, 'nvi', 'Jeremias', 'jr', 29, 25, '"Assim diz o Senhor dos Exércitos, o Deus de Israel: Você enviou cartas em seu próprio nome a todo o povo de Jerusalém, a Sofonias, filho do sacerdote Maaséias, e a todos os sacerdotes. Você disse a Sofonias:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19662, 'nvi', 'Jeremias', 'jr', 29, 26, '‘O Senhor o designou sacerdote em lugar de Joiada como encarregado do templo do Senhor; você deveria prender no tronco, com correntes de ferro, qualquer doido que agisse como profeta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19663, 'nvi', 'Jeremias', 'jr', 29, 27, 'E por que você não repreendeu Jeremias de Anatote, que se apresenta como profeta entre vocês?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19664, 'nvi', 'Jeremias', 'jr', 29, 28, 'Ele até mandou esta mensagem para nós que estamos na Babilônia: "O exílio será longo. Construam casas e habitem nelas; plantem jardins e comam de seus frutos" ’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19665, 'nvi', 'Jeremias', 'jr', 29, 29, 'O sacerdote Sofonias leu a carta ao profeta Jeremias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19666, 'nvi', 'Jeremias', 'jr', 29, 30, 'Então o Senhor dirigiu a palavra a Jeremias:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19667, 'nvi', 'Jeremias', 'jr', 29, 31, '"Envie esta mensagem a todos os exilados: ‘Assim diz o Senhor sobre Semaías, de Neelam: Embora eu não o tenha enviado, Semaías profetizou a vocês e fez com que vocês cressem numa mentira,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19668, 'nvi', 'Jeremias', 'jr', 29, 32, 'por isso, assim diz o Senhor: Castigarei Semaías, de Neelam, e os seus descendentes. Não lhe restará ninguém entre este povo, e ele não verá as coisas boas que vou fazer em favor de meu povo’, declara o Senhor, ‘porque ele pregou rebelião contra o Senhor’ ".');
