INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19669, 'nvi', 'Jeremias', 'jr', 30, 1, 'Esta é a palavra que veio a Jeremias da parte do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19670, 'nvi', 'Jeremias', 'jr', 30, 2, '"Assim diz o Senhor, o Deus de Israel: ‘Escreva num livro todas as palavras que eu lhe falei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19671, 'nvi', 'Jeremias', 'jr', 30, 3, 'Certamente vêm os dias’, diz o Senhor, ‘em que mudarei a sorte do meu povo, Israel e Judá, e os farei retornar à terra que dei aos seus antepassados, e eles a possuirão’, declara o Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19672, 'nvi', 'Jeremias', 'jr', 30, 4, 'Estas são as palavras que o Senhor falou acerca de Israel e de Judá:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19673, 'nvi', 'Jeremias', 'jr', 30, 5, '"Assim diz o Senhor: " ‘Ouvem-se gritos de pânico, de pavor e não de paz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19674, 'nvi', 'Jeremias', 'jr', 30, 6, 'Pergunte e veja: Pode um homem dar à luz? Por que vejo, então, todos os homens com as mãos no estômago, como uma mulher em trabalho de parto? Por que estão pálidos todos os rostos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19675, 'nvi', 'Jeremias', 'jr', 30, 7, 'Como será terrível aquele dia! Sem comparação! Será tempo de angústia para Jacó; mas ele será salvo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19676, 'nvi', 'Jeremias', 'jr', 30, 8, '" ‘Naquele dia’, declara o Senhor dos Exércitos, ‘quebrarei o jugo que está sobre o pescoço deles e arrebentarei as suas correntes; não mais serão escravizados pelos estrangeiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19677, 'nvi', 'Jeremias', 'jr', 30, 9, 'Servirão ao Senhor, ao seu Deus, e a Davi, seu rei, que darei a eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19678, 'nvi', 'Jeremias', 'jr', 30, 10, '" ‘Por isso, não tema Jacó, meu servo! Não fique assustado, ó Israel! ’, declara o Senhor. ‘Eu o salvarei de um lugar distante, e os seus descendentes, da terra do seu exílio. Jacó voltará e ficará em paz e em segurança; ninguém o inquietará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19679, 'nvi', 'Jeremias', 'jr', 30, 11, 'Porque eu estou com você e o salvarei’, diz o Senhor. ‘Destruirei completamente todas as nações entre as quais eu o dispersei; mas a você não destruirei completamente. Eu o disciplinarei, como você merece. Não o deixarei impune. ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19680, 'nvi', 'Jeremias', 'jr', 30, 12, '"Assim diz o Senhor: " ‘Seu ferimento é grave, sua ferida, incurável.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19681, 'nvi', 'Jeremias', 'jr', 30, 13, 'Não há quem defenda a sua causa; não há remédio para a sua ferida, que não cicatriza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19682, 'nvi', 'Jeremias', 'jr', 30, 14, 'Todos os seus amantes esqueceram-se de você; eles não se importam com você. Eu a golpeei como faz um inimigo; dei-lhe um castigo cruel, porque é grande a sua iniqüidade e numerosos são os seus pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19683, 'nvi', 'Jeremias', 'jr', 30, 15, 'Por que você grita por causa do seu ferimento, por sua ferida incurável? Fiz essas coisas a você porque é grande a sua iniqüidade e numerosos são os seus pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19684, 'nvi', 'Jeremias', 'jr', 30, 16, '" ‘Mas todos os que a devoram serão devorados; todos os seus adversários irão para o exílio. Aqueles que a saqueiam serão saqueados; eu despojarei todos os que a despojam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19685, 'nvi', 'Jeremias', 'jr', 30, 17, 'Farei cicatrizar o seu ferimento e curarei as suas feridas’, declara o Senhor, ‘porque a você, Sião, chamam de rejeitada, aquela por quem ninguém se importa’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19686, 'nvi', 'Jeremias', 'jr', 30, 18, '"Assim diz o Senhor: " ‘Mudarei a sorte das tendas de Jacó e terei compaixão das suas moradas. A cidade será reconstruída sobre as suas ruínas e o palácio no seu devido lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19687, 'nvi', 'Jeremias', 'jr', 30, 19, 'Deles virão ações de graça e o som de regozijo. Eu os farei aumentar e eles não diminuirão; eu os honrarei e eles não serão desprezados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19688, 'nvi', 'Jeremias', 'jr', 30, 20, 'Seus filhos serão como nos dias do passado, e a sua comunidade será firmada diante de mim; castigarei todos aqueles que os oprimem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19689, 'nvi', 'Jeremias', 'jr', 30, 21, 'Seu líder será um dentre eles; seu governante virá do meio deles. Eu o trarei para perto e ele se aproximará de mim; pois quem se arriscaria a aproximar-se de mim? ’, pergunta o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19690, 'nvi', 'Jeremias', 'jr', 30, 22, 'Por isso vocês serão o meu povo, e eu serei o seu Deus’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19691, 'nvi', 'Jeremias', 'jr', 30, 23, 'Vejam, a tempestade do Senhor! Sua fúria está a solta! Um vendaval vem sobre a cabeça dos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19692, 'nvi', 'Jeremias', 'jr', 30, 24, 'A ira do Senhor não se afastará até que ele tenha completado os seus propósitos. Em dias vindouros vocês compreenderão isso.');
