INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19733, 'nvi', 'Jeremias', 'jr', 32, 1, 'Esta é a palavra que o Senhor dirigiu a Jeremias no décimo ano do reinado de Zedequias, rei de Judá, que foi o ano décimo oitavo de Nabucodonosor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19734, 'nvi', 'Jeremias', 'jr', 32, 2, 'Naquela época, o exército do rei da Babilônia sitiava Jerusalém e o profeta Jeremias estava preso no pátio da guarda, no palácio real de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19735, 'nvi', 'Jeremias', 'jr', 32, 3, 'Zedequias, rei de Judá, havia aprisionado Jeremias acusando-o de profetizar que o Senhor iria entregar a cidade nas mãos do rei da Babilônia, e que este a conquistaria;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19736, 'nvi', 'Jeremias', 'jr', 32, 4, 'que Zedequias, rei de Judá, não escaparia das mãos dos babilônios, mas certamente seria entregue nas mãos do rei da Babilônia, falaria com ele face a face, e o veria com os próprios olhos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19737, 'nvi', 'Jeremias', 'jr', 32, 5, 'e que ele levaria Zedequias para a Babilônia, onde este ficaria até que o Senhor cuidasse da situação dele; e, ainda, que se eles lutassem contra os babilônios não seriam bem-sucedidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19738, 'nvi', 'Jeremias', 'jr', 32, 6, 'E Jeremias disse: "O Senhor dirigiu-me a palavra nos seguintes termos:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19739, 'nvi', 'Jeremias', 'jr', 32, 7, 'Hanameel, filho de seu tio Salum, virá ao seu encontro e dirá: ‘Compre a propriedade que tenho em Anatote, porque, sendo o parente mais próximo, você tem o direito e o dever de comprá-la’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19740, 'nvi', 'Jeremias', 'jr', 32, 8, '"Conforme o Senhor tinha dito, meu primo Hanameel veio ao meu encontro no pátio da guarda e disse: ‘Compre a propriedade que tenho em Anatote, no território de Benjamim, porque é seu o direito de posse e de resgate. Compre-a! ’ "Então, compreendi que essa era a palavra do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19741, 'nvi', 'Jeremias', 'jr', 32, 9, 'Assim, comprei do meu primo Hanameel a propriedade que ele possuía em Anatote. Pesei a prata e lhe paguei dezessete peças de prata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19742, 'nvi', 'Jeremias', 'jr', 32, 10, 'Assinei e selei a escritura, e pesei a prata na balança, diante de testemunhas por mim chamadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19743, 'nvi', 'Jeremias', 'jr', 32, 11, 'Peguei a escritura, a cópia selada com os termos e condições da compra, bem como a cópia não selada,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19744, 'nvi', 'Jeremias', 'jr', 32, 12, 'e entreguei essa escritura de compra a Baruque, filho de Nerias, filho de Maaséias, na presença de meu primo Hanameel, das testemunhas que tinham assinado a escritura e de todos os judeus que estavam sentados no pátio da guarda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19745, 'nvi', 'Jeremias', 'jr', 32, 13, '"Na presença deles dei as seguintes instruções a Baruque:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19746, 'nvi', 'Jeremias', 'jr', 32, 14, '‘Assim diz o Senhor dos Exércitos, Deus de Israel: Tome estes documentos, tanto a cópia selada como a não selada da escritura de compra, e coloque-os num jarro de barro para que se conservem por muitos anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19747, 'nvi', 'Jeremias', 'jr', 32, 15, 'Porque assim diz o Senhor dos Exércitos, Deus de Israel: Casas, campos e vinhas tornarão a ser comprados nesta terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19748, 'nvi', 'Jeremias', 'jr', 32, 16, '"Depois que entreguei a escritura de compra a Baruque, filho de Nerias, orei ao Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19749, 'nvi', 'Jeremias', 'jr', 32, 17, '"Ah! Soberano Senhor, tu fizeste os céus e a terra pelo teu grande poder e por teu braço estendido. Nada é difícil demais para ti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19750, 'nvi', 'Jeremias', 'jr', 32, 18, 'Mostras bondade até mil gerações, mas lanças os pecados dos pais sobre os seus filhos. Ó grande e poderoso Deus, cujo nome é o Senhor dos Exércitos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19751, 'nvi', 'Jeremias', 'jr', 32, 19, 'grandes são os teus propósitos e poderosos os teus feitos. Os teus olhos estão atentos aos atos dos homens; tu retribuis a cada um de acordo com a sua conduta, de acordo com os efeitos das suas obras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19752, 'nvi', 'Jeremias', 'jr', 32, 20, 'Realizaste sinais e maravilhas no Egito e continuas a fazê-los até hoje, tanto em Israel como entre toda a humanidade, e alcançaste o renome que hoje tens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19753, 'nvi', 'Jeremias', 'jr', 32, 21, 'Tiraste o teu povo do Egito com sinais e maravilhas, com mão poderosa e braço estendido, causando grande pavor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19754, 'nvi', 'Jeremias', 'jr', 32, 22, 'Deste a eles esta terra, que sob juramento prometeste aos seus antepassados; uma terra onde manam leite e mel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19755, 'nvi', 'Jeremias', 'jr', 32, 23, 'Eles vieram e tomaram posse dela, mas não te obedeceram nem seguiram a tua lei. Não fizeram nada daquilo que lhes ordenaste. Por isso trouxeste toda esta desgraça sobre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19756, 'nvi', 'Jeremias', 'jr', 32, 24, '"As rampas de cerco são erguidas pelos inimigos para tomarem a cidade, e pela guerra, pela fome e pela peste, ela será entregue nas mãos dos babilônios que a atacam. Cumpriu-se aquilo que disseste, como vês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19757, 'nvi', 'Jeremias', 'jr', 32, 25, 'Ainda assim, ó Soberano Senhor, tu me mandaste comprar a propriedade e convocar testemunhas do negócio, embora a cidade esteja entregue nas mãos dos babilônios! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19758, 'nvi', 'Jeremias', 'jr', 32, 26, 'A palavra do Senhor veio a mim, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19759, 'nvi', 'Jeremias', 'jr', 32, 27, '"Eu sou o Senhor, o Deus de toda a humanidade. Há alguma coisa difícil demais para mim?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19760, 'nvi', 'Jeremias', 'jr', 32, 28, 'Portanto, assim diz o Senhor: ‘Estou entregando esta cidade nas mãos dos babilônios e de Nabucodonosor, rei da Babilônia, que a conquistará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19761, 'nvi', 'Jeremias', 'jr', 32, 29, 'Os babilônios, que estão atacando esta cidade, entrarão e a incendiarão. Eles a queimarão juntamente com as casas nas quais o povo provocou a minha ira queimando incenso a Baal nos seus terraços, e derramando ofertas de bebida em honra de outros deuses.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19762, 'nvi', 'Jeremias', 'jr', 32, 30, '"Desde a sua juventude o povo de Israel e de Judá nada tem feito senão aquilo que eu considero mau; de fato, o povo de Israel nada tem feito além de provocar-me à ira", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19763, 'nvi', 'Jeremias', 'jr', 32, 31, 'Desde o dia em que foi construída até hoje, esta cidade tem despertado o meu furor de tal forma que tenho que tirá-la da minha frente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19764, 'nvi', 'Jeremias', 'jr', 32, 32, 'O povo de Israel e de Judá tem provocado a minha ira por causa de todo o mal que tem feito, tanto eles como os seus reis e os seus líderes, os seus sacerdotes e os seus profetas, os homens de Judá e os habitantes de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19765, 'nvi', 'Jeremias', 'jr', 32, 33, 'Voltaram as costas para mim e não o rosto; embora eu os tenha ensinado vez após vez, não quiseram ouvir-me nem aceitaram a correção.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19766, 'nvi', 'Jeremias', 'jr', 32, 34, 'Profanaram o templo que leva o meu nome, colocando nele as imagens de seus ídolos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19767, 'nvi', 'Jeremias', 'jr', 32, 35, 'Construíram o alto para Baal no vale de Ben-Hinom, para sacrificarem a Moloque os seus filhos e as suas filhas, coisa que nunca ordenei, prática repugnante que jamais imaginei; e, assim, levaram Judá a pecar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19768, 'nvi', 'Jeremias', 'jr', 32, 36, 'Portanto, assim diz o Senhor a esta cidade, sobre a qual vocês estão dizendo que será entregue nas mãos dos babilônios por meio da guerra, da fome e da peste:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19769, 'nvi', 'Jeremias', 'jr', 32, 37, '"Certamente eu os reunirei de todas as terras para onde os dispersei na minha ardente ira e no meu grande furor; eu os trarei de volta a este lugar e permitirei que vivam em segurança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19770, 'nvi', 'Jeremias', 'jr', 32, 38, 'Eles serão o meu povo, e eu serei o seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19771, 'nvi', 'Jeremias', 'jr', 32, 39, 'Darei a eles um só pensamento e uma só conduta, para que me temam durante toda a sua vida, para o seu próprio bem e o de seus filhos e descendentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19772, 'nvi', 'Jeremias', 'jr', 32, 40, 'Farei com eles uma aliança permanente: Jamais deixarei de fazer o bem a eles, e farei com que me temam de coração, para que jamais se desviem de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19773, 'nvi', 'Jeremias', 'jr', 32, 41, 'Terei alegria em fazer-lhes o bem, e os plantarei firmemente nesta terra de todo o meu coração e de toda a minha alma. Sim, é o que farei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19774, 'nvi', 'Jeremias', 'jr', 32, 42, '"Assim diz o Senhor: Assim como eu trouxe toda esta grande desgraça sobre este povo, também lhes darei a prosperidade que lhes prometo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19775, 'nvi', 'Jeremias', 'jr', 32, 43, 'De novo serão compradas propriedades nesta terra da qual vocês dizem: ‘É uma terra arrasada, sem homens nem animais, pois foi entregue nas mãos dos babilônios’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19776, 'nvi', 'Jeremias', 'jr', 32, 44, 'Propriedades serão compradas por prata e escrituras serão assinadas, seladas diante de testemunhas no território de Benjamim, nos povoados ao redor de Jerusalém, nas cidades de Judá, e nas cidades dos montes, da Sefelá e do Neguebe, porque eu restaurarei a sorte deles", declara o Senhor.');
