INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19777, 'nvi', 'Jeremias', 'jr', 33, 1, 'Jeremias ainda estava preso no pátio da guarda quando o Senhor lhe dirigiu a palavra pela segunda vez:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19778, 'nvi', 'Jeremias', 'jr', 33, 2, '"Assim diz o Senhor que fez a terra, o Senhor que a formou e a firmou; seu nome é Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19779, 'nvi', 'Jeremias', 'jr', 33, 3, '‘Clame a mim e eu responderei e lhe direi coisas grandiosas e insondáveis que você não conhece’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19780, 'nvi', 'Jeremias', 'jr', 33, 4, 'Porque assim diz o Senhor, o Deus de Israel, a respeito das casas desta cidade e dos palácios reais de Judá, que foram derrubados para servirem de defesa contra as rampas de cerco e a espada,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19781, 'nvi', 'Jeremias', 'jr', 33, 5, 'na luta contra os babilônios: ‘Elas ficarão cheias de cadáveres dos homens que matarei no meu furor. Ocultarei desta cidade o meu rosto por causa de toda a sua maldade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19782, 'nvi', 'Jeremias', 'jr', 33, 6, '" ‘Todavia, trarei restauração e cura para ela; curarei o meu povo e lhe darei muita prosperidade e segurança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19783, 'nvi', 'Jeremias', 'jr', 33, 7, 'Mudarei a sorte de Judá e de Israel e os reconstruirei como antigamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19784, 'nvi', 'Jeremias', 'jr', 33, 8, 'Eu os purificarei de todo o pecado que cometeram contra mim e perdoarei todos os seus pecados de rebelião contra mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19785, 'nvi', 'Jeremias', 'jr', 33, 9, 'Então Jerusalém será para mim uma fonte de alegria, de louvor e de glória, diante de todas as nações da terra que ouvirem acerca de todos os benefícios que faço por ela. Elas temerão e tremerão diante da paz e da prosperidade que eu lhe concedo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19786, 'nvi', 'Jeremias', 'jr', 33, 10, '"Assim diz o Senhor: ‘Vocês dizem que este lugar está devastado, e ficará sem homens nem animais. Contudo, nas cidades de Judá e nas ruas de Jerusalém, que estão devastadas, desabitadas, sem homens nem animais, mais uma vez se ouvirão');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19787, 'nvi', 'Jeremias', 'jr', 33, 11, 'as vozes de júbilo e de alegria, do noivo e da noiva, e as vozes daqueles que trazem ofertas de ação de graças para o templo do Senhor, dizendo: "Dêem graças ao Senhor dos Exércitos, pois ele é bom; o seu amor leal dura para sempre". Porque eu mudarei a sorte desta terra como antigamente’, declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19788, 'nvi', 'Jeremias', 'jr', 33, 12, '"Assim diz o Senhor dos Exércitos: ‘Neste lugar desolado, sem homens nem animais, haverá novamente pastagens onde os pastores farão descansar os seus rebanhos, em todas as suas cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19789, 'nvi', 'Jeremias', 'jr', 33, 13, 'Tanto nas cidades dos montes, da Sefelá, do Neguebe e do território de Benjamim, como nos povoados ao redor de Jerusalém e nas cidades de Judá, novamente passarão ovelhas sob as mãos daquele que as conta’, diz o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19790, 'nvi', 'Jeremias', 'jr', 33, 14, '" ‘Dias virão’, declara o Senhor, ‘em que cumprirei a promessa que fiz à comunidade de Israel e à comunidade de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19791, 'nvi', 'Jeremias', 'jr', 33, 15, '" ‘Naqueles dias e naquela época farei brotar um Renovo justo da linhagem de Davi; ele fará o que é justo e certo na terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19792, 'nvi', 'Jeremias', 'jr', 33, 16, 'Naqueles dias Judá será salva e Jerusalém viverá em segurança, e este é o nome pelo qual ela será chamada: O Senhor é a Nossa Justiça’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19793, 'nvi', 'Jeremias', 'jr', 33, 17, '"Porque assim diz o Senhor: ‘Davi jamais deixará de ter um descendente que se assente no trono de Israel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19794, 'nvi', 'Jeremias', 'jr', 33, 18, 'nem os sacerdotes, que são levitas, deixarão de ter descendente que esteja diante de mim para oferecer, continuamente, holocaustos, queimar ofertas de cereal e apresentar sacrifícios’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19795, 'nvi', 'Jeremias', 'jr', 33, 19, 'O Senhor dirigiu a palavra a Jeremias:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19796, 'nvi', 'Jeremias', 'jr', 33, 20, '"Assim diz o Senhor: ‘Se vocês puderem romper a minha aliança com o dia e a minha aliança com a noite, de modo que nem o dia nem a noite aconteçam no tempo que lhes está determinado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19797, 'nvi', 'Jeremias', 'jr', 33, 21, 'então poderá ser quebrada a minha aliança com o meu servo Davi, e neste caso ele não mais terá um descendente que reine no seu trono; e também será quebrada a minha aliança com os levitas que são sacerdotes e que me servem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19798, 'nvi', 'Jeremias', 'jr', 33, 22, 'Farei os descendentes do meu servo Davi e os levitas, que me servem, tão numerosos como as estrelas do céu e incontáveis como a areia das praias do mar’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19799, 'nvi', 'Jeremias', 'jr', 33, 23, 'O Senhor dirigiu a palavra a Jeremias:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19800, 'nvi', 'Jeremias', 'jr', 33, 24, '"Você reparou que essas pessoas estão dizendo que o Senhor rejeitou os dois reinos que tinha escolhido? Por isso desprezam o meu povo e não mais o considera como nação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19801, 'nvi', 'Jeremias', 'jr', 33, 25, 'Assim diz o Senhor: ‘Se a minha aliança com o dia e com a noite não mais vigorasse, se eu não tivesse estabelecido as leis fixas do céu e da terra,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19802, 'nvi', 'Jeremias', 'jr', 33, 26, 'então eu rejeitaria os descendentes de Jacó e do meu servo Davi, e não escolheria um dos seus descendentes para que governasse os descendentes de Abraão, de Isaque e de Jacó. Mas eu restaurarei a sorte deles e lhes manifestarei a minha compaixão’ ".');
