INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19803, 'nvi', 'Jeremias', 'jr', 34, 1, 'Quando Nabucodonosor, rei da Babilônia, todo o seu exército e todos os reinos e povos do império que ele governava lutavam contra Jerusalém, e contra todas as cidades ao redor, o Senhor dirigiu esta palavra a Jeremias:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19804, 'nvi', 'Jeremias', 'jr', 34, 2, '"Assim diz o Senhor, o Deus de Israel: Vá ao rei Zedequias de Judá e lhe diga: ‘Assim diz o Senhor: Estou entregando esta cidade nas mãos do rei da Babilônia, e ele a incendiará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19805, 'nvi', 'Jeremias', 'jr', 34, 3, 'Você não escapará, mas será capturado e entregue nas mãos dele. Com os seus próprios olhos você verá o rei da Babilônia, e ele falará com você face a face. E você irá para a Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19806, 'nvi', 'Jeremias', 'jr', 34, 4, '" ‘Ouça, porém, a promessa do Senhor, ó Zedequias, rei de Judá. Assim diz o Senhor a seu respeito: Você não morrerá pela espada,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19807, 'nvi', 'Jeremias', 'jr', 34, 5, 'mas morrerá em paz. E assim como o povo queimou incenso em honra dos seus antepassados, os reis que o precederam, também queimarão incenso em sua honra, e se lamentarão, clamando: "Ah, meu senhor! " Sim, eu mesmo faço essa promessa’, declara o Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19808, 'nvi', 'Jeremias', 'jr', 34, 6, 'O profeta Jeremias disse todas essas palavras ao rei Zedequias de Judá, em Jerusalém,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19809, 'nvi', 'Jeremias', 'jr', 34, 7, 'enquanto o exército do rei da Babilônia lutava contra Jerusalém e contra as outras cidades de Judá que ainda resistiam, Láquis e Azeca, pois só restaram essas cidades fortificadas em Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19810, 'nvi', 'Jeremias', 'jr', 34, 8, 'O Senhor dirigiu a palavra a Jeremias depois do acordo que o rei Zedequias fez com todo o povo de Jerusalém de proclamar a libertação dos escravos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19811, 'nvi', 'Jeremias', 'jr', 34, 9, 'Todos teriam que libertar seus escravos e escravas hebreus; ninguém poderia escravizar um compatriota judeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19812, 'nvi', 'Jeremias', 'jr', 34, 10, 'Assim, todos os líderes e o povo que firmaram esse acordo de libertação dos escravos, concordaram em deixá-los livres e não mais os escravizaram; o povo obedeceu e libertou os escravos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19813, 'nvi', 'Jeremias', 'jr', 34, 11, 'Mas, depois disso, mudou de idéia e tomou de volta os homens e as mulheres que havia libertado e tornou a escravizá-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19814, 'nvi', 'Jeremias', 'jr', 34, 12, 'Então o Senhor dirigiu a palavra a Jeremias, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19815, 'nvi', 'Jeremias', 'jr', 34, 13, '"Assim diz o Senhor, o Deus de Israel: Fiz uma aliança com os seus antepassados quando os tirei do Egito, da terra da escravidão. Eu disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19816, 'nvi', 'Jeremias', 'jr', 34, 14, '‘Ao fim de sete anos, cada um de vocês libertará todo compatriota hebreu que se vendeu a vocês. Depois de o servir por seis anos, você o libertará’. Mas os seus antepassados não me obedeceram nem me deram atenção.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19817, 'nvi', 'Jeremias', 'jr', 34, 15, 'Recentemente vocês se arrependeram e fizeram o que eu aprovo: Cada um de vocês proclamou liberdade para os seus compatriotas. Vocês até fizeram um acordo diante de mim no templo que leva o meu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19818, 'nvi', 'Jeremias', 'jr', 34, 16, 'Mas, agora, vocês voltaram atrás e profanaram o meu nome, pois cada um de vocês tomou de volta os homens e as mulheres que tinham libertado. Vocês voltaram a escravizá-los".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19819, 'nvi', 'Jeremias', 'jr', 34, 17, '"Portanto, assim diz o Senhor: Vocês não me obedeceram; não proclamaram libertação cada um para o seu compatriota e para o seu próximo. Por isso, eu agora proclamo libertação para vocês, diz o Senhor, pela espada, pela peste e pela fome. Farei com que vocês sejam um objeto de terror para todos os reinos da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19820, 'nvi', 'Jeremias', 'jr', 34, 18, 'Entregarei os homens que violaram a minha aliança e não cumpriram os termos da aliança que fizeram na minha presença, quando cortaram o bezerro em dois e andaram entre as partes do animal;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19821, 'nvi', 'Jeremias', 'jr', 34, 19, 'isto é, os líderes de Judá e de Jerusalém, os oficiais do palácio real, os sacerdotes e todo o povo da terra que andou entre as partes do bezerro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19822, 'nvi', 'Jeremias', 'jr', 34, 20, 'sim, eu os entregarei nas mãos dos inimigos que desejam tirar-lhes a vida. Seus cadáveres servirão de comida para as aves e para os animais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19823, 'nvi', 'Jeremias', 'jr', 34, 21, '"Eu entregarei Zedequias, rei de Judá, e os seus líderes, nas mãos dos inimigos que desejam tirar-lhes a vida, e do exército do rei da Babilônia, que retirou o cerco de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19824, 'nvi', 'Jeremias', 'jr', 34, 22, 'Darei a ordem", declara o Senhor, "e os trarei de volta a esta cidade. Eles lutarão contra ela, e vão conquistá-la e incendiá-la. Farei com que as cidades de Judá fiquem devastadas e desabitadas. "');
