INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19825, 'nvi', 'Jeremias', 'jr', 35, 1, 'Durante o reinado de Jeoaquim, filho de Josias, rei de Judá, o Senhor dirigiu esta palavra a Jeremias:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19826, 'nvi', 'Jeremias', 'jr', 35, 2, '"Vá à comunidade dos recabitas e convide-os a virem a uma das salas do templo do Senhor, e ofereça-lhes vinho para beber".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19827, 'nvi', 'Jeremias', 'jr', 35, 3, 'Então busquei Jazanias, filho de Jeremias, filho de Habazinias, seus irmãos e todos os seus filhos e toda a comunidade dos recabitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19828, 'nvi', 'Jeremias', 'jr', 35, 4, 'Eu os levei ao templo do Senhor, à sala dos filhos de Hanã, filho de Jigdalias, homem de Deus. A sala ficava ao lado da sala dos líderes e debaixo da sala de Maaséias, filho de Salum, o porteiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19829, 'nvi', 'Jeremias', 'jr', 35, 5, 'Então, coloquei vasilhas cheias de vinho e alguns copos diante dos membros da comunidade dos recabitas e lhes pedi que bebessem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19830, 'nvi', 'Jeremias', 'jr', 35, 6, 'Eles, porém, disseram: "Não bebemos vinho porque o nosso antepassado Jonadabe, filho de Recabe, nos deu esta ordem: ‘Nem vocês nem os seus descendentes beberão vinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19831, 'nvi', 'Jeremias', 'jr', 35, 7, 'Vocês não construirão casas nem semearão; não plantarão vinhas nem as possuirão; mas vocês sempre habitarão em tendas. Assim vocês viverão por muito tempo na terra na qual são nômades’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19832, 'nvi', 'Jeremias', 'jr', 35, 8, 'Temos obedecido a tudo o que nos ordenou nosso antepassado Jonadabe, filho de Recabe. Nós, nossas mulheres, nossos filhos e nossas filhas jamais bebemos vinho em toda a nossa vida,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19833, 'nvi', 'Jeremias', 'jr', 35, 9, 'não construímos casas para nossa moradia nem possuímos vinhas, campos ou plantações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19834, 'nvi', 'Jeremias', 'jr', 35, 10, 'Temos vivido em tendas e obedecido fielmente a tudo o que nosso antepassado Jonadabe nos ordenou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19835, 'nvi', 'Jeremias', 'jr', 35, 11, 'Mas, quando Nabucodonosor, rei da Babilônia, invadiu esta terra, dissemos: ‘Venham, vamos para Jerusalém para escapar dos exércitos dos babilônios e dos sírios’. Assim, permanecemos em Jerusalém".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19836, 'nvi', 'Jeremias', 'jr', 35, 12, 'O Senhor dirigiu a palavra a Jeremias, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19837, 'nvi', 'Jeremias', 'jr', 35, 13, '"Assim diz o Senhor dos Exércitos, Deus de Israel: Vá dizer aos homens de Judá e aos habitantes de Jerusalém: ‘Será que vocês não vão aprender a lição e obedecer às minhas palavras? ’, pergunta o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19838, 'nvi', 'Jeremias', 'jr', 35, 14, '‘Jonadabe, filho de Recabe, ordenou a seus filhos que não bebessem vinho, e essa ordem tem sido obedecida até hoje. Eles não bebem vinho porque obedecem à ordem do seu antepassado. Mas eu tenho falado a vocês repetidas vezes, e, contudo, vocês não me obedecem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19839, 'nvi', 'Jeremias', 'jr', 35, 15, 'Enviei a vocês, repetidas vezes, todos os meus servos, os profetas. Eles lhes diziam que cada um de vocês deveria converter-se da sua má conduta, corrigir as suas ações e deixar de seguir outros deuses para prestar-lhes culto. Assim, vocês habitariam na terra que dei a vocês e a seus antepassados. Mas vocês não me deram atenção nem me obedeceram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19840, 'nvi', 'Jeremias', 'jr', 35, 16, 'Os descendentes de Jonadabe, filho de Recabe, cumprem a ordem que o seu antepassado lhes deu, mas este povo não me obedece. ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19841, 'nvi', 'Jeremias', 'jr', 35, 17, '"Portanto, assim diz o Senhor dos Exércitos, Deus de Israel: ‘Trarei sobre Judá e sobre todos os habitantes de Jerusalém toda a desgraça da qual os adverti; porque falei a eles, mas não me ouviram, chamei-os, mas não me responderam’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19842, 'nvi', 'Jeremias', 'jr', 35, 18, 'Jeremias disse à comunidade dos recabitas: "Assim diz o Senhor dos Exércitos, Deus de Israel: ‘Vocês têm obedecido àquele que ordenou o seu antepassado Jonadabe; têm cumprido todas as suas instruções e têm feito tudo o que ele ordenou’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19843, 'nvi', 'Jeremias', 'jr', 35, 19, 'Por isso, assim diz o Senhor dos Exércitos, Deus de Israel: ‘Jamais faltará a Jonadabe, filho de Recabe, um descendente que me sirva’ ".');
