INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19844, 'nvi', 'Jeremias', 'jr', 36, 1, 'No quarto ano do reinado de Jeoaquim, filho de Josias, rei de Judá, o Senhor dirigiu esta palavra a Jeremias:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19845, 'nvi', 'Jeremias', 'jr', 36, 2, '"Pegue um rolo e escreva nele todas as palavras que lhe falei a respeito de Israel, de Judá e de todas as outras nações, desde que comecei a falar a você, durante o reinado de Josias, até hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19846, 'nvi', 'Jeremias', 'jr', 36, 3, 'Talvez, quando o povo de Judá souber de cada uma das desgraças que planejo trazer sobre eles, cada um se converta de sua má conduta e eu perdoe a iniqüidade e o pecado deles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19847, 'nvi', 'Jeremias', 'jr', 36, 4, 'Então Jeremias chamou Baruque, filho de Nerias, para que escrevesse no rolo, conforme Jeremias ditava, todas as palavras que o Senhor lhe havia falado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19848, 'nvi', 'Jeremias', 'jr', 36, 5, 'Depois Jeremias disse a Baruque: "Estou preso; não posso ir ao templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19849, 'nvi', 'Jeremias', 'jr', 36, 6, 'Por isso, vá ao templo do Senhor no dia do jejum e leia ao povo as palavras do Senhor que eu ditei, as quais você escreveu. Você também as lerá a todo o povo de Judá que vem de suas cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19850, 'nvi', 'Jeremias', 'jr', 36, 7, 'Talvez a súplica deles chegue diante do Senhor, e cada um se converta de sua má conduta, pois é grande o furor anunciado pelo Senhor contra este povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19851, 'nvi', 'Jeremias', 'jr', 36, 8, 'E Baruque, filho de Nerias, fez exatamente tudo aquilo que o profeta Jeremias lhe mandou fazer, e leu as palavras do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19852, 'nvi', 'Jeremias', 'jr', 36, 9, 'No nono mês do quinto ano do reinado de Jeoaquim, filho de Josias, rei de Judá, foi proclamado um jejum perante o Senhor para todo o povo de Jerusalém e para todo o povo que vinha das cidades de Judá para Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19853, 'nvi', 'Jeremias', 'jr', 36, 10, 'Baruque leu a todo o povo as palavras de Jeremias escritas no rolo. Ele as leu no templo do Senhor, da sala de Gemarias, filho do secretário Safã. A sala ficava no pátio superior, na porta Nova do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19854, 'nvi', 'Jeremias', 'jr', 36, 11, 'Quando Micaías, filho de Gemarias, filho de Safã, ouviu todas as palavras do Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19855, 'nvi', 'Jeremias', 'jr', 36, 12, 'desceu à sala do secretário, no palácio real, onde todos os líderes estavam sentados: o secretário Elisama, Delaías, filho de Semaías, Elnatã, filho de Acbor, Gemarias, filho de Safã, Zedequias, filho de Hananias, e todos os outros líderes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19856, 'nvi', 'Jeremias', 'jr', 36, 13, 'Micaías relatou-lhes tudo o que tinha ouvido quando Baruque leu ao povo o que estava escrito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19857, 'nvi', 'Jeremias', 'jr', 36, 14, 'Então todos os líderes mandaram por intermédio de Jeudi, filho de Netanias, neto de Selemias, bisneto de Cuchi, a seguinte mensagem a Baruque: "Pegue o rolo que você leu ao povo e venha aqui". Baruque, filho de Nerias, pegou o rolo e foi até eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19858, 'nvi', 'Jeremias', 'jr', 36, 15, 'Disseram-lhe: "Sente-se, e leia-o para nós". Então Baruque o leu para eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19859, 'nvi', 'Jeremias', 'jr', 36, 16, 'Quando ouviram todas aquelas palavras, entreolharam-se com medo e disseram a Baruque: "É absolutamente necessário que relatemos ao rei todas essas palavras".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19860, 'nvi', 'Jeremias', 'jr', 36, 17, 'Perguntaram a Baruque: "Diga-nos, como você escreveu tudo isso? Foi Jeremias quem o ditou a você? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19861, 'nvi', 'Jeremias', 'jr', 36, 18, '"Sim", Baruque respondeu, "ele ditou todas essas palavras, e eu as escrevi com tinta no rolo. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19862, 'nvi', 'Jeremias', 'jr', 36, 19, 'Os líderes disseram a Baruque: "Vá esconder-se com Jeremias; e que ninguém saiba onde vocês estão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19863, 'nvi', 'Jeremias', 'jr', 36, 20, 'Então deixaram o rolo na sala de Elisama, o secretário, e foram ao pátio do palácio real, e relataram tudo ao rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19864, 'nvi', 'Jeremias', 'jr', 36, 21, 'O rei mandou Jeudi pegar o rolo, e Jeudi o trouxe da sala de Elisama, o secretário, e o leu ao rei e a todos os líderes que estavam a seu serviço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19865, 'nvi', 'Jeremias', 'jr', 36, 22, 'Isso aconteceu no nono mês. O rei estava sentado em seu apartamento do inverno, perto de um braseiro aceso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19866, 'nvi', 'Jeremias', 'jr', 36, 23, 'Assim que Jeudi terminava de ler três ou quatro colunas, o rei as cortava com uma faca de escrivão e as atirava no braseiro, até que o rolo inteiro foi queimado no braseiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19867, 'nvi', 'Jeremias', 'jr', 36, 24, 'O rei e todos os seus conselheiros que ouviram todas aquelas palavras não ficaram alarmados nem rasgaram as suas roupas, lamentando-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19868, 'nvi', 'Jeremias', 'jr', 36, 25, 'Embora Elnatã, Delaías e Gemarias tivessem insistido com o rei que não queimasse o rolo, ele não quis ouvi-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19869, 'nvi', 'Jeremias', 'jr', 36, 26, 'Em vez disso, o rei ordenou a Jerameel, filho do rei, Seraías, filho de Azriel, e Selemias, filho de Abdeel, que prendessem o escriba Baruque e o profeta Jeremias. Mas o Senhor os tinha escondido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19870, 'nvi', 'Jeremias', 'jr', 36, 27, 'Depois que o rei queimou o rolo que continha as palavras que Jeremias ditou e Baruque escreveu, o Senhor dirigiu esta palavra a Jeremias:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19871, 'nvi', 'Jeremias', 'jr', 36, 28, '"Pegue outro rolo e escreva nele todas as palavras que estavam no primeiro, que Jeoaquim, rei de Judá, queimou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19872, 'nvi', 'Jeremias', 'jr', 36, 29, 'Também diga a Jeoaquim, rei de Judá: ‘Assim diz o Senhor: Você queimou aquele rolo e perguntou: "Por que você escreveu nele que o rei da Babilônia virá e destruirá esta terra e dela eliminará tanto homens como animais? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19873, 'nvi', 'Jeremias', 'jr', 36, 30, 'Pois assim diz o Senhor acerca de Jeoaquim, rei de Judá: Ele não terá nenhum descendente para sentar-se no trono de Davi; seu corpo será lançado fora e exposto ao calor de dia e à geada de noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19874, 'nvi', 'Jeremias', 'jr', 36, 31, 'Eu castigarei a ele, aos seus filhos e aos seus conselheiros por causa dos seus pecados. Trarei sobre eles e sobre os habitantes de Jerusalém e sobre os homens de Judá toda a desgraça que pronunciei contra eles, porquanto não me deram atenção’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19875, 'nvi', 'Jeremias', 'jr', 36, 32, 'Então Jeremias pegou outro rolo e o deu ao escriba Baruque, filho de Nerias, para que escrevesse nele, conforme Jeremias ditava, todas as palavras do livro que Jeoaquim, rei de Judá, tinha queimado, além de muitas outras palavras semelhantes que foram acrescentadas.');
