INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19029, 'nvi', 'Jeremias', 'jr', 4, 1, '"Se você voltar, ó Israel, volte para mim", diz o Senhor. "Se você afastar para longe de minha vista os seus ídolos detestáveis, e não se desviar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19030, 'nvi', 'Jeremias', 'jr', 4, 2, 'se você jurar pelo nome do Senhor, com fidelidade, justiça e retidão, então as nações serão por ele abençoadas e nele se gloriarão. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19031, 'nvi', 'Jeremias', 'jr', 4, 3, 'Assim diz o Senhor ao povo de Judá e de Jerusalém: "Lavrem seus campos não arados e não semeiem entre espinhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19032, 'nvi', 'Jeremias', 'jr', 4, 4, 'Purifiquem-se para o Senhor, sejam fiéis à aliança, homens de Judá e habitantes de Jerusalém! Se não fizerem isso, a minha ira se acenderá e queimará como fogo, por causa do mal que vocês fizeram; queimará e ninguém conseguirá apagá-la.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19033, 'nvi', 'Jeremias', 'jr', 4, 5, '"Anunciem em Judá! Proclamem em Jerusalém: ‘Toquem a trombeta por toda esta terra! ’ Gritem bem alto e digam: ‘Reúnam-se! Fujamos para as cidades fortificadas! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19034, 'nvi', 'Jeremias', 'jr', 4, 6, 'Ergam o sinal indicando Sião. Fujam sem demora em busca de abrigo! Porque do Norte eu estou trazendo desgraça, uma grande destruição".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19035, 'nvi', 'Jeremias', 'jr', 4, 7, 'Um leão saiu da sua toca, um destruidor de nações se pôs a caminho. Ele saiu de onde vive para arrasar a sua terra. Suas cidades ficarão em ruínas e sem habitantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19036, 'nvi', 'Jeremias', 'jr', 4, 8, 'Por isso, ponham vestes de lamento, chorem e gritem, pois o fogo da ira do Senhor não se desviou de nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19037, 'nvi', 'Jeremias', 'jr', 4, 9, '"Naquele dia", diz o Senhor, "o rei e os seus oficiais perderão a coragem, os sacerdotes ficarão horrorizados e os profetas, perplexos. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19038, 'nvi', 'Jeremias', 'jr', 4, 10, 'Então eu disse: "Ah, Soberano Senhor, como enganaste completamente este povo e a Jerusalém dizendo: ‘Vocês terão paz’, quando a espada está em nossa garganta".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19039, 'nvi', 'Jeremias', 'jr', 4, 11, 'Naquela época será dito a este povo e a Jerusalém: "Um vento escaldante, que vem das dunas do deserto, sopra na direção da minha filha, do meu povo, mas não para peneirar nem para limpar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19040, 'nvi', 'Jeremias', 'jr', 4, 12, 'É um vento forte demais, que vem da minha parte. Agora eu pronunciarei as minhas sentenças contra eles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19041, 'nvi', 'Jeremias', 'jr', 4, 13, 'Vejam! Ele avança como as nuvens; os seus carros de guerra são como um furacão e os seus cavalos são mais velozes do que as águias. Ai de nós! Estamos perdidos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19042, 'nvi', 'Jeremias', 'jr', 4, 14, 'Ó Jerusalém, lave o mal do seu coração para que você seja salva. Até quando você vai acolher projetos malignos no íntimo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19043, 'nvi', 'Jeremias', 'jr', 4, 15, 'Ouve-se uma voz proclamando desde Dã, desde os montes de Efraim se anuncia calamidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19044, 'nvi', 'Jeremias', 'jr', 4, 16, '"Relatem isso a esta nação e proclamem contra Jerusalém: ‘Um exército inimigo está vindo de uma terra distante, dando seu grito de guerra contra as cidades de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19045, 'nvi', 'Jeremias', 'jr', 4, 17, 'Eles a cercam como homens que guardam um campo, pois ela se rebelou contra mim’ ", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19046, 'nvi', 'Jeremias', 'jr', 4, 18, '"A sua própria conduta e as suas ações trouxeram isso sobre você. Como é amargo este seu castigo! Ele atinge até o seu coração! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19047, 'nvi', 'Jeremias', 'jr', 4, 19, 'Ah, minha angústia, minha angústia! Eu me contorço de dor. Ó paredes do meu coração! O meu coração dispara dentro de mim; não posso ficar calado. Ouvi o som da trombeta, ouvi o grito de guerra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19048, 'nvi', 'Jeremias', 'jr', 4, 20, 'Um desastre depois do outro; toda a minha terra foi devastada. Num instante as minhas tendas foram destruídas, e os meus abrigos num momento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19049, 'nvi', 'Jeremias', 'jr', 4, 21, 'Até quando verei o sinal levantado e ouvirei o som da trombeta?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19050, 'nvi', 'Jeremias', 'jr', 4, 22, '"O meu povo é tolo, eles não me conhecem. São crianças insensatas que nada compreendem. São hábeis para praticar o mal, mas não sabem fazer o bem. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19051, 'nvi', 'Jeremias', 'jr', 4, 23, 'Olhei para a terra e ela era sem forma e vazia; e para os céus, e a sua luz tinha desaparecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19052, 'nvi', 'Jeremias', 'jr', 4, 24, 'Olhei para os montes e eles estavam tremendo; todas as colinas estavam oscilando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19053, 'nvi', 'Jeremias', 'jr', 4, 25, 'Olhei, e não havia mais gente; todas as aves do céu tinham fugido em revoada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19054, 'nvi', 'Jeremias', 'jr', 4, 26, 'Olhei, e a terra fértil era deserto; todas as suas cidades estavam em ruínas por causa do Senhor, por causa do fogo da sua ira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19055, 'nvi', 'Jeremias', 'jr', 4, 27, 'Assim diz o Senhor: "Toda esta terra ficará devastada, embora eu não vá destruí-la completamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19056, 'nvi', 'Jeremias', 'jr', 4, 28, 'Por causa disso, a terra ficará de luto e o céu, em cima, se escurecerá; porque eu falei, e não me arrependi, decidi, e não voltei atrás".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19057, 'nvi', 'Jeremias', 'jr', 4, 29, 'Quando se ouvem os cavaleiros e os flecheiros, todos os habitantes da cidade fogem. Alguns vão para o meio dos arbustos; outros escalam as rochas. Todas as cidades são abandonadas, e ficam sem habitantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19058, 'nvi', 'Jeremias', 'jr', 4, 30, 'O que você está fazendo, ó cidade devastada? Por que se veste de vermelho e se enfeita com jóias de ouro? Por que você passa sombra nos olhos? Você se embeleza em vão, pois os seus amantes a desprezam e querem tirar-lhe a vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19059, 'nvi', 'Jeremias', 'jr', 4, 31, 'Ouvi um grito, como de mulher em trabalho de parto, como a agonia de uma mulher ao dar à luz o primeiro filho. É o grito da cidade de Sião, que está ofegante e estende as mãos, dizendo: "Ai de mim! Estou desfalecendo. Minha vida está nas mãos de assassinos! "');
