INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19959, 'nvi', 'Jeremias', 'jr', 41, 1, 'No sétimo mês, Ismael, filho de Netanias, filho de Elisama, que era de sangue real e tinha sido um dos oficiais do rei, foi até Gedalias, filho de Aicam, em Mispá, levando consigo dez homens. Enquanto comiam juntos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19960, 'nvi', 'Jeremias', 'jr', 41, 2, 'Ismael, filho de Netanias, e os dez homens que estavam com ele se levantaram e feriram à espada Gedalias, filho de Aicam, neto de Safã, matando aquele que o rei da Babilônia tinha nomeado governador de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19961, 'nvi', 'Jeremias', 'jr', 41, 3, 'Ismael também matou todos os judeus que estavam com Gedalias em Mispá, bem como os soldados babilônios que ali estavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19962, 'nvi', 'Jeremias', 'jr', 41, 4, 'No dia seguinte ao assassinato de Gedalias, antes que alguém o soubesse,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19963, 'nvi', 'Jeremias', 'jr', 41, 5, 'oitenta homens que haviam rapado a barba, rasgado suas roupas e feito cortes no corpo, vieram de Siquém, de Siló e de Samaria, trazendo ofertas de cereal e incenso para oferecer no templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19964, 'nvi', 'Jeremias', 'jr', 41, 6, 'Ismael, filho de Netanias, saiu de Mispá para encontrá-los, chorando enquanto caminhava. Quando os encontrou, disse: "Venham até onde se encontra Gedalias, filho de Aicam".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19965, 'nvi', 'Jeremias', 'jr', 41, 7, 'Quando entraram na cidade, Ismael, filho de Netanias, e os homens que estavam com ele os mataram e os atiraram numa cisterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19966, 'nvi', 'Jeremias', 'jr', 41, 8, 'Mas dez deles disseram a Ismael: "Não nos mate! Temos trigo e cevada, azeite e mel, escondidos num campo". Então ele os deixou em paz e não os matou com os demais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19967, 'nvi', 'Jeremias', 'jr', 41, 9, 'A cisterna na qual ele jogou os corpos dos homens que havia matado, juntamente com o de Gedalias, tinha sido cavada pelo rei Asa para defender-se de Baasa, rei de Israel. Ismael, filho de Netanias, encheu-a com os mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19968, 'nvi', 'Jeremias', 'jr', 41, 10, 'Ismael tomou como prisioneiros todo o restante do povo que estava em Mispá, inclusive as filhas do rei, sobre os quais Nebuzaradã, o comandante da guarda imperial, havia nomeado Gedalias, filho de Aicam, governador. Ismael, filho de Netanias, levou-os como prisioneiros e partiu para o território de Amom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19969, 'nvi', 'Jeremias', 'jr', 41, 11, 'Quando Joanã, filho de Careá, e todos os comandantes do exército, que com ele estavam, souberam do crime que Ismael, filho de Netanias, tinha cometido,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19970, 'nvi', 'Jeremias', 'jr', 41, 12, 'convocaram todos os seus soldados para lutar contra ele. Eles o alcançaram perto do grande açude de Gibeom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19971, 'nvi', 'Jeremias', 'jr', 41, 13, 'Quando todo o povo, que Ismael tinha levado como prisioneiro, viu Joanã, filho de Careá, e os comandantes do exército que estavam com ele, alegrou-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19972, 'nvi', 'Jeremias', 'jr', 41, 14, 'Todo o povo que Ismael tinha levado como prisioneiro de Mispá se voltou e passou para o lado de Joanã, filho de Careá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19973, 'nvi', 'Jeremias', 'jr', 41, 15, 'Mas Ismael, filho de Netanias, e oito de seus homens escaparam de Joanã e fugiram para o território de Amom');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19974, 'nvi', 'Jeremias', 'jr', 41, 16, 'Então, Joanã, filho de Careá, e todos os comandantes do exército que com ele estavam levaram todos os que tinham restado em Mispá, os quais ele tinha resgatado de Ismael, filho de Netanias, depois que este havia assassinado Gedalias, filho de Aicam: os soldados, as mulheres, as crianças e os oficiais do palácio real, que ele tinha trazido de Gibeom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19975, 'nvi', 'Jeremias', 'jr', 41, 17, 'E eles prosseguiram, parando em Gerute-Quimã, perto de Belém, a caminho do Egito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19976, 'nvi', 'Jeremias', 'jr', 41, 18, 'Queriam escapar dos babilônios. Estavam com medo porque Ismael, filho de Netanias, tinha matado Gedalias, filho de Aicam, a quem o rei da Babilônia nomeara governador de Judá.');
