INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19977, 'nvi', 'Jeremias', 'jr', 42, 1, 'Então todos os líderes do exército, inclusive Joanã, filho de Careá, e Jezanias, filho de Hosaías, e todo o povo, desde o menor até o maior, aproximaram-se');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19978, 'nvi', 'Jeremias', 'jr', 42, 2, 'do profeta Jeremias e lhe disseram: "Por favor, ouça a nossa petição e ore ao Senhor, ao seu Deus, por nós e em favor de todo este remanescente; pois, como você vê, embora fôssemos muitos, agora só restam poucos de nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19979, 'nvi', 'Jeremias', 'jr', 42, 3, 'Ore rogando ao Senhor, ao seu Deus, que nos diga para onde devemos ir e o que devemos fazer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19980, 'nvi', 'Jeremias', 'jr', 42, 4, '"Eu os atenderei", respondeu o profeta Jeremias. "Orarei ao Senhor, ao seu Deus, conforme vocês pediram. E tudo o que o Senhor responder eu lhes direi; nada esconderei de vocês. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19981, 'nvi', 'Jeremias', 'jr', 42, 5, 'Então disseram a Jeremias: "Que o Senhor seja uma testemunha verdadeira e fiel contra nós, caso não façamos tudo o que o Senhor, o seu Deus, nos ordenar por você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19982, 'nvi', 'Jeremias', 'jr', 42, 6, 'Quer seja favorável ou não, obedeceremos ao Senhor, ao nosso Deus, a quem o enviamos, para que tudo vá bem conosco, pois obedeceremos ao Senhor, ao nosso Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19983, 'nvi', 'Jeremias', 'jr', 42, 7, 'Dez dias depois o Senhor dirigiu a palavra a Jeremias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19984, 'nvi', 'Jeremias', 'jr', 42, 8, 'e ele convocou Joanã, filho de Careá, e todos os comandantes do exército que estavam com ele e todo o povo, desde o menor até o maior.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19985, 'nvi', 'Jeremias', 'jr', 42, 9, 'Disse-lhes então: "Assim diz o Senhor, o Deus de Israel, a quem vocês me enviaram para apresentar a petição de vocês:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19986, 'nvi', 'Jeremias', 'jr', 42, 10, '‘Se vocês permanecerem nesta terra, eu os edificarei e não os destruirei; eu os plantarei e não os arrancarei, pois muito me pesa a desgraça que eu trouxe sobre vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19987, 'nvi', 'Jeremias', 'jr', 42, 11, 'Não tenham medo do rei da Babilônia, a quem vocês agora temem. Não tenham medo dele, declara o Senhor, pois estou com vocês e os salvarei e os livrarei das mãos dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19988, 'nvi', 'Jeremias', 'jr', 42, 12, 'Eu terei compaixão de vocês; e ele também terá compaixão de vocês e lhes permitirá retornar à terra de vocês’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19989, 'nvi', 'Jeremias', 'jr', 42, 13, '"Contudo, se vocês disserem ‘Não permaneceremos nesta terra’, e assim desobedecerem ao Senhor, ao seu Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19990, 'nvi', 'Jeremias', 'jr', 42, 14, 'e se disserem: ‘Não, nós iremos para o Egito, onde não veremos a guerra nem ouviremos o som da trombeta, nem passaremos fome’,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19991, 'nvi', 'Jeremias', 'jr', 42, 15, 'ouçam a palavra do Senhor, ó remanescente de Judá. Assim diz o Senhor dos Exércitos, Deus de Israel: ‘Se vocês estão decididos a ir para o Egito e lá forem residir,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19992, 'nvi', 'Jeremias', 'jr', 42, 16, 'a guerra que vocês temem os alcançará, e a fome que receiam os seguirá até o Egito, e lá vocês morrerão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19993, 'nvi', 'Jeremias', 'jr', 42, 17, 'Todos os que estão decididos a partir e residir no Egito morrerão pela guerra, pela fome e pela peste; nem um só deles sobreviverá ou escapará da desgraça que trarei sobre eles’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19994, 'nvi', 'Jeremias', 'jr', 42, 18, 'Assim diz o Senhor dos Exércitos, Deus de Israel: ‘Como o meu furor foi derramado sobre os habitantes de Jerusalém, também a minha ira será derramada sobre vocês, quando forem para o Egito. Vocês serão objeto de maldição e de pavor, de desprezo e de afronta. Vocês jamais tornarão a ver este lugar’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19995, 'nvi', 'Jeremias', 'jr', 42, 19, '"Ó remanescente de Judá, o Senhor lhes disse: ‘Não vão para o Egito’. Estejam certos disto: Eu hoje os advirto');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19996, 'nvi', 'Jeremias', 'jr', 42, 20, 'que vocês cometeram um erro fatal quando me enviaram ao Senhor, ao seu Deus, pedindo: ‘Ore ao Senhor, ao nosso Deus, em nosso favor. Diga-nos tudo o que ele lhe falar, e nós o faremos’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19997, 'nvi', 'Jeremias', 'jr', 42, 21, 'Eu lhes disse, hoje mesmo, o que o Senhor, o seu Deus, me mandou dizer a vocês, mas vocês não lhe estão obedecendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19998, 'nvi', 'Jeremias', 'jr', 42, 22, 'Agora, porém, estejam certos de que vocês morrerão pela guerra, pela fome e pela peste, no lugar que vocês desejam residir".');
