INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20012, 'nvi', 'Jeremias', 'jr', 44, 1, 'Esta é a palavra do Senhor, que foi dirigida a Jeremias, para todos os judeus que estavam no Egito e viviam em Migdol, Tafnes, Mênfis, e na região de Patros:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20013, 'nvi', 'Jeremias', 'jr', 44, 2, '"Assim diz o Senhor dos Exércitos, Deus de Israel: Vocês viram toda a desgraça que eu trouxe sobre Jerusalém e sobre todas as cidades de Judá. Hoje elas estão em ruínas e desabitadas');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20014, 'nvi', 'Jeremias', 'jr', 44, 3, 'por causa do mal que fizeram. Seus moradores provocaram a minha ira queimando incenso e prestando culto a outros deuses, que nem eles nem vocês nem seus antepassados jamais conheceram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20015, 'nvi', 'Jeremias', 'jr', 44, 4, 'Dia após dia, eu lhes enviei meus servos, os profetas, que disseram: ‘Não façam essa abominação detestável! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20016, 'nvi', 'Jeremias', 'jr', 44, 5, 'Mas eles não me ouviram nem me deram atenção; não se converteram de sua impiedade nem cessaram de queimar incenso a outros deuses.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20017, 'nvi', 'Jeremias', 'jr', 44, 6, 'Por isso, o meu furor foi derramado e queimou as cidades de Judá e as ruas de Jerusalém, tornando-as na ruína desolada que são no dia de hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20018, 'nvi', 'Jeremias', 'jr', 44, 7, '"Assim diz o Senhor, o Deus dos Exércitos, o Deus de Israel: Por que trazer uma desgraça tão grande sobre si mesmos, eliminando de Judá homens e mulheres, crianças e recém-nascidos, sem deixar remanescente algum?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20019, 'nvi', 'Jeremias', 'jr', 44, 8, 'Por que vocês provocam a minha ira com o que fazem, queimando incenso a outros deuses no Egito, onde vocês vieram residir? Vocês se destruirão a si mesmos e se tornarão objeto de desprezo e afronta entre todas as nações da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20020, 'nvi', 'Jeremias', 'jr', 44, 9, 'Acaso vocês se esqueceram da impiedade cometida por seus antepassados, pelos reis de Judá e as mulheres deles, e da impiedade cometida por vocês e suas mulheres na terra de Judá e nas ruas de Jerusalém?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20021, 'nvi', 'Jeremias', 'jr', 44, 10, 'Até hoje eles não se humilharam nem mostraram reverência, e não têm seguido a minha lei e os decretos que coloquei diante de vocês e dos seus antepassados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20022, 'nvi', 'Jeremias', 'jr', 44, 11, '"Portanto, assim diz o Senhor dos Exércitos, Deus de Israel: Estou decidido a trazer desgraça sobre vocês e a destruir todo o Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20023, 'nvi', 'Jeremias', 'jr', 44, 12, 'Tomarei o remanescente de Judá, que decidiu partir e residir no Egito, e todos morrerão no Egito. Cairão pela espada ou pela fome; desde o menor até o maior, morrerão pela espada ou pela fome. Eles se tornarão objeto de maldição e de pavor, de desprezo e de afronta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20024, 'nvi', 'Jeremias', 'jr', 44, 13, 'Castigarei aqueles que vivem no Egito com a guerra, a fome e a peste, como castiguei Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20025, 'nvi', 'Jeremias', 'jr', 44, 14, 'Ninguém dentre o remanescente de Judá que foi morar no Egito escapará ou sobreviverá para voltar à terra de Judá, para a qual anseiam voltar e nela anseiam viver; nenhum voltará, exceto uns poucos fugitivos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20026, 'nvi', 'Jeremias', 'jr', 44, 15, 'Então, todos os homens que sabiam que as suas mulheres queimavam incenso a outros deuses, e todas as mulheres que estavam presentes, em grande número, e todo o povo que morava no Egito, e na região de Patros, disseram a Jeremias:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20027, 'nvi', 'Jeremias', 'jr', 44, 16, '"Nós não daremos atenção à mensagem que você nos apresenta em nome do Senhor!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20028, 'nvi', 'Jeremias', 'jr', 44, 17, 'É certo que faremos tudo o que dissemos que faríamos: Queimaremos incenso à Rainha dos Céus e derramaremos ofertas de bebidas para ela, tal como fazíamos, nós e nossos antepassados, nossos reis e nossos líderes, nas cidades de Judá e nas ruas de Jerusalém. Naquela época tínhamos fartura de comida, éramos prósperos e em nada sofríamos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20029, 'nvi', 'Jeremias', 'jr', 44, 18, 'Mas, desde que paramos de queimar incenso à Rainha dos Céus e de derramar ofertas de bebidas a ela, nada temos tido e temos perecido pela espada e pela fome".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20030, 'nvi', 'Jeremias', 'jr', 44, 19, 'E as mulheres acrescentaram: "Quando queimávamos incenso à Rainha dos Céus e derramávamos ofertas de bebidas para ela, será que era sem o consentimento de nossos maridos que fazíamos bolos na forma da imagem dela e derramávamos ofertas de bebidas para ela? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20031, 'nvi', 'Jeremias', 'jr', 44, 20, 'Então Jeremias disse a todo o povo, tanto aos homens como às mulheres que estavam respondendo a ele:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20032, 'nvi', 'Jeremias', 'jr', 44, 21, '"E o Senhor? Não se lembra ele do incenso queimado nas cidades de Judá e nas ruas de Jerusalém por vocês e por seus antepassados, seus reis e seus líderes e pelo povo da terra? Será que ele não pensa nisso?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20033, 'nvi', 'Jeremias', 'jr', 44, 22, 'Quando o Senhor não pôde mais suportar as impiedades e as práticas repugnantes de vocês, a terra de vocês ficou devastada e desolada, e tornou-se objeto de maldição e desabitada, como se vê no dia de hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20034, 'nvi', 'Jeremias', 'jr', 44, 23, 'Foi porque vocês queimaram incenso e pecaram contra o Senhor, e não obedeceram à sua palavra nem seguiram a sua lei, os seus decretos e os seus testemunhos, que esta desgraça caiu sobre vocês, como se vê no dia de hoje".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20035, 'nvi', 'Jeremias', 'jr', 44, 24, 'Disse então Jeremias a todo o povo, inclusive às mulheres: "Ouçam a palavra do Senhor, todos vocês, judeus que estão no Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20036, 'nvi', 'Jeremias', 'jr', 44, 25, 'Assim diz o Senhor dos Exércitos, Deus de Israel: Vocês e suas mulheres cumpriram o que prometeram quando disseram: ‘Certamente cumpriremos os votos que fizemos de queimar incenso e derramar ofertas de bebidas à Rainha dos Céus’. "Prossigam! Façam o que prometeram! Cumpram os seus votos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20037, 'nvi', 'Jeremias', 'jr', 44, 26, 'Mas ouçam a palavra do Senhor, todos vocês, judeus que vivem no Egito: ‘Eu juro pelo meu grande nome’, diz o Senhor, ‘que em todo o Egito ninguém de Judá voltará a invocar o meu nome ou a jurar pela vida do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20038, 'nvi', 'Jeremias', 'jr', 44, 27, 'Vigiarei sobre eles para trazer-lhes a desgraça e não o bem; os judeus do Egito perecerão pela espada e pela fome até que sejam todos destruídos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20039, 'nvi', 'Jeremias', 'jr', 44, 28, 'Serão poucos os que escaparão da espada e voltarão do Egito para a terra de Judá. Então, todo o remanescente de Judá que veio residir no Egito saberá qual é a palavra que se realiza, a minha ou a deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20040, 'nvi', 'Jeremias', 'jr', 44, 29, '" ‘Este será o sinal para vocês de que os castigarei neste lugar’, declara o Senhor, ‘e então vocês ficarão sabendo que as minhas ameaças de trazer-lhes desgraça certamente se realizarão’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20041, 'nvi', 'Jeremias', 'jr', 44, 30, 'Assim diz o Senhor: ‘Entregarei o faraó Hofra, rei do Egito, nas mãos dos seus inimigos que desejam tirar-lhe a vida, assim como entreguei Zedequias, rei de Judá, nas mãos de Nabucodonosor, rei da Babilônia, o inimigo que desejava tirar a vida dele’ ".');
