INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20075, 'nvi', 'Jeremias', 'jr', 47, 1, 'Esta é a palavra do Senhor que veio ao profeta Jeremias acerca dos filisteus, antes do ataque do faraó a Gaza:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20076, 'nvi', 'Jeremias', 'jr', 47, 2, 'Assim diz o Senhor: "Vejam como as águas estão subindo do norte; elas se tornam uma torrente transbordante. Inundarão esta terra e tudo o que nela existe; as cidades e os seus habitantes. O povo clamará, gritarão todos os habitantes desta terra,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20077, 'nvi', 'Jeremias', 'jr', 47, 3, 'ao estrondo dos cascos dos seus cavalos galopando, ao barulho dos seus carros de guerra, e ao estampido de suas rodas. Os pais não se voltarão para ajudar seus filhos; pois suas mãos estão fracas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20078, 'nvi', 'Jeremias', 'jr', 47, 4, 'Pois chegou o dia de destruir todos os filisteus e de eliminar todos os sobreviventes que poderiam ajudar Tiro e Sidom. O Senhor destruirá os filisteus, o remanescente da ilha de Caftor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20079, 'nvi', 'Jeremias', 'jr', 47, 5, 'Os habitantes de Gaza rasparam a cabeça; Ascalom está calada. Ó remanescentes da planície, até quando você fará incisões no próprio corpo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20080, 'nvi', 'Jeremias', 'jr', 47, 6, '" ‘Ah, espada do Senhor, quando você descansará? Volte à sua bainha, acalme-se e repouse. ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20081, 'nvi', 'Jeremias', 'jr', 47, 7, 'Mas como poderá ela descansar quando o Senhor lhe deu ordens, quando ele lhe ordenou que ataque Ascalom e o litoral? "');
