INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20082, 'nvi', 'Jeremias', 'jr', 48, 1, 'Acerca de Moabe assim diz o Senhor dos Exércitos, Deus de Israel: "Ai de Nebo, pois ficou em ruínas. Quiriataim foi derrotada e capturada; a fortaleza foi derrotada e destroçada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20083, 'nvi', 'Jeremias', 'jr', 48, 2, 'Moabe não é mais louvada; em Hesbom tramam a sua ruína: ‘Venham! Vamos dar fim àquela nação’. Você também ficará calada, ó Madmém; a espada a perseguirá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20084, 'nvi', 'Jeremias', 'jr', 48, 3, 'Ouçam os gritos de Horonaim: ‘Devastação! Grande destruição!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20085, 'nvi', 'Jeremias', 'jr', 48, 4, 'Moabe está destruída! ’ É o grito que se ouve até em Zoar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20086, 'nvi', 'Jeremias', 'jr', 48, 5, 'Eles sobem pelo caminho para Luíte, chorando amargamente enquanto seguem; na estrada que desce a Horonaim ouvem-se gritos angustiados por causa da destruição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20087, 'nvi', 'Jeremias', 'jr', 48, 6, 'Fujam! Corram para salvar suas vidas; tornem-se como um arbusto no deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20088, 'nvi', 'Jeremias', 'jr', 48, 7, 'Uma vez que vocês confiam em seus feitos e em suas riquezas, vocês também serão capturados, e Camos irá para o exílio, junto com seus sacerdotes e líderes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20089, 'nvi', 'Jeremias', 'jr', 48, 8, 'O destruidor virá contra todas as cidades, e nenhuma escapará. O vale se tornará ruínas, e o planalto será destruído, como o Senhor falou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20090, 'nvi', 'Jeremias', 'jr', 48, 9, 'Ponham sal sobre Moabe, pois ela será deixada em ruínas; suas cidades ficarão devastadas, sem nenhum habitante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20091, 'nvi', 'Jeremias', 'jr', 48, 10, '"Maldito o que faz com negligência o trabalho do Senhor! Maldito aquele que impede a sua espada de derramar sangue!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20092, 'nvi', 'Jeremias', 'jr', 48, 11, '"Moabe tem estado tranqüila desde a sua juventude, como o vinho deixado com os seus resíduos; não foi mudada de vasilha em vasilha. Nunca foi para o exílio; por isso, o seu sabor permanece o mesmo e o seu cheiro não mudou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20093, 'nvi', 'Jeremias', 'jr', 48, 12, 'Portanto, certamente vêm os dias", declara o Senhor, "quando enviarei decantadores que o decantarão; esvaziarão as suas jarras e as despedaçarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20094, 'nvi', 'Jeremias', 'jr', 48, 13, 'Então Moabe se decepcionará com Camos, assim como Israel se decepcionou com Betel, em quem confiava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20095, 'nvi', 'Jeremias', 'jr', 48, 14, '"Como vocês podem dizer: ‘Somos guerreiros, somos homens de guerra’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20096, 'nvi', 'Jeremias', 'jr', 48, 15, 'Moabe foi destruída e suas cidades serão invadidas; o melhor dos seus jovens desceu para a matança", declara o Rei, cujo nome é o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20097, 'nvi', 'Jeremias', 'jr', 48, 16, '"A derrota de Moabe está próxima; a sua desgraça vem rapidamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20098, 'nvi', 'Jeremias', 'jr', 48, 17, 'Lamentem por ela, todos os seus vizinhos, todos os que conhecem a sua fama. Digam: ‘Como está quebrado o cajado poderoso, o cetro glorioso! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20099, 'nvi', 'Jeremias', 'jr', 48, 18, '"Desçam de sua glória e sentem-se sobre o chão ressequido, ó moradores da cidade de Dibom, pois o destruidor de Moabe veio para atacá-los e destruir as suas fortalezas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20100, 'nvi', 'Jeremias', 'jr', 48, 19, 'Fiquem junto à estrada e vigiem, vocês que vivem em Aroer. Perguntem ao homem que foge e à mulher que escapa, perguntem a eles: ‘O que aconteceu? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20101, 'nvi', 'Jeremias', 'jr', 48, 20, 'Moabe ficou envergonhada, pois está destroçada. Gritem e clamem! Anunciem junto ao Arnom que Moabe foi destruída.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20102, 'nvi', 'Jeremias', 'jr', 48, 21, 'O julgamento chegou ao planalto: a Holom, Jaza e Mefaate,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20103, 'nvi', 'Jeremias', 'jr', 48, 22, 'a Dibom, Nebo e Bete-Diblataim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20104, 'nvi', 'Jeremias', 'jr', 48, 23, 'a Quiriataim, Bete-Gamul e Bete-Meom,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20105, 'nvi', 'Jeremias', 'jr', 48, 24, 'a Queriote e Bozra, a todas as cidades de Moabe, distantes e próximas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20106, 'nvi', 'Jeremias', 'jr', 48, 25, 'O poder de Moabe foi eliminado; seu braço está quebrado", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20107, 'nvi', 'Jeremias', 'jr', 48, 26, '"Embriaguem-na, pois ela desafiou o Senhor. Moabe se revolverá no seu vômito e será objeto de ridículo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20108, 'nvi', 'Jeremias', 'jr', 48, 27, 'Não foi Israel objeto de ridículo para você? Foi ele encontrado em companhia de ladrões para que você sacuda a cabeça sempre que fala dele?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20109, 'nvi', 'Jeremias', 'jr', 48, 28, 'Abandonem as cidades! Habitem entre as rochas, vocês que moram em Moabe! Sejam como uma pomba que faz o seu ninho nas bordas de um precipício.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20110, 'nvi', 'Jeremias', 'jr', 48, 29, '"Temos ouvido do orgulho de Moabe: da sua extrema arrogância, do seu orgulho e soberba, e do seu espírito de superioridade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20111, 'nvi', 'Jeremias', 'jr', 48, 30, 'Conheço bem a sua arrogância", declara o Senhor. "A sua tagarelice sem fundamento e as suas ações que nada alcançam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20112, 'nvi', 'Jeremias', 'jr', 48, 31, 'Por isso, lamentarei por Moabe, gritarei por causa de toda a terra de Moabe, prantearei pelos habitantes de Quir-Heres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20113, 'nvi', 'Jeremias', 'jr', 48, 32, 'Chorarei por vocês mais do que choro por Jazar, ó videiras de Sibma. Os seus ramos se estendiam até o mar, e chegavam até Jazar. O destruidor caiu sobre as suas frutas e sobre as suas uvas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20114, 'nvi', 'Jeremias', 'jr', 48, 33, 'A alegria e a satisfação se foram das terras férteis de Moabe. Interrompi a produção de vinho nos lagares. Ninguém mais pisa as uvas com gritos de alegria; embora haja gritos, não são de alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20115, 'nvi', 'Jeremias', 'jr', 48, 34, '"O grito de Hesbom é ouvido em Eleale e Jaaz, desde Zoar até Horonaim e Eglate-Selisia, pois até as águas do Ninrim secaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20116, 'nvi', 'Jeremias', 'jr', 48, 35, 'Em Moabe darei fim àqueles que fazem ofertas nos altares idólatras e queimam incenso a seus deuses", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20117, 'nvi', 'Jeremias', 'jr', 48, 36, '"Por isso o meu coração lamenta-se por Moabe, como uma flauta; lamenta-se como uma flauta pelos habitantes de Quir-Heres. A riqueza que acumularam se foi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20118, 'nvi', 'Jeremias', 'jr', 48, 37, 'Toda cabeça foi rapada e toda barba foi cortada; toda mão sofreu incisões e toda cintura foi coberta com veste de lamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20119, 'nvi', 'Jeremias', 'jr', 48, 38, 'Em todos os terraços de Moabe e nas praças não há nada senão pranto, pois despedacei Moabe como a um jarro que ninguém deseja", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20120, 'nvi', 'Jeremias', 'jr', 48, 39, '"Como ela foi destruída! Como lamentam! Como Moabe dá as costas, envergonhada! Moabe tornou-se objeto de ridículo e de pavor para todos os seus vizinhos. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20121, 'nvi', 'Jeremias', 'jr', 48, 40, 'Assim diz o Senhor: "Vejam! Uma águia planando estende as asas sobre Moabe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20122, 'nvi', 'Jeremias', 'jr', 48, 41, 'Queriote será capturada, e as fortalezas serão tomadas. Naquele dia, a coragem dos guerreiros de Moabe será como a de uma mulher em trabalho de parto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20123, 'nvi', 'Jeremias', 'jr', 48, 42, 'Moabe será destruída como nação pois ela desafiou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20124, 'nvi', 'Jeremias', 'jr', 48, 43, 'Terror e cova e laço esperam por você, ó povo de Moabe", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20125, 'nvi', 'Jeremias', 'jr', 48, 44, '"Quem fugir do terror cairá numa cova, e quem sair da cova será apanhado num laço. Trarei sobre Moabe a hora do seu castigo", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20126, 'nvi', 'Jeremias', 'jr', 48, 45, '"Na sombra de Hesbom os fugitivos se encontram desamparados, pois um fogo saiu de Hesbom, uma labareda do meio de Seom; e queima as testas dos homens de Moabe e os crânios de homens turbulentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20127, 'nvi', 'Jeremias', 'jr', 48, 46, 'Ai de você, ó Moabe! O povo de Camos está destruído; Seus filhos são levados para o exílio, e suas filhas para o cativeiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20128, 'nvi', 'Jeremias', 'jr', 48, 47, '"Contudo, restaurarei a sorte de Moabe em dias vindouros", declara o Senhor. Aqui termina a sentença sobre Moabe.');
