INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20168, 'nvi', 'Jeremias', 'jr', 50, 1, 'Esta é a palavra que o Senhor falou pelo profeta Jeremias acerca da Babilônia e da terra dos babilônios:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20169, 'nvi', 'Jeremias', 'jr', 50, 2, '"Anunciem e proclamem entre as nações, ergam um sinal e proclamem; não escondam nada. Digam: ‘A Babilônia foi conquistada; Bel foi humilhado, Marduque apavorado. As imagens da Babilônia estão humilhadas e seus ídolos apavorados’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20170, 'nvi', 'Jeremias', 'jr', 50, 3, 'Uma nação vinda do norte a atacará, arrasará a sua terra e não deixará nela nenhum habitante; tanto homens como animais fugirão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20171, 'nvi', 'Jeremias', 'jr', 50, 4, '"Naqueles dias e naquela época", declara o Senhor, "o povo de Israel e o povo de Judá virão juntos, chorando e buscando o Senhor seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20172, 'nvi', 'Jeremias', 'jr', 50, 5, 'Perguntarão pelo caminho para Sião e voltarão o rosto na direção dela. Virão e se apegarão ao Senhor numa aliança permanente que não será esquecida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20173, 'nvi', 'Jeremias', 'jr', 50, 6, '"Meu povo tem sido ovelhas perdidas; seus pastores as desencaminharam e as fizeram perambular pelos montes. Elas vaguearam por montanhas e colinas e se esqueceram de seu próprio curral.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20174, 'nvi', 'Jeremias', 'jr', 50, 7, 'Todos que as encontram as devoram. Os seus adversários disseram: ‘Não somos culpados, pois elas pecaram contra o Senhor, sua verdadeira pastagem, o Senhor, a esperança de seus antepassados’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20175, 'nvi', 'Jeremias', 'jr', 50, 8, '"Fujam da Babilônia; saiam da terra dos babilônios e sejam como os bodes que lideram o rebanho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20176, 'nvi', 'Jeremias', 'jr', 50, 9, 'Vejam! Eu mobilizarei e trarei contra a Babilônia uma coalizão de grandes nações do norte. Elas tomarão posição de combate contra ela e a conquistarão. Suas flechas serão como guerreiros bem treinados, que não voltam de mãos vazias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20177, 'nvi', 'Jeremias', 'jr', 50, 10, 'Assim a Babilônia será saqueada; todos os que a saquearem se fartarão", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20178, 'nvi', 'Jeremias', 'jr', 50, 11, '"Ainda que você esteja alegre e exultante, você que saqueia a minha herança; ainda que você seja brincalhão como uma novilha solta no pasto, e relincha como os garanhões,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20179, 'nvi', 'Jeremias', 'jr', 50, 12, 'sua mãe se envergonhará profundamente; aquela que lhes deu à luz ficará constrangida. Ela se tornará a menor das nações, um deserto, uma terra seca e árida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20180, 'nvi', 'Jeremias', 'jr', 50, 13, 'Por causa da ira do Senhor ela não será habitada, mas estará completamente desolada. Todos os que passarem pela Babilônia ficarão chocados e zombarão por causa de todas as suas feridas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20181, 'nvi', 'Jeremias', 'jr', 50, 14, '"Tomem posição de combate em volta da Babilônia, todos vocês que empunham o arco. Atirem nela! Não poupem flechas, pois ela pecou contra o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20182, 'nvi', 'Jeremias', 'jr', 50, 15, 'Soem contra ela um grito de guerra de todos os lados! Ela se rende, suas torres caem e suas muralhas são derrubadas. Esta é a vingança do Senhor; vinguem-se dela! Façam a ela o que ela fez aos outros!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20183, 'nvi', 'Jeremias', 'jr', 50, 16, 'Eliminem da Babilônia o semeador, e o ceifeiro com a sua foice na colheita. Por causa da espada do opressor que cada um volte para o seu próprio povo, e cada um fuja para a sua própria terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20184, 'nvi', 'Jeremias', 'jr', 50, 17, '"Israel é um rebanho disperso, afugentado por leões. O primeiro a devorá-lo foi o rei da Assíria; e o último a esmagar os seus ossos foi Nabucodonosor, rei da Babilônia. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20185, 'nvi', 'Jeremias', 'jr', 50, 18, 'Portanto, assim diz o Senhor dos Exércitos, o Deus de Israel: "Castigarei o rei da Babilônia e a sua terra assim como castiguei o rei da Assíria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20186, 'nvi', 'Jeremias', 'jr', 50, 19, 'Mas trarei Israel de volta a sua própria pastagem e ele pastará no Carmelo e em Basã; e saciará o seu apetite nos montes de Efraim e em Gileade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20187, 'nvi', 'Jeremias', 'jr', 50, 20, 'Naqueles dias, naquela época", declara o Senhor, "se procurará pela iniqüidade de israel, mas nada será achado, pelos pecados de Judá, mas nenhum será encontrado, pois perdoarei o remanescente que eu poupar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20188, 'nvi', 'Jeremias', 'jr', 50, 21, '"Ataquem a terra de Merataim e aqueles que moram em Pecode. Persigam-nos, matem-nos e destruam-nos totalmente", declara o Senhor. "Façam tudo que lhes ordenei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20189, 'nvi', 'Jeremias', 'jr', 50, 22, 'Há ruído de batalha na terra; grande destruição!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20190, 'nvi', 'Jeremias', 'jr', 50, 23, 'Quão quebrado e destroçado está o martelo de toda a terra! Quão arrasada está a Babilônia entre as nações!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20191, 'nvi', 'Jeremias', 'jr', 50, 24, 'Preparei uma armadilha para você, ó Babilônia, e você foi apanhada antes de percebê-lo; você foi achada e capturada porque se opôs ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20192, 'nvi', 'Jeremias', 'jr', 50, 25, 'O Senhor abriu o seu arsenal e trouxe para fora as armas da sua ira, pois o Soberano Senhor dos Exércitos tem trabalho para fazer na terra dos babilônios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20193, 'nvi', 'Jeremias', 'jr', 50, 26, 'Venham contra ela dos confins da terra. Arrombem os seus celeiros; empilhem-na como feixes de cereal. Destruam-na totalmente e não lhe deixem nenhum remanescente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20194, 'nvi', 'Jeremias', 'jr', 50, 27, 'Matem todos os seus jovens guerreiros! Que eles desçam para o matadouro! Ai deles! Pois chegou o seu dia, a hora de serem castigados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20195, 'nvi', 'Jeremias', 'jr', 50, 28, 'Escutem os fugitivos e refugiados vindos da Babilônia, declarando em Sião como, o Senhor, o nosso Deus se vingou, como se vingou de seu templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20196, 'nvi', 'Jeremias', 'jr', 50, 29, '"Convoquem flecheiros contra a Babilônia, todos aqueles que empunham o arco. Acampem-se todos ao redor dela; não deixem ninguém escapar. Retribuam a ela conforme os seus feitos; façam com ela tudo o que ela fez. Porque ela desafiou o Senhor, o Santo de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20197, 'nvi', 'Jeremias', 'jr', 50, 30, 'Por isso, os seus jovens cairão nas ruas e todos os seus guerreiros se calarão naquele dia", declara o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20198, 'nvi', 'Jeremias', 'jr', 50, 31, '"Vejam, estou contra você, ó arrogante", declara o Soberano Senhor dos Exércitos, "pois chegou o seu dia, a sua hora de ser castigada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20199, 'nvi', 'Jeremias', 'jr', 50, 32, 'A arrogância tropeçará e cairá, e ninguém a ajudará a se levantar. Incendiarei as suas cidades, e o fogo consumirá tudo ao seu redor. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20200, 'nvi', 'Jeremias', 'jr', 50, 33, 'Assim diz o Senhor dos Exércitos: "O povo de Israel está sendo oprimido, e também o povo de Judá. Todos os seus captores os prendem à força, recusando deixá-los ir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20201, 'nvi', 'Jeremias', 'jr', 50, 34, 'Contudo, o Redentor deles é forte; o Senhor dos Exércitos é o seu nome. Ele mesmo defenderá a causa deles, e trará descanso à terra, mas inquietação aos que vivem na Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20202, 'nvi', 'Jeremias', 'jr', 50, 35, '"Uma espada contra os babilônios! ", declara o Senhor; "contra os que vivem na Babilônia e contra seus líderes e sábios!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20203, 'nvi', 'Jeremias', 'jr', 50, 36, 'Uma espada contra os seus falsos profetas! Eles se tornarão tolos. Uma espada contra os seus guerreiros! Eles ficarão apavorados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20204, 'nvi', 'Jeremias', 'jr', 50, 37, 'Uma espada contra os seus cavalos, os seus carros de guerra e contra todos os estrangeiros em suas fileiras! Eles serão como mulheres. Uma espada contra os seus tesouros! Eles serão saqueados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20205, 'nvi', 'Jeremias', 'jr', 50, 38, 'Uma espada contra as suas águas! Elas secarão. Porque é uma terra de imagens esculpidas, e eles enlouquecem por causa de seus ídolos horríveis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20206, 'nvi', 'Jeremias', 'jr', 50, 39, '"Por isso, criaturas do deserto e hienas nela morarão, e as corujas nela habitarão. Ela jamais voltará a ser habitada nem haverá quem nela viva no futuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20207, 'nvi', 'Jeremias', 'jr', 50, 40, 'Como Deus destruiu Sodoma e Gomorra e as cidades vizinhas", diz o Senhor, "ninguém mais habitará ali, nenhum homem residirá nela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20208, 'nvi', 'Jeremias', 'jr', 50, 41, '"Vejam! Vem vindo um povo do norte; uma grande nação e muitos reis se mobilizam desde os confins da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20209, 'nvi', 'Jeremias', 'jr', 50, 42, 'Eles empunham o arcos e a lança; são cruéis e sem misericórdia, e o seu barulho é como o bramido do mar. Vêm montados em seus cavalos, em formação de batalha, para atacá-la, ó cidade de Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20210, 'nvi', 'Jeremias', 'jr', 50, 43, 'Quando o rei da Babilônia ouviu relatos sobre eles, as suas mãos amoleceram. A angústia tomou conta dele, dores como as de uma mulher dando à luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20211, 'nvi', 'Jeremias', 'jr', 50, 44, 'Assim como um leão que sobe da mata do Jordão em direção aos pastos verdejantes, subitamente eu caçarei a Babilônia para fora de sua terra. Quem é o escolhido que designarei para isso? Quem é como eu que possa me desafiar? E que pastor pode me resistir? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20212, 'nvi', 'Jeremias', 'jr', 50, 45, 'Por isso, ouçam o que o Senhor planejou contra a Babilônia, o que ele preparou contra a terra dos babilônios: os menores do rebanho serão arrastados, e as pastagens ficarão devastadas por causa deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20213, 'nvi', 'Jeremias', 'jr', 50, 46, 'Ao som da tomada da Babilônia a terra tremerá; o grito deles ressoará entre as nações.');
