INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20214, 'nvi', 'Jeremias', 'jr', 51, 1, 'Assim diz o Senhor: "Vejam! Levantarei um vento destruidor contra a Babilônia, contra o povo de Lebe-Camai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20215, 'nvi', 'Jeremias', 'jr', 51, 2, 'Enviarei estrangeiros para a Babilônia a fim de peneirá-la como trigo e devastar a sua terra. No dia de sua desgraça virão contra ela de todos os lados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20216, 'nvi', 'Jeremias', 'jr', 51, 3, 'Que o arqueiro não arme o seu arco nem vista a sua armadura. Não poupem os seus jovens guerreiros, destruam completamente o seu exército.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20217, 'nvi', 'Jeremias', 'jr', 51, 4, 'Eles cairão mortos na Babilônia, mortalmente feridos em suas ruas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20218, 'nvi', 'Jeremias', 'jr', 51, 5, 'Israel e Judá não foram abandonadas como viúvas pelo seu Deus, o Senhor dos Exércitos, embora a terra dos babilônios esteja cheia de culpa diante do Santo de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20219, 'nvi', 'Jeremias', 'jr', 51, 6, '"Fujam da Babilônia! Cada um por si! Não sejam destruídos por causa da iniqüidade dela. É hora da vingança do Senhor; ele pagará a ela o que ela merece.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20220, 'nvi', 'Jeremias', 'jr', 51, 7, 'A Babilônia era um cálice de ouro nas mãos do Senhor; ela embriagou a terra toda. As nações beberam o seu vinho; por isso agora, enlouqueceram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20221, 'nvi', 'Jeremias', 'jr', 51, 8, 'A Babilônia caiu de repente e ficou arruinada. Lamentem por ela! Consigam bálsamo para a ferida dela; talvez ela possa ser curada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20222, 'nvi', 'Jeremias', 'jr', 51, 9, '"Gostaríamos de ter curado Babilônia, mas ela não pode ser curada; deixem-na e vamos cada um para a sua própria terra, pois o julgamento dela chega ao céu, eleva-se tão alto quanto as nuvens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20223, 'nvi', 'Jeremias', 'jr', 51, 10, '"O Senhor defendeu o nosso nome; venham, contemos em Sião o que o Senhor, o nosso Deus, tem feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20224, 'nvi', 'Jeremias', 'jr', 51, 11, '"Afiem as flechas, peguem os escudos! O Senhor incitou o espírito dos reis dos medos, porque seu propósito é destruir a Babilônia. O Senhor se vingará, se vingará de seu templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20225, 'nvi', 'Jeremias', 'jr', 51, 12, 'Ergam o sinal para atacar as muralhas da Babilônia! Reforcem a guarda! Posicionem as sentinelas! Preparem uma emboscada! O Senhor executará o seu plano, o que ameaçou fazer contra os habitantes da Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20226, 'nvi', 'Jeremias', 'jr', 51, 13, 'Você que vive junto a muitas águas e está rico de tesouros, chegou o seu fim, a hora de você ser eliminado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20227, 'nvi', 'Jeremias', 'jr', 51, 14, 'O Senhor dos Exércitos jurou por si mesmo: com certeza a encherei de homens, como com um enxame de gafanhotos, e eles gritarão triunfante sobre você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20228, 'nvi', 'Jeremias', 'jr', 51, 15, '"Mas foi Deus quem fez a terra com o seu poder; firmou o mundo com a sua sabedoria e estendeu os céus com o seu entendimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20229, 'nvi', 'Jeremias', 'jr', 51, 16, 'Ao som do seu trovão, as águas no céu rugem; ele faz com que as nuvens se levantem desde os confins da terra. Ele faz relâmpagos para a chuva e faz sair o vento de seus depósitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20230, 'nvi', 'Jeremias', 'jr', 51, 17, '"São todos eles estúpidos e ignorantes; cada ourives é envergonhado pela imagem que esculpiu. Suas imagens esculpidas são uma fraude, elas não têm fôlego de vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20231, 'nvi', 'Jeremias', 'jr', 51, 18, 'Elas são inúteis, são objetos de zombaria. Quando vier o julgamento delas, perecerão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20232, 'nvi', 'Jeremias', 'jr', 51, 19, 'Aquele que é a Porção de Jacó não é como esses, pois ele é quem forma todas as coisas, e Israel é a tribo de sua propriedade; o Senhor dos Exércitos é o seu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20233, 'nvi', 'Jeremias', 'jr', 51, 20, '"Você é o meu martelo, a minha arma de guerra. Com você eu despedaço nações, com você eu destruo reinos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20234, 'nvi', 'Jeremias', 'jr', 51, 21, 'com você despedaço cavalo e cavaleiro, com você despedaço carro de guerra e cocheiro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20235, 'nvi', 'Jeremias', 'jr', 51, 22, 'com você despedaço homem e mulher, com você despedaço velho e jovem, com você despedaço rapaz e moça,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20236, 'nvi', 'Jeremias', 'jr', 51, 23, 'com você despedaço pastor e rebanho, com você despedaço lavrador e bois, com você despedaço governadores e oficiais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20237, 'nvi', 'Jeremias', 'jr', 51, 24, '"Retribuirei à Babilônia e a todos os que vivem na Babilônia por toda a maldade que fizeram em Sião diante dos olhos de vocês", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20238, 'nvi', 'Jeremias', 'jr', 51, 25, '"Estou contra você, ó montanha destruidora, você que destrói a terra inteira", declara o Senhor. "Estenderei minha mão contra você, eu a farei rolar dos penhascos, e farei de você uma montanha calcinada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20239, 'nvi', 'Jeremias', 'jr', 51, 26, 'Nenhuma pedra sua será cortada para servir de pedra angular, nem para um alicerce, pois você estará arruinada para sempre", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20240, 'nvi', 'Jeremias', 'jr', 51, 27, '"Ergam um estandarte na terra! Toquem a trombeta entre as nações! Preparem as nações para o combate contra ela; convoquem contra ela estes reinos: Ararate, Mini e Asquenaz. Nomeiem um comandante contra ela; lancem os cavalos ao ataque como um enxame de gafanhotos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20241, 'nvi', 'Jeremias', 'jr', 51, 28, 'Preparem as nações para o combate contra ela: os reis dos medos, seus governadores e todos os seus oficiais, e todos os países que governam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20242, 'nvi', 'Jeremias', 'jr', 51, 29, 'A terra treme e se contorce de dor, pois permanecem de pé os planos do Senhor contra a Babilônia: desolar a terra da Babilônia para que fique desabitada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20243, 'nvi', 'Jeremias', 'jr', 51, 30, 'Os guerreiros da Babilônia pararam de lutar; permanecem em suas fortalezas. A força deles acabou; tornaram-se como mulheres. As habitações dela estão incendiadas; as trancas de suas portas estão quebradas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20244, 'nvi', 'Jeremias', 'jr', 51, 31, 'Um emissário vai após outro, e um mensageiro sai após outro mensageiro para anunciar ao rei da Babilônia que sua cidade inteira foi capturada,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20245, 'nvi', 'Jeremias', 'jr', 51, 32, 'os pontos de cruzar o rio foram tomados, a vegetação dos pântanos foi incendiada, e os soldados ficaram aterrorizados. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20246, 'nvi', 'Jeremias', 'jr', 51, 33, 'Assim diz o Senhor dos Exércitos, Deus de Israel: "A cidade de Babilônia é como uma eira a época da colheita logo chegará para ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20247, 'nvi', 'Jeremias', 'jr', 51, 34, '"Nabucodonosor, rei da Babilônia, devorou-nos, lançou-nos em confusão, fez de nós um jarro vazio. Tal como uma serpente ele nos engoliu e encheu seu estômago com nossas finas comidas e então nos vomitou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20248, 'nvi', 'Jeremias', 'jr', 51, 35, 'Que a violência cometida à nossa carne esteja sobre a Babilônia", dizem os habitantes de Sião. "Que o nosso sangue esteja sobre aqueles que moram na Babilônia", diz Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20249, 'nvi', 'Jeremias', 'jr', 51, 36, 'Por isso, assim diz o Senhor: "Vejam, defenderei a causa de vocês e os vingarei; secarei o seu mar e esgotarei as suas fontes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20250, 'nvi', 'Jeremias', 'jr', 51, 37, 'A Babilônia se tornará um amontoado de ruínas, uma habitação de chacais, objeto de pavor e de zombaria, um lugar onde ninguém vive.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20251, 'nvi', 'Jeremias', 'jr', 51, 38, 'O seu povo todo ruge como leõezinhos, rosnam como filhotes de leão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20252, 'nvi', 'Jeremias', 'jr', 51, 39, 'Mas, enquanto estiverem excitados, prepararei um banquete para eles e os deixarei bêbados, para que fiquem bem alegres e, então, durmam e jamais acordem", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20253, 'nvi', 'Jeremias', 'jr', 51, 40, '"Eu os levarei como cordeiros para o matadouro, como carneiros e bodes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20254, 'nvi', 'Jeremias', 'jr', 51, 41, '"Como Sesaque será capturada, o orgulho de toda a terra tomado! Que horror a Babilônia será entre as nações!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20255, 'nvi', 'Jeremias', 'jr', 51, 42, 'O mar se levantará sobre a Babilônia; suas ondas agitadas a cobrirão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20256, 'nvi', 'Jeremias', 'jr', 51, 43, 'Suas cidades serão arrasadas, uma terra seca e deserta, uma terra onde ninguém mora, pela qual nenhum homem passa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20257, 'nvi', 'Jeremias', 'jr', 51, 44, 'Castigarei Bel na Babilônia e o farei vomitar o que engoliu. As nações não mais acorrerão a ele. E a muralha da Babilônia cairá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20258, 'nvi', 'Jeremias', 'jr', 51, 45, '"Saia dela, meu povo! Cada um salve a sua própria vida, da ardente ira do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20259, 'nvi', 'Jeremias', 'jr', 51, 46, 'Não desanimem nem tenham medo quando ouvirem rumores na terra; um rumor chega este ano, outro no próximo, rumor de violência na terra e de governante contra governante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20260, 'nvi', 'Jeremias', 'jr', 51, 47, 'Portanto, certamente vêm os dias quando castigarei as imagens esculpidas da Babilônia; toda a sua terra será envergonhada, e todos os seus mortos jazerão caídos dentro dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20261, 'nvi', 'Jeremias', 'jr', 51, 48, 'Então o céu e a terra e tudo o que existe neles gritará de alegria por causa da Babilônia, pois do norte destruidores a atacarão", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20262, 'nvi', 'Jeremias', 'jr', 51, 49, '"A Babilônia cairá por causa dos mortos de Israel, assim como os mortos de toda a terra caíram por causa da Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20263, 'nvi', 'Jeremias', 'jr', 51, 50, 'Vocês que escaparam da espada, saiam! Não permaneçam! Lembrem-se do Senhor numa terra distante, e pensem em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20264, 'nvi', 'Jeremias', 'jr', 51, 51, '"Estamos envergonhados pois fomos insultados e a vergonha cobre o nosso rosto, porque estrangeiros penetraram nos lugares santos do templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20265, 'nvi', 'Jeremias', 'jr', 51, 52, '"Portanto, centamente vêm os dias", declara o Senhor, "quando castigarei as suas imagens esculpidas, e por toda a sua terra os feridos gemerão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20266, 'nvi', 'Jeremias', 'jr', 51, 53, 'Mesmo que a Babilônia chegue ao céu e fortifique no alto a sua fortaleza, enviarei destruidores contra ela", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20267, 'nvi', 'Jeremias', 'jr', 51, 54, '"Vem da Babilônia o som de um grito, o som de grande destruição vem da terra dos babilônios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20268, 'nvi', 'Jeremias', 'jr', 51, 55, 'O Senhor destruirá a Babilônia; ele silenciará o seu grande ruído. Ondas de inimigos avançarão como grandes águas; o rugir de suas vozes ressoará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20269, 'nvi', 'Jeremias', 'jr', 51, 56, 'Um destruidor virá contra a Babilônia; seus guerreiros serão capturados, e seus arcos serão quebrados. Pois o Senhor é um Deus de retribuição; ele retribuirá plenamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20270, 'nvi', 'Jeremias', 'jr', 51, 57, 'Embebedarei os seus líderes e os seus sábios; os seus governadores, os seus oficiais e os seus guerreiros. Eles dormirão para sempre e jamais acordarão", declara o Rei, cujo nome é Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20271, 'nvi', 'Jeremias', 'jr', 51, 58, 'Assim diz o Senhor dos Exércitos: "A larga muralha da Babilônia será desmantelada e suas altas portas serão incendiadas. Os povos se exaurem por nada, o trabalho das nações não passa de combustível para as chamas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20272, 'nvi', 'Jeremias', 'jr', 51, 59, 'Esta é a mensagem que Jeremias deu ao responsável pelo acampamento Seraías, filho de Nerias, filho de Maaséias, quando ele foi à Babilônia com o rei Zedequias de Judá no quarto ano do seu reinado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20273, 'nvi', 'Jeremias', 'jr', 51, 60, 'Jeremias escreveu num rolo todas as desgraças que sobreviriam à Babilônia, tudo que fora registrado acerca da Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20274, 'nvi', 'Jeremias', 'jr', 51, 61, 'Ele disse a Seraías: "Quando você chegar à Babilônia, tenha o cuidado de ler todas estas palavras em voz alta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20275, 'nvi', 'Jeremias', 'jr', 51, 62, 'Então diga: ‘Ó Senhor, disseste que destruirás este lugar, para que nem homem nem animal viva nele, pois ficará em ruínas para sempre’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20276, 'nvi', 'Jeremias', 'jr', 51, 63, 'Quando você terminar de ler este rolo, amarre nele uma pedra e atire-o no Eufrates.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20277, 'nvi', 'Jeremias', 'jr', 51, 64, 'Então diga: ‘Assim Babilônia afundará para não mais se erguer, por causa da desgraça que trarei sobre ela. E seu povo cairá’ ". Aqui terminam as palavras de Jeremias.');
