INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19091, 'nvi', 'Jeremias', 'jr', 6, 1, '"Fuja para um lugar seguro, povo de Benjamim! Fuja de Jerusalém! Toquem a trombeta em Tecoa! Ponham sinal em Bete-Haquerém! Porque já se vê a desgraça que vem do Norte, uma terrível destruição!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19092, 'nvi', 'Jeremias', 'jr', 6, 2, 'Destruirei a cidade de Sião; você é como uma bela pastagem,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19093, 'nvi', 'Jeremias', 'jr', 6, 3, 'para onde os pastores vêm com os seus rebanhos; armam as suas tendas ao redor dela e apascentam, cada um no seu lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19094, 'nvi', 'Jeremias', 'jr', 6, 4, '"Preparem-se para enfrentá-la na batalha! Vamos, ataquemos ao meio-dia! Ai de nós! O dia declina e as sombras da tarde já se estendem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19095, 'nvi', 'Jeremias', 'jr', 6, 5, 'Vamos, ataquemos de noite! Destruamos as suas fortalezas! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19096, 'nvi', 'Jeremias', 'jr', 6, 6, 'Assim diz o Senhor dos Exércitos: "Derrubem as árvores e construam rampas de cerco contra Jerusalém. Ó cidade da falsidade! Ela está cheia de opressão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19097, 'nvi', 'Jeremias', 'jr', 6, 7, 'Assim como um poço produz água, também ela produz sua maldade. Violência! Destruição! É o que se ouve dentro dela; doenças e feridas estão sempre diante de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19098, 'nvi', 'Jeremias', 'jr', 6, 8, 'Ouça a minha advertência, ó Jerusalém! Do contrário eu me afastarei inteiramente de você e farei de você uma desolação, uma terra desabitada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19099, 'nvi', 'Jeremias', 'jr', 6, 9, 'Assim diz o Senhor dos Exércitos: "Rebusque-se o remanescente de Israel tão completamente, como se faz com uma videira, como faz quem colhe uvas: repassa os ramos cacho por cacho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19100, 'nvi', 'Jeremias', 'jr', 6, 10, 'A quem posso eu falar ou advertir? Quem me escutará? Os ouvidos deles são obstinados, e eles não podem ouvir. A palavra do Senhor é para eles desprezível, não encontram nela motivo de prazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19101, 'nvi', 'Jeremias', 'jr', 6, 11, 'Mas a ira do Senhor dentro de mim transborda, já não posso retê-la. "Derrama-a sobre as crianças na rua e sobre os jovens reunidos em grupos; pois eles também serão pegos juntos com os maridos e as mulheres, os velhos e os de idade bem avançada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19102, 'nvi', 'Jeremias', 'jr', 6, 12, 'As casas deles serão entregues a outros, juntamente com os seus campos e as suas mulheres, quando eu estender a minha mão contra os que vivem nesta terra", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19103, 'nvi', 'Jeremias', 'jr', 6, 13, '"Desde o menor até o maior, todos são gananciosos; profetas e sacerdotes igualmente, todos praticam o engano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19104, 'nvi', 'Jeremias', 'jr', 6, 14, 'Eles tratam da ferida do meu povo como se não fosse grave. ‘Paz, paz’, dizem, quando não há paz alguma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19105, 'nvi', 'Jeremias', 'jr', 6, 15, 'Ficarão eles envergonhados da sua conduta detestável? Não, eles não sentem vergonha alguma, nem mesmo sabem corar. Portanto, cairão entre os que caem; serão humilhados quando eu os castigar", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19106, 'nvi', 'Jeremias', 'jr', 6, 16, 'Assim diz o Senhor: "Ponham-se nas encruzilhadas e olhem; perguntem pelos caminhos antigos, perguntem pelo bom caminho. Sigam-no e acharão descanso". Mas vocês disseram: ‘Não seguiremos! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19107, 'nvi', 'Jeremias', 'jr', 6, 17, 'Coloquei sentinelas entre vocês e disse: Prestem atenção ao som da trombeta! Mas vocês disseram: ‘Não daremos atenção’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19108, 'nvi', 'Jeremias', 'jr', 6, 18, 'Vejam, ó nações; observe, ó assembléia, o que acontecerá a eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19109, 'nvi', 'Jeremias', 'jr', 6, 19, 'Ouça, ó terra: Vou trazer desgraça sobre este povo, o fruto das suas maquinações, porque não deram atenção às minhas palavras e rejeitaram a minha lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19110, 'nvi', 'Jeremias', 'jr', 6, 20, 'De que me serve o incenso trazido de Sabá, ou o cálamo aromático de uma terra distante? Os seus holocaustos não são aceitáveis nem me agradam as suas ofertas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19111, 'nvi', 'Jeremias', 'jr', 6, 21, 'Assim diz o Senhor: "Estou colocando obstáculos diante deste povo. Pais e filhos tropeçarão neles; vizinhos e amigos perecerão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19112, 'nvi', 'Jeremias', 'jr', 6, 22, 'Assim diz o Senhor: "Veja! Um exército vem do Norte; uma grande nação está sendo mobilizada desde os confins da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19113, 'nvi', 'Jeremias', 'jr', 6, 23, 'Eles empunham o arco e a lança; são cruéis e sem misericórdia, e o barulho que fazem é como o bramido do mar. Vêm montando os seus cavalos em formação de batalha, para atacá-la, ó cidade de Sião. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19114, 'nvi', 'Jeremias', 'jr', 6, 24, 'Ouvimos os relatos sobre eles, e as nossas mãos amoleceram. A angústia tomou conta de nós, dores como as da mulher que está dando à luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19115, 'nvi', 'Jeremias', 'jr', 6, 25, 'Não saiam aos campos nem andem pelas estradas, pois o inimigo traz a espada e há terror por todos os lados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19116, 'nvi', 'Jeremias', 'jr', 6, 26, 'Ó minha filha, meu povo, ponha vestes de lamento e revolva-se em cinza. Lamente-se com choro amargurado, como quem chora por um filho único, pois subitamente o destruidor virá sobre nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19117, 'nvi', 'Jeremias', 'jr', 6, 27, '"Eu o designei como examinador de metais, provador do meu povo para que você examine e ponha à prova a conduta deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19118, 'nvi', 'Jeremias', 'jr', 6, 28, 'Todos eles são rebeldes obstinados, e propagadores de calúnias. Estão endurecidos como o bronze e o ferro. Todos eles são corruptos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19119, 'nvi', 'Jeremias', 'jr', 6, 29, 'O fole sopra com força para separar o chumbo com o fogo, mas o refino prossegue em vão; os ímpios não são expurgados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19120, 'nvi', 'Jeremias', 'jr', 6, 30, 'São chamados prata rejeitada, porque o Senhor os rejeitou. "');
