INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19121, 'nvi', 'Jeremias', 'jr', 7, 1, 'Esta é a palavra que veio a Jeremias da parte do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19122, 'nvi', 'Jeremias', 'jr', 7, 2, '"Fique junto à porta o templo do Senhor e proclame esta mensagem: " ‘Ouçam a palavra do Senhor, todos vocês de Judá que atravessam estas portas para adorar o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19123, 'nvi', 'Jeremias', 'jr', 7, 3, 'Assim diz o Senhor dos Exércitos, o Deus de Israel: Corrijam a sua conduta e as suas ações, eu os farei habitar neste lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19124, 'nvi', 'Jeremias', 'jr', 7, 4, 'Não confiem em palavras enganosas: "Este é o templo do Senhor, o templo do Senhor, o templo do Senhor! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19125, 'nvi', 'Jeremias', 'jr', 7, 5, 'Mas se vocês realmente corrigirem a sua conduta e as suas ações, e se, de fato, tratarem uns aos outros com justiça,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19126, 'nvi', 'Jeremias', 'jr', 7, 6, 'se não oprimirem o estrangeiro, o órfão e a viúva e não derramarem sangue inocente neste lugar, e se vocês não seguirem outros deuses para a sua própria ruína,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19127, 'nvi', 'Jeremias', 'jr', 7, 7, 'então eu os farei habitar neste lugar, na terra que dei aos seus antepassados desde a antigüidade e para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19128, 'nvi', 'Jeremias', 'jr', 7, 8, 'Mas vejam! Vocês confiam em palavras enganosas e inúteis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19129, 'nvi', 'Jeremias', 'jr', 7, 9, '" ‘Vocês pensam que podem roubar e matar, cometer adultério e jurar falsamente, queimar incenso a Baal e seguir outros deuses que vocês não conheceram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19130, 'nvi', 'Jeremias', 'jr', 7, 10, 'e depois vir e permanecer perante mim neste templo, que leva o meu nome, e dizer: Estamos seguros!, seguros para continuar com todas essas práticas repugnantes?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19131, 'nvi', 'Jeremias', 'jr', 7, 11, 'Esse templo, que leva o meu nome, tornou-se para vocês um covil de ladrões? Cuidado! Eu mesmo estou vendo isso’ ", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19132, 'nvi', 'Jeremias', 'jr', 7, 12, '" ‘Portanto, vão agora a Siló, o meu lugar de adoração, onde primeiro fiz uma habitação em honra do meu nome, e vejam o que eu lhe fiz por causa da impiedade de Israel, meu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19133, 'nvi', 'Jeremias', 'jr', 7, 13, 'Mas agora, visto que vocês fizeram todas essas coisas, diz o Senhor, apesar de eu lhes ter falado repetidas vezes, e vocês não me terem dado atenção, e de eu lhes ter chamado, e vocês não me terem respondido,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19134, 'nvi', 'Jeremias', 'jr', 7, 14, 'eu farei a este templo que leva o meu nome, no qual vocês confiam, o lugar de adoração que dei a vocês e aos seus antepassados, o mesmo que fiz a Siló.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19135, 'nvi', 'Jeremias', 'jr', 7, 15, 'Expulsarei vocês da minha presença, como fiz com todos os seus compatriotas, o povo de Efraim’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19136, 'nvi', 'Jeremias', 'jr', 7, 16, '"Mas a você, Jeremias, não ore por este povo nem faça súplicas ou pedidos em favor dele, nem interceda por ele junto a mim, pois eu não o ouvirei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19137, 'nvi', 'Jeremias', 'jr', 7, 17, 'Não vê o que estão fazendo nas cidades de Judá e nas ruas de Jerusalém?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19138, 'nvi', 'Jeremias', 'jr', 7, 18, 'Os filhos ajuntam a lenha, os pais acendem o fogo, e as mulheres preparam a massa e fazem bolos para a Rainha dos Céus. Além disso, derramam ofertas a outros deuses para provocarem a minha ira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19139, 'nvi', 'Jeremias', 'jr', 7, 19, 'Mas será que é a mim que eles estão provocando?, pergunta o Senhor. Não é a si mesmos, para a sua própria vergonha?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19140, 'nvi', 'Jeremias', 'jr', 7, 20, '" ‘Portanto, assim diz o Soberano Senhor: A minha ardente ira será derramada sobre este lugar, sobre os homens, os animais, e as árvores do campo, como também sobre o produto do solo; ela arderá como fogo, e não poderá ser extinguida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19141, 'nvi', 'Jeremias', 'jr', 7, 21, '" ‘Assim diz o Senhor dos Exércitos, o Deus de Israel: Juntem os seus holocaustos aos outros sacrifícios e comam a carne vocês mesmos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19142, 'nvi', 'Jeremias', 'jr', 7, 22, 'Quando tirei do Egito os seus antepassados, nada lhes falei nem lhes ordenei quanto a holocaustos e sacrifícios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19143, 'nvi', 'Jeremias', 'jr', 7, 23, 'Dei-lhes, entretanto, esta ordem: Obedeçam-me, e eu serei o seu Deus e vocês serão o meu povo. Vocês andarão em todo o caminho que eu lhes ordenar, para que tudo lhes vá bem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19144, 'nvi', 'Jeremias', 'jr', 7, 24, 'Mas eles não me ouviram nem me deram atenção. Antes, seguiram o raciocínio rebelde dos seus corações maus. Andaram para trás e não para a frente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19145, 'nvi', 'Jeremias', 'jr', 7, 25, 'Desde a época em que os seus antepassados saíram do Egito até o dia de hoje, eu lhes enviei os meus servos, os profetas, dia após dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19146, 'nvi', 'Jeremias', 'jr', 7, 26, 'Mas eles não me ouviram nem me deram atenção. Antes tornaram-se obstinados e foram piores do que os antepassados deles’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19147, 'nvi', 'Jeremias', 'jr', 7, 27, '"Quando você lhes disser tudo isso, eles não o escutarão; quando você os chamar, não responderão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19148, 'nvi', 'Jeremias', 'jr', 7, 28, 'Portanto, diga a eles: ‘Esta é uma nação que não obedeceu ao Senhor, ao seu Deus, nem aceitou a correção. A verdade foi destruída e desapareceu dos seus lábios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19149, 'nvi', 'Jeremias', 'jr', 7, 29, 'Cortem os seus cabelos consagrados e joguem-os fora. Lamentem-se sobre os montes estéreis, pois o Senhor rejeitou e abandonou esta geração que provocou a sua ira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19150, 'nvi', 'Jeremias', 'jr', 7, 30, '" ‘Os de Judá fizeram o que eu reprovo, declara o Senhor. Profanaram o templo que leva o meu nome, colocando nele as imagens dos seus ídolos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19151, 'nvi', 'Jeremias', 'jr', 7, 31, 'Construíram o alto de Tofete no vale de Ben-Hinom, para queimarem em sacrifício os seus filhos e as suas filhas, coisa que nunca ordenei e que jamais me veio à mente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19152, 'nvi', 'Jeremias', 'jr', 7, 32, 'Por isso, certamente vêm os dias, declara o Senhor, em que não mais chamarão este lugar Tofete ou vale de Ben-Hinom, mas vale da Matança, pois ali enterrarão cadáveres até que não haja mais lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19153, 'nvi', 'Jeremias', 'jr', 7, 33, 'Então os cadáveres deste povo servirão de comida para as aves e para os animais, e não haverá quem os afugente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19154, 'nvi', 'Jeremias', 'jr', 7, 34, 'Darei fim às vozes de júbilo e de alegria, às vozes do noivo e da noiva nas cidades de Judá e nas ruas de Jerusalém, pois esta terra se tornará um deserto.');
