INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19177, 'nvi', 'Jeremias', 'jr', 9, 1, 'Ah, se a minha cabeça fosse uma fonte de água e os meus olhos um manancial de lágrimas! Eu choraria noite e dia pelos mortos do meu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19178, 'nvi', 'Jeremias', 'jr', 9, 2, 'Ah, se houvesse um alojamento para mim no deserto, para que eu pudesse deixar o meu povo e afastar-me dele. São todos adúlteros, um bando de traidores!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19179, 'nvi', 'Jeremias', 'jr', 9, 3, '"A língua deles é como um arco pronto para atirar. É a falsidade, não a verdade, que prevalece nesta terra. Eles vão de um crime a outro; eles não me reconhecem", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19180, 'nvi', 'Jeremias', 'jr', 9, 4, '"Cuidado com os seus amigos, não confie em seus parentes. Porque cada parente é um enganador, e cada amigo um caluniador.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19181, 'nvi', 'Jeremias', 'jr', 9, 5, 'Amigo engana amigo, ninguém fala a verdade. Eles treinaram a língua para mentir; e, sendo perversos, eles se cansam demais para se converterem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19182, 'nvi', 'Jeremias', 'jr', 9, 6, 'De opressão em opressão, de engano em engano, eles se recusam a reconhecer-me", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19183, 'nvi', 'Jeremias', 'jr', 9, 7, 'Portanto, assim diz o Senhor dos Exércitos: "Vejam, sou eu que os refinarei e os provarei. Que mais posso eu fazer pelo meu povo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19184, 'nvi', 'Jeremias', 'jr', 9, 8, 'A língua deles é uma flecha mortal; eles falam traiçoeiramente. Cada um mostra-se cordial com o seu próximo, mas no íntimo lhe prepara uma armadilha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19185, 'nvi', 'Jeremias', 'jr', 9, 9, 'Deixarei eu de castigá-los? ", pergunta o Senhor. "Não me vingarei de uma nação como essa? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19186, 'nvi', 'Jeremias', 'jr', 9, 10, 'Chorarei, prantearei e me lamentarei pelos montes por causa das pastagens da estepe; pois estão abandonadas e ninguém mais as percorre. Não se ouve o mugir do gado; tanto as aves como os animais fugiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19187, 'nvi', 'Jeremias', 'jr', 9, 11, '"Farei de Jerusalém um amontoado de ruínas, uma habitação de chacais. Devastarei as cidades de Judá até não restar nenhum morador. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19188, 'nvi', 'Jeremias', 'jr', 9, 12, 'Quem é bastante sábio para compreender isso? Quem foi instruído pelo Senhor, que possa explicá-lo? Por que a terra está arruinada e devastada como um deserto pelo qual ninguém passa?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19189, 'nvi', 'Jeremias', 'jr', 9, 13, 'O Senhor disse: "Foi porque abandonaram a minha lei, que estabeleci diante deles; não me obedeceram nem seguiram a minha lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19190, 'nvi', 'Jeremias', 'jr', 9, 14, 'Em vez disso, seguiram a dureza de seus próprios corações, indo atrás dos baalins, como os seus antepassados lhes ensinaram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19191, 'nvi', 'Jeremias', 'jr', 9, 15, 'Por isso, assim diz o Senhor dos Exércitos, o Deus de Israel: "Vejam! Farei este povo comer comida amarga e beber água envenenada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19192, 'nvi', 'Jeremias', 'jr', 9, 16, 'Eu os espalharei entre nações que nem eles nem os seus antepassados conheceram; e enviarei contra eles a espada até exterminá-los".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19193, 'nvi', 'Jeremias', 'jr', 9, 17, 'Assim diz o Senhor dos Exércitos: "Considerem: Chamem as pranteadoras profissionais; mandem chamar as mais hábeis entre elas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19194, 'nvi', 'Jeremias', 'jr', 9, 18, 'Venham elas depressa e lamentem por nós, até que os nossos olhos transbordem de lágrimas e águas corram de nossas pálpebras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19195, 'nvi', 'Jeremias', 'jr', 9, 19, 'O som de lamento se ouve desde Sião: ‘Como estamos arruinados! Como é grande a nossa humilhação! Deixamos a nossa terra porque as nossas casas estão em ruínas’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19196, 'nvi', 'Jeremias', 'jr', 9, 20, 'Ó mulheres, ouçam agora a palavra do Senhor; abram os ouvidos às palavras de sua boca. Ensinem suas filhas a lamentar-se; ensinem umas as outras a prantear.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19197, 'nvi', 'Jeremias', 'jr', 9, 21, 'A morte subiu e penetrou pelas nossas janelas e invadiu as nossas fortalezas, eliminando das ruas as crianças e das praças, os rapazes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19198, 'nvi', 'Jeremias', 'jr', 9, 22, '"Diga: ‘Assim declara o Senhor: " ‘Cadáveres ficarão estirados como esterco em campo aberto, como o trigo deixado para trás pelo ceifeiro, sem que ninguém o ajunte’. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19199, 'nvi', 'Jeremias', 'jr', 9, 23, 'Assim diz o Senhor: "Não se glorie o sábio em sua sabedoria nem o forte em sua força nem o rico em sua riqueza,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19200, 'nvi', 'Jeremias', 'jr', 9, 24, 'mas quem se gloriar, glorie-se nisto: em compreender-me e conhecer-me, pois eu sou o Senhor, e ajo com lealdade, com justiça e com retidão sobre a terra, pois é dessas coisas que me agrado", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19201, 'nvi', 'Jeremias', 'jr', 9, 25, '"Vêm chegando os dias", declara o Senhor, "em que castigarei todos os que são circuncidados apenas no corpo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (19202, 'nvi', 'Jeremias', 'jr', 9, 26, 'como também o Egito, Judá, Edom, Amom, Moabe e todos os que rapam a cabeça e vivem no deserto; porque todas essas nações são incircuncisas, e a comunidade de Israel tem o coração obstinado. "');
