INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6065, 'nvi', 'Josué', 'js', 10, 1, 'Sucedeu que Adoni-Zedeque, rei de Jerusalém, soube que Josué tinha conquistado Ai e a tinha destruído totalmente, fazendo com Ai e seu rei o que fizera com Jericó e seu rei, e que o povo de Gibeom tinha feito a paz com Israel e estava vivendo no meio deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6066, 'nvi', 'Josué', 'js', 10, 2, 'Ele e o seu povo ficaram com muito medo, pois Gibeom era tão importante como uma cidade governada por um rei; era maior do que Ai, e todos os seus homens eram bons guerreiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6067, 'nvi', 'Josué', 'js', 10, 3, 'Por isso Adoni-Zedeque, rei de Jerusalém, fez o seguinte apelo a Hoão, rei de Hebrom, a Piram, rei de Jarmute, a Jafia, rei de Láquis, e a Debir, rei de Eglom:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6068, 'nvi', 'Josué', 'js', 10, 4, '"Venham para cá e ajudem-me a atacar Gibeom, pois ela fez a paz com Josué e com os israelitas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6069, 'nvi', 'Josué', 'js', 10, 5, 'Então os cinco reis dos amorreus, os reis de Jerusalém, de Hebrom, de Jarmute, de Láquis e de Eglom reuniram-se e vieram com todos os seus exércitos. Cercaram Gibeom e a atacaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6070, 'nvi', 'Josué', 'js', 10, 6, 'Os gibeonitas enviaram esta mensagem a Josué, ao acampamento de Gilgal: "Não abandone os seus servos. Venha depressa! Salve-nos! Ajude-nos, pois todos os reis amorreus que vivem nas montanhas se uniram contra nós! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6071, 'nvi', 'Josué', 'js', 10, 7, 'Josué partiu de Gilgal com todo o seu exército, inclusive com os seus melhores guerreiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6072, 'nvi', 'Josué', 'js', 10, 8, 'E disse o Senhor a Josué: "Não tenha medo desses reis; eu os entreguei nas suas mãos. Nenhum deles conseguirá resistir a você".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6073, 'nvi', 'Josué', 'js', 10, 9, 'Depois de uma noite inteira de marcha desde Gilgal, Josué os apanhou de surpresa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6074, 'nvi', 'Josué', 'js', 10, 10, 'O Senhor os lançou em confusão diante de Israel, que lhes impôs grande derrota em Gibeom. Os israelitas os perseguiram na subida para Bete-Horom e os mataram por todo o caminho, até Azeca e Maquedá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6075, 'nvi', 'Josué', 'js', 10, 11, 'Enquanto fugiam de Israel na descida de Bete-Horom para Azeca, do céu o Senhor lançou sobre eles grandes pedras de granizo, que mataram mais gente do que as espadas dos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6076, 'nvi', 'Josué', 'js', 10, 12, 'No dia em que o Senhor entregou os amorreus aos israelitas, Josué exclamou ao Senhor, na presença de Israel: "Sol, pare sobre Gibeom! E você, ó lua, sobre o vale de Aijalom! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6077, 'nvi', 'Josué', 'js', 10, 13, 'O sol parou, e a lua se deteve, até a nação vingar-se dos seus inimigos, como está escrito no Livro de Jasar. O sol parou no meio do céu e por quase um dia inteiro não se pôs.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6078, 'nvi', 'Josué', 'js', 10, 14, 'Nunca antes nem depois houve um dia como aquele, quando o Senhor atendeu a um homem. Sem dúvida o Senhor lutava por Israel!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6079, 'nvi', 'Josué', 'js', 10, 15, 'Então Josué voltou com todo o Israel ao acampamento de Gilgal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6080, 'nvi', 'Josué', 'js', 10, 16, 'Os cinco reis fugiram e se esconderam na caverna de Maquedá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6081, 'nvi', 'Josué', 'js', 10, 17, 'Avisaram a Josué que eles tinham sido achados numa caverna em Maquedá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6082, 'nvi', 'Josué', 'js', 10, 18, 'Disse ele: "Rolem grandes pedras até a entrada da caverna, e deixem ali alguns homens de guarda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6083, 'nvi', 'Josué', 'js', 10, 19, 'Mas não se detenham! Persigam os inimigos. Ataquem-nos pela retaguarda e não os deixem chegar às suas cidades, pois o Senhor, o seu Deus, os entregou em suas mãos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6084, 'nvi', 'Josué', 'js', 10, 20, 'Assim Josué e os israelitas os derrotaram por completo, quase exterminando-os. Mas alguns conseguiram escapar e refugiaram-se em suas cidades fortificadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6085, 'nvi', 'Josué', 'js', 10, 21, 'O exército inteiro voltou então em segurança a Josué, ao acampamento de Maquedá, e depois disso, ninguém mais ousou abrir a boca para provocar os israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6086, 'nvi', 'Josué', 'js', 10, 22, 'Então disse Josué: "Abram a entrada da caverna e tragam-me aqueles cinco reis".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6087, 'nvi', 'Josué', 'js', 10, 23, 'Os cinco reis foram tirados da caverna. Eram os reis de Jerusalém, de Hebrom, de Jarmute, de Láquis e de Eglom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6088, 'nvi', 'Josué', 'js', 10, 24, 'Quando os levaram a Josué, ele convocou todos os homens de Israel e disse aos comandantes do exército que o tinham acompanhado: "Venham aqui e ponham o pé no pescoço destes reis". E eles obedeceram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6089, 'nvi', 'Josué', 'js', 10, 25, 'Disse-lhes Josué: "Não tenham medo! Não se desanimem! Sejam fortes e corajosos! É isso que o Senhor fará com todos os inimigos que vocês tiverem que combater".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6090, 'nvi', 'Josué', 'js', 10, 26, 'Depois Josué matou os reis e mandou pendurá-los em cinco árvores, onde ficaram até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6091, 'nvi', 'Josué', 'js', 10, 27, 'Ao pôr-do-sol, sob as ordens de Josué, eles foram tirados das árvores e jogados na caverna onde haviam se escondido. Na entrada da caverna colocaram grandes pedras, que lá estão até hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6092, 'nvi', 'Josué', 'js', 10, 28, 'Naquele dia Josué tomou Maquedá. Atacou a cidade e matou o seu rei à espada e exterminou todos os que nela viviam, sem deixar sobreviventes. E fez com o rei de Maquedá o que tinha feito com o rei de Jericó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6093, 'nvi', 'Josué', 'js', 10, 29, 'Então Josué, e todo o Israel com ele, avançou de Maquedá para Libna e a atacou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6094, 'nvi', 'Josué', 'js', 10, 30, 'O Senhor entregou também aquela cidade e seu rei nas mãos dos israelitas. Josué atacou a cidade e matou à espada todos os que nela viviam, sem deixar nenhum sobrevivente ali. E fez com o seu rei o que fizera com o rei de Jericó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6095, 'nvi', 'Josué', 'js', 10, 31, 'Depois Josué, e todo o Israel com ele, avançou de Libna para Láquis, cercou-a e a atacou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6096, 'nvi', 'Josué', 'js', 10, 32, 'O Senhor entregou Láquis nas mãos dos israelitas, e Josué tomou-a no dia seguinte. Atacou a cidade e matou à espada todos os que nela viviam, como tinha feito com Libna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6097, 'nvi', 'Josué', 'js', 10, 33, 'Nesse meio tempo Horão, rei de Gezer, fora socorrer Láquis, mas Josué o derrotou, a ele e ao seu exército, sem deixar sobrevivente algum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6098, 'nvi', 'Josué', 'js', 10, 34, 'Josué, e todo o Israel com ele, avançou de Láquis para Eglom, cercou-a e a atacou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6099, 'nvi', 'Josué', 'js', 10, 35, 'Eles a conquistaram naquele mesmo dia, feriram-na à espada e exterminaram os que nela viviam, como tinham feito com Láquis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6100, 'nvi', 'Josué', 'js', 10, 36, 'Então Josué, e todo o Israel com ele, foi de Eglom para Hebrom e a atacou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6101, 'nvi', 'Josué', 'js', 10, 37, 'Tomaram a cidade e a feriram à espada, como também o seu rei, os seus povoados e todos os que nela viviam, sem deixar sobrevivente algum. Destruíram totalmente a cidade e todos os que nela viviam, como tinham feito com Eglom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6102, 'nvi', 'Josué', 'js', 10, 38, 'Depois Josué, e todo o Israel com ele, voltou e atacou Debir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6103, 'nvi', 'Josué', 'js', 10, 39, 'Tomaram a cidade, seu rei e seus povoados, e os mataram à espada. Exterminaram os que nela viviam, sem deixar sobrevivente algum. Fizeram com Debir e seu rei o que tinham feito com Libna e seu rei e com Hebrom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6104, 'nvi', 'Josué', 'js', 10, 40, 'Assim Josué conquistou a região toda, incluindo a serra central, o Neguebe, as encostas e as vertentes, e derrotou todos os seus reis, sem deixar sobrevivente algum. Exterminou tudo o que respirava, conforme o Senhor, o Deus de Israel, tinha ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6105, 'nvi', 'Josué', 'js', 10, 41, 'Josué os derrotou desde Cades-Barnéia até Gaza, e toda a região de Gósen, e de lá até Gibeom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6106, 'nvi', 'Josué', 'js', 10, 42, 'Também subjugou todos esses reis e conquistou suas terras numa única campanha, pois o Senhor, o Deus de Israel, lutou por Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6107, 'nvi', 'Josué', 'js', 10, 43, 'Então Josué retornou com todo o Israel ao acampamento de Gilgal.');
