INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6108, 'nvi', 'Josué', 'js', 11, 1, 'Quando Jabim, rei de Hazor, soube disso, enviou mensagem a Jobabe, rei de Madom, aos reis de Sinrom e Acsafe,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6109, 'nvi', 'Josué', 'js', 11, 2, 'e aos reis do norte que viviam nas montanhas, na Arabá ao sul de Quinerete, na Sefelá e em Nafote-Dor, a oeste;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6110, 'nvi', 'Josué', 'js', 11, 3, 'aos cananeus a leste e a oeste; aos amorreus, aos hititas, aos ferezeus e aos jebuseus das montanhas; e aos heveus do sopé do Hermom, na região de Mispá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6111, 'nvi', 'Josué', 'js', 11, 4, 'Saíram com todas as suas tropas, um exército imenso, tão numeroso como a areia da praia, além de um grande número de cavalos e carros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6112, 'nvi', 'Josué', 'js', 11, 5, 'Todos esses reis se uniram e acamparam junto às águas de Merom, para lutar contra Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6113, 'nvi', 'Josué', 'js', 11, 6, 'E o Senhor disse a Josué: "Não tenha medo deles, porque amanhã a esta hora entregarei todos mortos a Israel. A você cabe cortar os tendões dos cavalos deles e queimar os seus carros".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6114, 'nvi', 'Josué', 'js', 11, 7, 'Josué e todo o seu exército os surpreenderam junto às águas de Merom e os atacaram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6115, 'nvi', 'Josué', 'js', 11, 8, 'e o Senhor os entregou nas mãos de Israel, que os derrotou e os perseguiu até Sidom, a grande, até Misrefote-Maim e até o vale de Mispá, a leste. Eles os mataram sem deixar sobrevivente algum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6116, 'nvi', 'Josué', 'js', 11, 9, 'Josué os tratou como o Senhor lhe tinha ordenado. Cortou os tendões dos seus cavalos e queimou os seus carros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6117, 'nvi', 'Josué', 'js', 11, 10, 'Na mesma ocasião Josué voltou, conquistou Hazor e matou o seu rei à espada. ( Hazor tinha sido a capital de todos esses reinos. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6118, 'nvi', 'Josué', 'js', 11, 11, 'Matou à espada todos os que nela estavam. Exterminou-os totalmente, sem poupar nada que respirasse, e incendiou Hazor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6119, 'nvi', 'Josué', 'js', 11, 12, 'Josué conquistou todas essas cidades e matou à espada os reis que as governavam. Destruiu-as totalmente, como Moisés, servo do Senhor, tinha ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6120, 'nvi', 'Josué', 'js', 11, 13, 'Contudo, Israel não incendiou nenhuma das cidades construídas nas colinas, com exceção de Hazor, que Josué incendiou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6121, 'nvi', 'Josué', 'js', 11, 14, 'Os israelitas tomaram posse de todos os despojos e dos animais dessas cidades, mas mataram todo o povo à espada, até exterminá-lo completamente, sem poupar ninguém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6122, 'nvi', 'Josué', 'js', 11, 15, 'Tudo o que o Senhor tinha ordenado a seu servo Moisés, Moisés ordenou a Josué, e Josué obedeceu, sem deixar de cumprir nada de tudo o que o Senhor tinha ordenado a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6123, 'nvi', 'Josué', 'js', 11, 16, 'Assim Josué conquistou toda aquela terra: a serra central, todo o Neguebe, toda a região de Gósen, a Sefelá, a Arabá e os montes de Israel e suas planícies,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6124, 'nvi', 'Josué', 'js', 11, 17, 'desde o monte Halaque, que se ergue na direção de Seir, até Baal-Gade, no vale do Líbano, no sopé do monte Hermom. Ele capturou todos os seus reis e os matou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6125, 'nvi', 'Josué', 'js', 11, 18, 'Josué guerreou contra todos esses reis por muito tempo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6126, 'nvi', 'Josué', 'js', 11, 19, 'Com exceção dos heveus que viviam em Gibeom, nenhuma cidade fez a paz com os israelitas, que a todas conquistou em combate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6127, 'nvi', 'Josué', 'js', 11, 20, 'Pois foi o próprio Senhor que endureceu os seus corações para guerrearem contra Israel, para que ele os destruísse totalmente, exterminando-os sem misericórdia, como o Senhor tinha ordenado a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6128, 'nvi', 'Josué', 'js', 11, 21, 'Naquela ocasião Josué exterminou os enaquins dos montes de Hebrom, de Debir e de Anabe, de todos os montes de Judá, e de Israel. Josué destruiu-os totalmente, e também as suas cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6129, 'nvi', 'Josué', 'js', 11, 22, 'Nenhum enaquim foi deixado vivo no território israelita; somente em Gaza, em Gate e em Asdode é que alguns sobreviveram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6130, 'nvi', 'Josué', 'js', 11, 23, 'Foi assim que Josué conquistou toda a terra, conforme o Senhor tinha dito a Moisés, e deu-a por herança a Israel, repartindo-a entre as suas tribos. E a terra teve descanso da guerra.');
