INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6155, 'nvi', 'Josué', 'js', 13, 1, 'Sendo Josué já velho, de idade bastante avançada, o Senhor lhe disse: "Você já está velho, e ainda há muita terra para ser conquistada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6156, 'nvi', 'Josué', 'js', 13, 2, '"Esta é a terra que resta: todas as regiões dos filisteus e dos gesuritas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6157, 'nvi', 'Josué', 'js', 13, 3, 'desde o rio Sior, próximo ao Egito, até o território de Ecrom, ao norte, todo esse território considerado cananeu. Abrange a região dos aveus, isto é, dos cinco chefes filisteus, governantes de Gaza, de Asdode, de Ascalom, de Gate e de Ecrom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6158, 'nvi', 'Josué', 'js', 13, 4, 'Resta ainda, desde o sul, toda a terra dos cananeus, desde Ara dos sidônios até Afeque, a região dos amorreus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6159, 'nvi', 'Josué', 'js', 13, 5, 'a dos gibleus, todo o Líbano, para o leste, desde Baal-Gade, ao pé do monte Hermom, até Lebo-Hamate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6160, 'nvi', 'Josué', 'js', 13, 6, '"Todos os habitantes das montanhas, desde o Líbano até Misrefote-Maim, isto é, todos os sidônios; eu mesmo os expulsarei da presença dos israelitas. Você, porém, distribuirá esta terra a Israel por herança, como lhe ordenei,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6161, 'nvi', 'Josué', 'js', 13, 7, 'repartindo-a agora entre as nove tribos e a metade da tribo de Manassés".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6162, 'nvi', 'Josué', 'js', 13, 8, 'Com a outra metade da tribo de Manassés, as tribos de Rúben e de Gade já haviam recebido a herança a leste do Jordão, conforme Moisés, servo do Senhor, lhes tinha designado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6163, 'nvi', 'Josué', 'js', 13, 9, 'Esse território se estendia de Aroer, na margem do ribeiro do Arnom, e da cidade situada no meio do vale desse ribeiro, e incluía todo o planalto de Medeba, até Dibom,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6164, 'nvi', 'Josué', 'js', 13, 10, 'e todas as cidades de Seom, rei dos amorreus, que governava em Hesbom, e prosseguia até a fronteira dos amonitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6165, 'nvi', 'Josué', 'js', 13, 11, 'Também incluía Gileade, o território dos gesuritas e maacatitas, toda a região do monte Hermom e toda a Basã, até Salcá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6166, 'nvi', 'Josué', 'js', 13, 12, 'isto é, todo o reino de Ogue, em Basã, que tinha reinado em Asterote e Edrei, um dos últimos refains sobreviventes. Moisés os tinha derrotado e tomado as suas terras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6167, 'nvi', 'Josué', 'js', 13, 13, 'Mas os israelitas não expulsaram os gesuritas e maacatitas, de modo que até hoje continuam a viver no meio deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6168, 'nvi', 'Josué', 'js', 13, 14, 'Mas à tribo de Levi não deu herança alguma, visto que as ofertas preparadas no fogo ao Senhor, ao Deus de Israel, são a herança deles, como já lhes dissera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6169, 'nvi', 'Josué', 'js', 13, 15, 'À tribo de Rúben, clã por clã, Moisés dera o seguinte território:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6170, 'nvi', 'Josué', 'js', 13, 16, 'Desde Aroer, na margem do ribeiro do Arnom, e desde a cidade situada no meio do vale desse ribeiro, e todo o planalto depois de Medeba,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6171, 'nvi', 'Josué', 'js', 13, 17, 'até Hesbom e todas as suas cidades no planalto, inclusive Dibom, Bamote-Baal, Bete-Baal-Meom,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6172, 'nvi', 'Josué', 'js', 13, 18, 'Jaza, Quedemote, Mefaate,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6173, 'nvi', 'Josué', 'js', 13, 19, 'Quiriataim, Sibma, Zerete-Saar, na encosta do vale,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6174, 'nvi', 'Josué', 'js', 13, 20, 'Bete-Peor, as encostas do Pisga, e Bete-Jesimote:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6175, 'nvi', 'Josué', 'js', 13, 21, 'todas as cidades do planalto e todo o domínio de Seom, rei dos amorreus, que governava em Hesbom. Moisés o tinha derrotado, bem como aos líderes midianitas Evi, Requém, Zur, Hur e Reba, aliados de Seom, que viviam naquela terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6176, 'nvi', 'Josué', 'js', 13, 22, 'Além dos que foram mortos na guerra, os israelitas mataram à espada Balaão, filho de Beor, que praticava adivinhação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6177, 'nvi', 'Josué', 'js', 13, 23, 'A fronteira da tribo de Rúben era a margem do Jordão. Essas cidades e os seus povoados foram a herança de Rúben, clã por clã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6178, 'nvi', 'Josué', 'js', 13, 24, 'À tribo de Gade, clã por clã, Moisés dera o seguinte território:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6179, 'nvi', 'Josué', 'js', 13, 25, 'O território de Jazar, todas as cidades de Gileade e metade do território amonita até Aroer, perto de Rabá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6180, 'nvi', 'Josué', 'js', 13, 26, 'Estendia-se desde Hesbom até Ramate-Mispa e Betonim, e desde Maanaim até o território de Debir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6181, 'nvi', 'Josué', 'js', 13, 27, 'No vale do Jordão incluía Bete-Arã, Bete-Ninra, Sucote e Zafom; o restante do domínio de Seom, rei de Hesbom. Abrangia a margem leste do Jordão até o mar de Quinerete.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6182, 'nvi', 'Josué', 'js', 13, 28, 'Essa região com suas cidades e povoados foram a herança de Gade, clã por clã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6183, 'nvi', 'Josué', 'js', 13, 29, 'À metade da tribo de Manassés, isto é, à metade dos descendentes de Manassés, clã por clã, Moisés dera o seguinte território:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6184, 'nvi', 'Josué', 'js', 13, 30, 'O seu território se estendia desde Maanaim e incluía toda a região de Basã, todo o domínio de Ogue, rei de Basã: todos os povoados de Jair em Basã, sessenta cidades;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6185, 'nvi', 'Josué', 'js', 13, 31, 'metade de Gileade, e Asterote e Edrei, cidades do reino de Ogue, em Basã. Esse foi o território destinado à metade dos descendentes de Maquir, filho de Manassés, clã por clã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6186, 'nvi', 'Josué', 'js', 13, 32, 'Essa foi a herança que Moisés lhes deu quando estava na planície de Moabe, do outro lado do Jordão, a leste de Jericó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6187, 'nvi', 'Josué', 'js', 13, 33, 'Mas à tribo de Levi, Moisés não deu herança alguma; o Senhor, o Deus de Israel, é a herança deles, como já lhes dissera.');
