INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6294, 'nvi', 'Josué', 'js', 18, 1, 'Toda a comunidade dos israelitas reuniu-se em Siló e ali armou a Tenda do Encontro. A terra foi dominada por eles;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6295, 'nvi', 'Josué', 'js', 18, 2, 'mas, sete tribos ainda não tinham recebido a sua herança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6296, 'nvi', 'Josué', 'js', 18, 3, 'Então Josué disse aos israelitas: "Até quando vocês vão negligenciar a posse da terra que o Senhor, o Deus dos seus antepassados, lhes deu?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6297, 'nvi', 'Josué', 'js', 18, 4, 'Escolham três homens de cada tribo, e eu os enviarei. Eles vão examinar a terra e mapeá-la, conforme a herança de cada tribo. Depois voltarão a mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6298, 'nvi', 'Josué', 'js', 18, 5, 'Dividam a terra em sete partes. Judá ficará em seu território ao sul, e a tribo de José em seu território ao norte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6299, 'nvi', 'Josué', 'js', 18, 6, 'Depois que fizerem um mapa das sete partes da terra, tragam-no para mim, e eu farei sorteio para vocês na presença do Senhor, do nosso Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6300, 'nvi', 'Josué', 'js', 18, 7, 'Mas os levitas nada receberão entre vocês, pois o sacerdócio do Senhor é a herança deles. Gade, Rúben e a metade da tribo de Manassés já receberam a sua herança no lado leste do Jordão, dada a eles por Moisés, servo do Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6301, 'nvi', 'Josué', 'js', 18, 8, 'Quando os homens estavam de partida para mapear a terra, Josué os instruiu: "Vão examinar a terra e façam uma descrição dela. Depois voltem, e eu farei um sorteio para vocês aqui em Siló, na presença do Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6302, 'nvi', 'Josué', 'js', 18, 9, 'Os homens partiram e percorreram a terra. Descreveram-na num rolo, cidade por cidade, em sete partes, e retornaram a Josué, ao acampamento de Siló.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6303, 'nvi', 'Josué', 'js', 18, 10, 'Josué fez então um sorteio para eles em Siló, na presença do Senhor, e ali distribuiu a terra aos israelitas, conforme a porção devida a cada tribo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6304, 'nvi', 'Josué', 'js', 18, 11, 'Saiu a sorte para a tribo de Benjamim, clã por clã. O território sorteado ficava entre as tribos de Judá e José:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6305, 'nvi', 'Josué', 'js', 18, 12, 'No lado norte a sua fronteira começava no Jordão, passava pela encosta norte de Jericó e prosseguia para o oeste, para a região montanhosa, terminando no deserto de Bete-Áven.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6306, 'nvi', 'Josué', 'js', 18, 13, 'Dali ia para a encosta sul de Luz, que é Betel, e descia para Atarote-Adar, na montanha que está ao sul de Bete-Horom Baixa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6307, 'nvi', 'Josué', 'js', 18, 14, 'Da montanha que fica defronte de Bete-Horom, no sul, a fronteira virava para o sul, ao longo do lado ocidental, e terminava em Quiriate-Baal, que é Quiriate-Jearim, cidade do povo de Judá. Esse era o lado ocidental.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6308, 'nvi', 'Josué', 'js', 18, 15, 'A fronteira sul começava no oeste, nos arredores de Quiriate-Jearim, e chegava à fonte de Neftoa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6309, 'nvi', 'Josué', 'js', 18, 16, 'A fronteira descia até o sopé da montanha que fica defronte do vale de Ben-Hinom, ao norte do vale de Refaim. Depois, prosseguia, descendo pelo vale de Hinom ao longo da encosta sul da cidade dos jebuseus e chegava até En-Rogel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6310, 'nvi', 'Josué', 'js', 18, 17, 'Fazia então uma curva para o norte, ia para En-Semes, continuava até Gelilote, que fica defronte da subida de Adumim, e descia até a Pedra de Boã, nome filho de Rúben.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6311, 'nvi', 'Josué', 'js', 18, 18, 'Prosseguia para a encosta norte de Bete-Arabá, e daí descia para a Arabá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6312, 'nvi', 'Josué', 'js', 18, 19, 'Depois ia para a encosta norte de Bete-Hogla e terminava na baía norte do mar Salgado, na foz do Jordão, no sul. Essa era a fronteira sul.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6313, 'nvi', 'Josué', 'js', 18, 20, 'O Jordão delimitava a fronteira oriental. Essas eram as fronteiras que demarcavam por todos os lados a herança dos clãs de Benjamim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6314, 'nvi', 'Josué', 'js', 18, 21, 'A tribo de Benjamim, clã por clã, recebeu as seguintes cidades: Jericó, Bete-Hogla, Emeque-Queziz,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6315, 'nvi', 'Josué', 'js', 18, 22, 'Bete-Arabá, Zemaraim, Betel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6316, 'nvi', 'Josué', 'js', 18, 23, 'Avim, Pará, Ofra,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6317, 'nvi', 'Josué', 'js', 18, 24, 'Quefar-Amonai, Ofni e Geba. Eram doze cidades com os seus povoados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6318, 'nvi', 'Josué', 'js', 18, 25, 'Gibeom, Ramá, Beerote,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6319, 'nvi', 'Josué', 'js', 18, 26, 'Mispá, Quefira, Mosa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6320, 'nvi', 'Josué', 'js', 18, 27, 'Requém, Irpeel, Tarala,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6321, 'nvi', 'Josué', 'js', 18, 28, 'Zela, Elefe, Jebus, que é Jerusalém, Gibeá e Quiriate. Eram catorze cidades com os seus povoados. Essa foi a herança dos clãs de Benjamim.');
