INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6382, 'nvi', 'Josué', 'js', 21, 1, 'Os chefes de família dos levitas se aproximaram do sacerdote Eleazar, de Josué, filho de Num, e dos chefes das outras famílias das tribos dos israelitas');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6383, 'nvi', 'Josué', 'js', 21, 2, 'em Siló, na terra de Canaã, e lhes disseram: "O Senhor ordenou por meio de Moisés que vocês nos dessem cidades onde possamos habitar, e pastagens para os nossos animais".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6384, 'nvi', 'Josué', 'js', 21, 3, 'Por isso, de acordo com a ordem do Senhor, os israelitas deram da sua própria herança as seguintes cidades com suas pastagens aos levitas:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6385, 'nvi', 'Josué', 'js', 21, 4, 'A sorte saiu primeiro para os coatitas, clã por clã. Os levitas, que eram descendentes do sacerdote Arão, receberam treze cidades das tribos de Judá, de Simeão e de Benjamim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6386, 'nvi', 'Josué', 'js', 21, 5, 'Os outros descendentes de Coate receberam dez cidades dos clãs das tribos de Efraim e de Dã, e da metade da tribo de Manassés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6387, 'nvi', 'Josué', 'js', 21, 6, 'Os descendentes de Gérson receberam treze cidades dos clãs das tribos de Issacar, de Aser e de Naftali, e da metade da tribo de Manassés estabelecida em Basã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6388, 'nvi', 'Josué', 'js', 21, 7, 'Os descendentes de Merari, clã por clã, receberam doze cidades das tribos de Rúben, de Gade e de Zebulom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6389, 'nvi', 'Josué', 'js', 21, 8, 'Dessa maneira os israelitas deram aos levitas essas cidades com suas pastagens, como o Senhor tinha ordenado por meio de Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6390, 'nvi', 'Josué', 'js', 21, 9, 'Das tribos de Judá e de Simeão, os israelitas deram as seguintes cidades, indicadas nominalmente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6391, 'nvi', 'Josué', 'js', 21, 10, 'Foram dadas aos descendentes de Arão que pertenciam aos clãs coatitas dos levitas, pois para eles saiu a primeira sorte:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6392, 'nvi', 'Josué', 'js', 21, 11, 'Quiriate-Arba, que é Hebrom, com as suas pastagens ao redor, nos montes de Judá. ( Arba era antepassado de Enaque. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6393, 'nvi', 'Josué', 'js', 21, 12, 'Mas os campos e os povoados em torno da cidade foram dados a Calebe, filho de Jefoné, como sua propriedade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6394, 'nvi', 'Josué', 'js', 21, 13, 'Assim, aos descendentes do sacerdote Arão deram Hebrom, cidade de refúgio para os acusados de homicídio, Libna,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6395, 'nvi', 'Josué', 'js', 21, 14, 'Jatir, Estemoa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6396, 'nvi', 'Josué', 'js', 21, 15, 'Holom, Debir,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6397, 'nvi', 'Josué', 'js', 21, 16, 'Aim, Jutá e Bete-Semes, cada qual com os seus arredores. Foram nove cidades dadas por essas duas tribos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6398, 'nvi', 'Josué', 'js', 21, 17, 'Da tribo de Benjamim deram-lhes Gibeom, Geba,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6399, 'nvi', 'Josué', 'js', 21, 18, 'Anatote e Almom, cada qual com os seus arredores. Eram quatro cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6400, 'nvi', 'Josué', 'js', 21, 19, 'Todas as cidades dadas aos sacerdotes, descendentes de Arão, foram treze; cada qual com os seus arredores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6401, 'nvi', 'Josué', 'js', 21, 20, 'Os outros clãs coatitas dos levitas receberam cidades da tribo de Efraim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6402, 'nvi', 'Josué', 'js', 21, 21, 'Nos montes de Efraim receberam Siquém, cidade de refúgio para os acusados de homicídio, Gezer,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6403, 'nvi', 'Josué', 'js', 21, 22, 'Quibzaim e Bete-Horom, cada qual com os seus arredores. Foram quatro cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6404, 'nvi', 'Josué', 'js', 21, 23, 'Também da tribo de Dã receberam Elteque, Gibetom,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6405, 'nvi', 'Josué', 'js', 21, 24, 'Aijalom e Gate-Rimom, cada qual com os seus arredores. Foram quatro cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6406, 'nvi', 'Josué', 'js', 21, 25, 'Da meia tribo de Manassés receberam Taanaque e Gate-Rimom, cada qual com os seus arredores. Eram duas cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6407, 'nvi', 'Josué', 'js', 21, 26, 'Todas essas dez cidades e seus arredores foram dadas aos outros clãs coatitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6408, 'nvi', 'Josué', 'js', 21, 27, 'Os clãs levitas gersonitas receberam da metade da tribo de Manassés: Golã, em Basã, cidade de refúgio para os acusados de homicídio, e Beesterá, cada qual com os seus arredores. Foram duas cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6409, 'nvi', 'Josué', 'js', 21, 28, 'Receberam da tribo de Issacar: Quisiom, Daberate,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6410, 'nvi', 'Josué', 'js', 21, 29, 'Jarmute e En-Ganim, cada qual com os seus arredores. Foram quatro cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6411, 'nvi', 'Josué', 'js', 21, 30, 'Receberam da tribo de Aser: Misal, Abdom,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6412, 'nvi', 'Josué', 'js', 21, 31, 'Helcate e Reobe, cada qual com os seus arredores. Foram quatro cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6413, 'nvi', 'Josué', 'js', 21, 32, 'Receberam da tribo de Naftali: Quedes, na Galiléia, cidade de refúgio dos acusados de homicídio, Hamote-Dor e Cartã, cada qual com os seus arredores. Foram três cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6414, 'nvi', 'Josué', 'js', 21, 33, 'Todas as cidades dos clãs gersonitas foram treze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6415, 'nvi', 'Josué', 'js', 21, 34, 'Os clãs meraritas, o restante dos levitas, receberam as seguintes cidades: Da tribo de Zebulom: Jocneão, Cartá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6416, 'nvi', 'Josué', 'js', 21, 35, 'Dimna e Naalal, cada qual com os seus arredores. Foram quatro cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6417, 'nvi', 'Josué', 'js', 21, 36, 'Da tribo de Rúben: Bezer, Jaza,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6418, 'nvi', 'Josué', 'js', 21, 37, 'Quedemote e Mefaate, cada qual com os seus arredores. Foram quatro cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6419, 'nvi', 'Josué', 'js', 21, 38, 'Da tribo de Gade: em Gileade, Ramote, cidade de refúgio dos acusados de homicídio, Maanaim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6420, 'nvi', 'Josué', 'js', 21, 39, 'Hesbom e Jazar, cada qual com os seus arredores. Foram quatro cidades ao todo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6421, 'nvi', 'Josué', 'js', 21, 40, 'Todas as cidades dadas aos clãs meraritas, que eram o restante dos levitas, foram doze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6422, 'nvi', 'Josué', 'js', 21, 41, 'No total, as cidades dos levitas nos territórios dos outros israelitas foram quarenta e oito cidades com os seus arredores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6423, 'nvi', 'Josué', 'js', 21, 42, 'Cada uma de todas essas cidades tinha pastagens ao seu redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6424, 'nvi', 'Josué', 'js', 21, 43, 'Assim o Senhor deu aos israelitas toda a terra que tinha prometido sob juramento aos seus antepassados, e eles tomaram posse dela e se estabeleceram ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6425, 'nvi', 'Josué', 'js', 21, 44, 'O Senhor lhes concedeu descanso de todos os lados, como tinha jurado aos seus antepassados. Nenhum dos seus inimigos pode resisti-los, pois o Senhor entregou a todos eles em suas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6426, 'nvi', 'Josué', 'js', 21, 45, 'De todas as boas promessas do Senhor à nação de Israel, nenhuma delas falhou; todas se cumpriram.');
