INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6477, 'nvi', 'Josué', 'js', 24, 1, 'Então Josué reuniu todas as tribos de Israel em Siquém. Convocou as autoridades, os líderes, os juízes e os oficiais de Israel, e eles compareceram diante de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6478, 'nvi', 'Josué', 'js', 24, 2, 'Josué disse a todo o povo: "Assim diz o Senhor, o Deus de Israel: ‘Há muito tempo, os seus antepassados, inclusive Terá, pai de Abraão e de Naor, viviam além do Eufrates e prestavam culto a outros deuses.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6479, 'nvi', 'Josué', 'js', 24, 3, 'Mas eu tirei seu pai Abraão da terra dalém do Eufrates e o conduzi por toda a Canaã e lhe dei muitos descendentes. Dei-lhe Isaque,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6480, 'nvi', 'Josué', 'js', 24, 4, 'e a Isaque dei Jacó e Esaú. A Esaú dei os montes de Seir, mas Jacó e seus filhos desceram para o Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6481, 'nvi', 'Josué', 'js', 24, 5, '" ‘Então enviei Moisés e Arão e feri os egípcios com pragas, com as quais os castiguei, e depois tirei vocês de lá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6482, 'nvi', 'Josué', 'js', 24, 6, 'Quando tirei os seus antepassados do Egito, vocês vieram para o mar, e os egípcios os perseguiram com carros de guerra e cavaleiros até o mar Vermelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6483, 'nvi', 'Josué', 'js', 24, 7, 'Mas os seus antepassados clamaram a mim, e eu coloquei trevas entre vocês e os egípcios; fiz voltar o mar sobre eles e os encobrir. Vocês viram com os seus próprios olhos o que eu fiz com os egípcios. Depois disso vocês viveram no deserto longo tempo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6484, 'nvi', 'Josué', 'js', 24, 8, '" ‘Eu os trouxe para a terra dos amorreus que viviam a leste do Jordão. Eles lutaram contra vocês, mas eu os entreguei nas suas mãos. Eu os destruí diante de vocês, e vocês se apossaram da terra deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6485, 'nvi', 'Josué', 'js', 24, 9, 'Quando Balaque, rei de Moabe, filho de Zipor, se preparava para lutar contra Israel, mandou buscar Balaão, filho de Beor, para lançar maldição sobre vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6486, 'nvi', 'Josué', 'js', 24, 10, 'Mas eu não quis ouvir Balaão, de modo que ele os abençoou vez após vez, e eu os livrei das mãos dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6487, 'nvi', 'Josué', 'js', 24, 11, '" ‘Depois vocês atravessaram o Jordão e chegaram a Jericó. Os chefes de Jericó lutaram contra vocês, assim como os amorreus, os ferezeus, os cananeus, os hititas, os girgaseus, os heveus e os jebuseus, mas eu os entreguei nas mãos de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6488, 'nvi', 'Josué', 'js', 24, 12, 'Eu lhes causei pânico para expulsá-los de diante de vocês, como fiz aos dois reis amorreus. Não foi a espada e o arco que lhes deram a vitória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6489, 'nvi', 'Josué', 'js', 24, 13, 'Foi assim que lhes dei uma terra que vocês não cultivaram e cidades que vocês não construíram. Nelas vocês moram, e comem de vinhas e olivais que não plantaram’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6490, 'nvi', 'Josué', 'js', 24, 14, '"Agora temam o Senhor e sirvam-no com integridade e fidelidade. Joguem fora os deuses que os seus antepassados adoraram além do Eufrates e no Egito, e sirvam ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6491, 'nvi', 'Josué', 'js', 24, 15, 'Se, porém, não lhes agrada servir ao Senhor, escolham hoje a quem irão servir, se aos deuses que os seus antepassados serviram além do Eufrates, ou aos deuses dos amorreus, em cuja terra vocês estão vivendo. Mas, eu e a minha família serviremos ao Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6492, 'nvi', 'Josué', 'js', 24, 16, 'Então o povo respondeu: "Longe de nós abandonar o Senhor para servir outros deuses!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6493, 'nvi', 'Josué', 'js', 24, 17, 'Foi o próprio Senhor, o nosso Deus, que nos tirou, a nós e a nossos pais, do Egito, daquela terra de escravidão, e realizou aquelas grandes maravilhas diante dos nossos olhos. Ele nos protegeu no caminho e entre as nações pelas quais passamos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6494, 'nvi', 'Josué', 'js', 24, 18, 'Além disso, o Senhor expulsou de diante de nós todas as nações, inclusive os amorreus, que viviam nesta terra. Nós também serviremos ao Senhor, porque ele é o nosso Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6495, 'nvi', 'Josué', 'js', 24, 19, 'Josué disse ao povo: "Vocês não têm condições de servir ao Senhor. Ele é Deus santo! É Deus zeloso! Ele não perdoará a rebelião e o pecado de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6496, 'nvi', 'Josué', 'js', 24, 20, 'Se abandonarem o Senhor e servirem a deuses estrangeiros, ele se voltará contra vocês e os castigará. Mesmo depois de ter sido bondoso com vocês, ele os exterminará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6497, 'nvi', 'Josué', 'js', 24, 21, 'O povo, porém, respondeu a Josué: "De maneira nenhuma! Nós serviremos ao Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6498, 'nvi', 'Josué', 'js', 24, 22, 'Disse então Josué: "Vocês são testemunhas contra vocês mesmos de que escolheram servir ao Senhor". "Somos", responderam eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6499, 'nvi', 'Josué', 'js', 24, 23, 'Disse Josué: "Agora, então, joguem fora os deuses estrangeiros que estão com vocês e voltem-se de coração para o Senhor, o Deus de Israel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6500, 'nvi', 'Josué', 'js', 24, 24, 'E o povo disse a Josué: "Serviremos ao Senhor nosso Deus e lhe obedeceremos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6501, 'nvi', 'Josué', 'js', 24, 25, 'Naquele dia Josué firmou um acordo com o povo em Siquém, e lhe deu decretos e leis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6502, 'nvi', 'Josué', 'js', 24, 26, 'Josué registrou essas coisas no Livro da Lei de Deus. Depois ergueu uma grande pedra ali, sob a Grande Árvore, perto do santuário do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6503, 'nvi', 'Josué', 'js', 24, 27, 'Então disse ele a todo o povo: "Vejam esta pedra! Ela será uma testemunha contra nós, pois ouviu todas as palavras que o Senhor nos disse. Será uma testemunha contra vocês, caso sejam infiéis ao Deus de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6504, 'nvi', 'Josué', 'js', 24, 28, 'Depois Josué despediu o povo, e cada um foi para a sua propriedade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6505, 'nvi', 'Josué', 'js', 24, 29, 'Passado algum tempo, Josué, filho de Num, servo do Senhor, morreu. Tinha cento e dez anos de idade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6506, 'nvi', 'Josué', 'js', 24, 30, 'E o sepultaram na terra que recebeu por herança, em Timnate-Sera, nos montes de Efraim, ao norte do monte Gaás.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6507, 'nvi', 'Josué', 'js', 24, 31, 'Israel serviu ao Senhor durante toda a vida de Josué e dos líderes que sobreviveram depois dele e que sabiam de tudo o que o Senhor fizera em favor de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6508, 'nvi', 'Josué', 'js', 24, 32, 'Os ossos de José, que os israelitas haviam trazido do Egito, foram enterrados em Siquém, no quinhão de terra que Jacó havia comprado dos filhos de Hamor, pai de Siquém, por cem peças de prata. Aquele terreno tornou-se herança dos descendentes de José.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6509, 'nvi', 'Josué', 'js', 24, 33, 'Sucedeu também que Eleazar, filho de Arão, morreu e foi sepultado em Gibeá, que fora dada a seu filho Finéias, nos montes de Efraim.');
