INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5911, 'nvi', 'Josué', 'js', 4, 1, 'Quando toda a nação terminou de atravessar o Jordão, o Senhor disse a Josué:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5912, 'nvi', 'Josué', 'js', 4, 2, '"Escolha doze homens dentre o povo, um de cada tribo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5913, 'nvi', 'Josué', 'js', 4, 3, 'e mande que apanhem doze pedras do meio do Jordão, do lugar onde os sacerdotes ficaram parados. Levem-nas com vocês para o local onde forem passar a noite".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5914, 'nvi', 'Josué', 'js', 4, 4, 'Josué convocou os doze homens que escolhera dentre os israelitas, um de cada tribo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5915, 'nvi', 'Josué', 'js', 4, 5, 'e lhes disse: "Passem adiante da arca do Senhor, o seu Deus, até o meio do Jordão. Ponha cada um de vocês uma pedra nos ombros, conforme o número das tribos dos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5916, 'nvi', 'Josué', 'js', 4, 6, 'Elas servirão de sinal para vocês. No futuro, quando os seus filhos lhes perguntarem: ‘Que significam essas pedras? ’,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5917, 'nvi', 'Josué', 'js', 4, 7, 'respondam que as águas do Jordão foram interrompidas diante da arca da aliança do Senhor. Quando a arca atravessou o Jordão, as águas foram interrompidas. Essas pedras serão um memorial perpétuo para o povo de Israel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5918, 'nvi', 'Josué', 'js', 4, 8, 'Os israelitas fizeram como Josué lhes havia ordenado. Apanharam doze pedras do meio do Jordão, conforme o número das tribos de Israel, como o Senhor tinha ordenado a Josué; e as levaram ao acampamento, onde as deixaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5919, 'nvi', 'Josué', 'js', 4, 9, 'Josué ergueu também doze pedras no meio do Jordão, no local onde os sacerdotes que carregavam a arca da aliança tinham ficado. E elas estão lá até hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5920, 'nvi', 'Josué', 'js', 4, 10, 'Os sacerdotes que carregavam a arca permaneceram de pé no meio do Jordão até que o povo fez tudo o que o Senhor ordenara a Josué, por meio de Moisés. E o povo atravessou apressadamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5921, 'nvi', 'Josué', 'js', 4, 11, 'Quando todos tinham acabado de atravessar, a arca do Senhor e os sacerdotes passaram para o outro lado, diante do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5922, 'nvi', 'Josué', 'js', 4, 12, 'Os homens das tribos de Rúben, de Gade e da metade da tribo de Manassés atravessaram preparados para lutar, à frente dos israelitas, como Moisés os tinha orientado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5923, 'nvi', 'Josué', 'js', 4, 13, 'Cerca de quarenta mil homens preparados para a guerra passaram perante o Senhor, rumo à planície de Jericó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5924, 'nvi', 'Josué', 'js', 4, 14, 'Naquele dia o Senhor exaltou Josué à vista de todo o Israel; e eles o respeitaram enquanto viveu, como tinham respeitado Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5925, 'nvi', 'Josué', 'js', 4, 15, 'Então o Senhor disse a Josué:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5926, 'nvi', 'Josué', 'js', 4, 16, '"Ordene aos sacerdotes que carregam a arca da aliança que saiam do Jordão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5927, 'nvi', 'Josué', 'js', 4, 17, 'E Josué lhes ordenou que saíssem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5928, 'nvi', 'Josué', 'js', 4, 18, 'Quando os sacerdotes que carregavam a arca da aliança do Senhor saíram do Jordão, mal tinham posto os pés em terra seca, as águas do Jordão voltaram ao seu lugar, e cobriram como antes as suas margens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5929, 'nvi', 'Josué', 'js', 4, 19, 'No décimo dia do primeiro mês o povo subiu do Jordão e acampou em Gilgal, na fronteira leste de Jericó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5930, 'nvi', 'Josué', 'js', 4, 20, 'E em Gilgal Josué ergueu as doze pedras tiradas do Jordão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5931, 'nvi', 'Josué', 'js', 4, 21, 'Disse ele aos israelitas: "No futuro, quando os filhos perguntarem aos seus pais: ‘Que significam essas pedras? ’,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5932, 'nvi', 'Josué', 'js', 4, 22, 'expliquem a eles: Aqui Israel atravessou o Jordão em terra seca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5933, 'nvi', 'Josué', 'js', 4, 23, 'Pois o Senhor, o seu Deus, secou o Jordão perante vocês até que o tivessem atravessado. O Senhor, o seu Deus, fez com o Jordão como fizera com o mar Vermelho, quando o secou diante de nós até que o tivéssemos atravessado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5934, 'nvi', 'Josué', 'js', 4, 24, 'Ele assim fez para que todos os povos da terra saibam que a mão do Senhor é poderosa e para que vocês sempre temam o Senhor, o seu Deus".');
