INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5977, 'nvi', 'Josué', 'js', 7, 1, 'Mas os israelitas foram infiéis com relação às coisas consagradas. Acã, filho de Carmi, filho de Zinri, filho de Zerá, da tribo de Judá, apossou-se de algumas delas. E a ira do Senhor acendeu-se contra Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5978, 'nvi', 'Josué', 'js', 7, 2, 'Sucedeu que Josué enviou homens de Jericó a Ai, que fica perto de Bete-Áven, a leste de Betel, e ordenou-lhes: "Subam e espionem a região". Os homens subiram e espionaram Ai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5979, 'nvi', 'Josué', 'js', 7, 3, 'Quando voltaram a Josué, disseram: "Não é preciso que todos avancem contra Ai. Envie uns dois ou três mil homens para atacá-la. Não canse todo o exército, pois eles são poucos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5980, 'nvi', 'Josué', 'js', 7, 4, 'Por isso cerca de três mil homens atacaram a cidade; mas os homens de Ai os puseram em fuga,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5981, 'nvi', 'Josué', 'js', 7, 5, 'chegando a matar trinta e seis deles. Eles perseguiram os israelitas desde a porta da cidade até Sebarim, e os feriram na descida. Diante disso o povo desanimou-se completamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5982, 'nvi', 'Josué', 'js', 7, 6, 'Então Josué, com as autoridades de Israel, rasgou as vestes, prostrou-se, rosto em terra, diante da arca do Senhor, cobrindo de terra a cabeça, e ali permaneceu até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5983, 'nvi', 'Josué', 'js', 7, 7, 'Disse então Josué: "Ah, Soberano Senhor, por que fizeste este povo atravessar o Jordão? Foi para nos entregar nas mãos dos amorreus e nos destruir? Antes nos contentássemos em continuar no outro lado do Jordão!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5984, 'nvi', 'Josué', 'js', 7, 8, 'Que poderei dizer, Senhor, agora que Israel foi derrotado por seus inimigos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5985, 'nvi', 'Josué', 'js', 7, 9, 'Os cananeus e os demais habitantes desta terra saberão disso, nos cercarão e eliminarão o nosso nome da terra. Que farás, então, pelo teu grande nome? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5986, 'nvi', 'Josué', 'js', 7, 10, 'O Senhor disse a Josué: "Levante-se! Por que você está aí prostrado?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5987, 'nvi', 'Josué', 'js', 7, 11, 'Israel pecou. Violaram a aliança que eu lhes ordenei. Eles se apossaram de coisas consagradas, roubaram-nas, esconderam-nas, e as colocaram junto de seus bens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5988, 'nvi', 'Josué', 'js', 7, 12, 'Por isso os israelitas não conseguem resistir aos inimigos; fogem deles porque se tornaram merecedores da sua destruição. Não estarei mais com vocês, se não destruírem do meio de vocês o que foi consagrado à destruição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5989, 'nvi', 'Josué', 'js', 7, 13, '"Vá, santifique o povo! Diga-lhes: Santifiquem-se para amanhã, pois assim diz o Senhor, o Deus de Israel: Há coisas consagradas à destruição no meio de vocês, ó Israel. Vocês não conseguirão resistir aos seus inimigos enquanto não as retirarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5990, 'nvi', 'Josué', 'js', 7, 14, '"Apresentem-se de manhã, uma tribo de cada vez. A tribo que o Senhor escolher virá à frente, um clã de cada vez; o clã que o Senhor escolher virá à frente, uma família de cada vez; e a família que o Senhor escolher virá à frente, um homem de cada vez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5991, 'nvi', 'Josué', 'js', 7, 15, 'Aquele que for pego com as coisas consagradas será queimado no fogo com tudo o que lhe pertence. Violou a aliança do Senhor e cometeu loucura em Israel! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5992, 'nvi', 'Josué', 'js', 7, 16, 'Na manhã seguinte Josué mandou os israelitas virem à frente segundo as suas tribos, e a de Judá foi a escolhida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5993, 'nvi', 'Josué', 'js', 7, 17, 'Os clãs de Judá vieram à frente, e ele escolheu os zeraítas. Fez o clã dos zeraítas vir à frente, família por família, e o escolhido foi Zinri.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5994, 'nvi', 'Josué', 'js', 7, 18, 'Josué fez a família de Zinri vir à frente, homem por homem, e Acã, filho de Carmi, filho de Zinri, filho de Zerá, da tribo de Judá, foi o escolhido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5995, 'nvi', 'Josué', 'js', 7, 19, 'Então Josué disse a Acã: "Meu filho, para a glória do Senhor, o Deus de Israel, diga a verdade. Conte-me o que você fez; não me esconda nada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5996, 'nvi', 'Josué', 'js', 7, 20, 'Acã respondeu: "É verdade que pequei contra o Senhor, contra o Deus de Israel. O que fiz foi o seguinte:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5997, 'nvi', 'Josué', 'js', 7, 21, 'quando vi entre os despojos uma bela capa feita na Babilônia, dois quilos e quatrocentos gramas de prata e uma barra de ouro de seiscentos gramas, eu os cobicei e me apossei deles. Estão escondidos no chão da minha tenda, com a prata por baixo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5998, 'nvi', 'Josué', 'js', 7, 22, 'Josué enviou alguns homens que correram à tenda de Acã; lá estavam escondidas as coisas, com a prata por baixo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5999, 'nvi', 'Josué', 'js', 7, 23, 'Retiraram-nas da tenda e as levaram a Josué e a todos os israelitas, e as puseram perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6000, 'nvi', 'Josué', 'js', 7, 24, 'Então Josué, junto com todo o Israel, levou Acã, bisneto de Zerá, e a prata, a capa, a barra de ouro, seus filhos e filhas, seus bois, seus jumentos, suas ovelhas, sua tenda e tudo o que lhe pertencia, ao vale de Acor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6001, 'nvi', 'Josué', 'js', 7, 25, 'Disse Josué: "Por que você nos causou esta desgraça? Hoje o Senhor lhe causará desgraça". E todo o Israel o apedrejou, e depois apedrejou também os seus, e os queimou no fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6002, 'nvi', 'Josué', 'js', 7, 26, 'Sobre Acã ergueram um grande monte de pedras, que existe até hoje. Então o Senhor se afastou do fogo da sua ira. Por isso foi dado àquele lugar o nome de vale de Acor, nome que permanece até hoje.');
